#define ITER 80.
#define PI 3.141592

mat2 rot(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c,s,-s,c);
}

vec2 moda (vec2 p, float per)
{
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a,per)-per/2.;
    return vec2(cos(a),sin(a))*l;
}

// iq's palette
vec3 palette(float t,vec3 a, vec3 b, vec3 c, vec3 d)
{
	return a+b*cos(2.*PI*(c*t+d));    
}

float sc (vec3 p, float s)
{
 p = abs(p);
    p = max(p,p.yzx);
    return min(p.x,min(p.y,p.z))-s;
}

float box (vec3 p, vec3 c)
{
    return length(max(abs(p)-c,0.));
}

float cyl (vec2 p, float r)
{
	return length(p)-r;    
}

float sphe (vec3 p, float r)
{
    return length(p)-r;
}

float wcube (vec3 p, float s)
{
    return max(-sc(p,s), box(p, vec3(s+0.09)));
}

float prim1 (vec3 p, float per)
{
    p.xz *= rot(p.y);
    p.y = mod(p.y-per/2.,per)-per/2.;

    return wcube(p,.3);
}

float prim2 (vec3 p, float per)
{
    float c = cyl(p.xz,0.1);
    p.y -= tan(iTime);
    p.y = mod(p.y-per/2.,per)-per/2.;

    float s = sphe(p, 0.2);
    return min(c,s);
}

float elevators (vec3 p, float per)
{
    p.z = mod(p.z-per/2.,per)-per/2.;
    p.xz = moda(p.xz, (2.*PI)/4.);
    p.x -= sin(iTime)+3.;
    return min(prim1(p,0.7),prim2(p,0.8));
}

float hex (vec3 p, float per)
 {
     p.z = mod(p.z-per/2.,per)-per/2.;
     p.xy *= rot(iTime);
    p.xy = moda(p.xy, (2.*PI)/6.);
    p.x -= 2.;
    return min(prim1(p,0.7),prim2(p,0.8));
} 


float SDF (vec3 p)
{

    return min(elevators(p,6.), hex(p,3.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    vec3 p = vec3(0.001,.001,iTime*0.5);
    vec3 dir = normalize(vec3(uv,1.));

    float shad = 0.;
    vec3 col = vec3(0.);
    for (float i = 0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {shad = i/ITER;
         col = vec3(1.-shad)*palette(p.z*2.,
                                    vec3(0.,0.3,0.7),
                                    vec3(0.2,0.3,0.3),
                                    vec3(.2),
                                    vec3 (0.,0.2,iTime*0.5));
         break;
        }
        else col = palette(length(uv),
                          vec3 (0.,0.1,0.7),
                           vec3(0.,0.5,0.3),
                           vec3 (0.3),
                           vec3(0.5));
        p += d*dir*0.7;
    }

    // Output to screen
    fragColor = vec4(pow(col, vec3(0.45)),1.0);
}

vec4 light;
float ui;
mat2 m,n,nn;
float map (vec3 p) {
    float d = length(p-light.xyz)-light.w;
    d = min(d,max(10.-p.z,0.));
    float t = 2.5;
    for (int i = 0; i < 13; i++) {
        t = t*0.66;
        p.xy = m*p.xy;
        p.yz = n*p.yz;
        p.zx = nn*p.zx;
        p.xz = abs(p.xz) - t;

    }
    d = min(d,length(p)-1.4*t);

    return d;
}
vec3 norm (vec3 p) {
    vec2 e = vec2 (.001,0.);
    return normalize(vec3(
        map(p+e.xyy) - map(p-e.xyy),
        map(p+e.yxy) - map(p-e.yxy),
        map(p+e.yyx) - map(p-e.yyx)
    ));
}
vec3 dive (vec3 p, vec3 d) {
    for (int i = 0; i < 20; i++) {
        p += d*map(p);
    }
    return p;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 v = fragCoord/iResolution.xy*2.-1.;
	v.x *= iResolution.x/iResolution.y;
    ui = 100.*iTime;
    float y = -0.001*ui;
    m = mat2(sin(y),cos(y),-cos(y),sin(y));
    y = 0.0035*ui;
    n = mat2(sin(y),cos(y),-cos(y),sin(y));
    y = 0.0023*ui;
    nn = mat2(sin(y),cos(y),-cos(y),sin(y));
    vec3 r = vec3(0,0,-15.+2.*sin(0.01*ui));
    light = vec4(10.*sin(0.01*ui),2,-23,1);
    vec3 d = normalize(vec3(v,5.));
    vec3 p = dive(r,d);
    d = normalize(light.xyz-p);
    vec3 no = norm(p);
    vec3 col = vec3(.7,.8,.9);
    vec3 bounce = dive(p+0.01*d,d);
    col = mix(col,vec3(0),dot(no, normalize(light.xyz-p)));
    if (length(bounce-light.xyz) > light.w+0.1) col *= 0.2;

    fragColor = vec4(col,1.0);
}

vec2 rand2(in vec2 p)
{
	return fract(vec2(sin(p.x * 591.32 + p.y * 154.077), cos(p.x * 391.32 + p.y * 49.077)));
}

float voronoi(in vec2 x)
{
	vec2 p = floor(x);
	vec2 f = fract(x);
	float minDistance = 1.;

	for(int j = -1; j <= 1; j ++)
	{
		for(int i = -1; i <= 1; i ++)
		{
			vec2 b = vec2(i, j);
            vec2 rand = .5 + .5 * sin(iTime * 3. + 12. * rand2(p + b));
			vec2 r = vec2(b) - f + rand;
			minDistance = min(minDistance, length(r));
		}
	}
	return minDistance;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    float val = pow(voronoi(uv * 8.) * 1.25, 7.) * 2.;
    float gridLineThickness = 2. / iResolution.y;
    vec2 grid = step(mod(uv, .1), vec2(gridLineThickness));    

    fragColor = vec4(0., val * (grid.x + grid.y), 0., 1.);

}

bool circle (vec2 uv, vec2 p, float radius)
{
    	float dist = sqrt((uv.x - p.x) * (uv.x -p.x) + (uv.y - p.y) * (uv.y - p.y)); 
    	if (dist < radius) 
            return true;
    	else return false; 


}


vec4 heart(vec2 p, vec2 cen, float rad) { 

    vec2 relative = p-cen; //relative position
    float a = atan(relative.x, relative.y)/3.141593; //angle 
    float r = a*rad; //radius 
    float t;
    float step = 1.0; 

    if(relative.x > 0.0){
        //right area 

        float d = length(p-cen)-r*1.5; 
        t = smoothstep (0.5, step, d); 

    }else{ 
        //left area

        float d = length(p-cen)-r*-1.5;
        t = smoothstep (0.0, step, d);

    } 

    return vec4(0.9,0.3,0.5,1.0-t);    //color

}


