void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 res = iResolution.xy,
        off = cen * res,
        uv = 0.5 + (fragCoord - off) / res,
        m = 0.5 + (iMouse.xy - off) / res;

    if (iMouse.z <= 0.) {
        float time = iTime * 1.5;
        m = .5 + .3 * vec2(cos(time * .15), sin(time * .33));
    }

    // Julia/Mandlebrot is z(n) = z(n-1)^2 + c
    // Julia: 
    //   Where z and c are points on the complex plane
    //   z(0) is the point being drawn on the plane
    //   c is an arbitrary point on the plane
    // Mandlebrot:
    //   Where z and c are points on the complex plane
    //   z(0) is 0 + 0i (this can be an arbitrary point to further explore the set space)
    //   c is the point being drawn on the plane

    // Complex numbers take the form (a + bi)
    // Squared complex numbers take the following algebraic expansion
    //   (x + yi) ^ 2
	//   ==> (x + yi)(x + yi)
    //   ==> (x^2 + 2xyi + (yi)^2)
    //   ==> (x^2 + 2xyi - y^2)
    //   ==> (x^2 - y^2) + (2xy)i
    //   ==> x' + y'i
    // Where x' = (x^2 - y^2) and y' = 2xy

    // Julia
    float xtemp = 0.;
    vec2 z = scalev(uv, scale);

    // Mandlebrot
    //vec2 z = vec2(0.);

    // Madlebrot (extended by mouse position)
    //vec2 z = vec2(scalex(m.x, scale), scaley(m.y, scale));

    float l = LEN(z);
    float o = 0.;

    for(float i = 1.; i <= itr; i++) {
        vec2 s = scalev(m, scale);
        //xtemp = (z.x * z.x) - (z.y * z.y) + s.x;
        //z.y = (2. * z.x * z.y) + s.y;
        //z.x = xtemp;

        // deMoivre Matrix forumlation basically does the above
        // |  z.x z.y |   
        // | -z.y z.x | * | z.x z.y | + s
        //   ==> z.x = (z.x * z.x) - (z.y * -z.y) + s.x
        //   ==> z.y = (z.x * z.y) + (z.x + z.y) + s.y
        z = mat2(z, -z.y, z.x) * z + s;        
        l = LEN(z);

        if(l > escape) {
            o = i;
            break;            
        }
    }          

    if (distance(m, uv) < 0.005) {
        // Draw Indicator
        fragColor = vec4(1.);
    }
    else if (o == 0.) {
        // Inside Set (Black regions)
        fragColor = vec4(0.);
    } else {
        // Escaped value, color by number of orbits to escape
        float t = o/float(itr / 64.) + (0.5*iTime);
        vec3 cola = pal(t, vec3(0.5), vec3(0.5), vec3(1.0), vec3(0.0,0.10,0.20) );
        vec3 colb = pal(t, vec3(0.5), vec3(0.5), vec3(1.0,0.7,0.4), vec3(0.0,0.15,0.20) );
        vec3 col = mix(cola, colb, (sin(0.1*iTime) + 1.) * 0.5);
        fragColor = vec4(col, 1.);
    }

    // Output to screen
}

vec2 rotate(vec2 uv, float amount) {
    vec2 prime = vec2( uv.x*cos(amount) + uv.y*sin(amount),
                      -uv.x*sin(amount) + uv.y*cos(amount));
    return prime;
}
