void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // center (0,n) in screen
    vec2 uv = fragCoord - vec2(iResolution.x/2.0,iResolution.y/2.0);
    vec2 width = vec2(10.0,50.0);

    vec3 col = vec3(0.0);
    float dist = length(uv);
    float rad = length(width);
    float box_size = max(width.x*2.,width.y*2.);
    vec2 offset_pos = uv + box_size/2.;
    vec2 aligned = vec2(floor(offset_pos.x/box_size), floor(offset_pos.y/box_size));
    vec2 center = (aligned+0.5) * box_size;

    vec2 angled = rotate(uv, iTime*-0.0613)+vec2(rad,rad);
    float box_size_ang = 2.*rad;
    vec2 aligned_pos_ang = vec2(angled.x/(box_size_ang), angled.y/box_size_ang);
    vec2 aligned_ang = floor(aligned_pos_ang);
    vec2 center_ang = (aligned_ang+0.5) * box_size_ang;
    vec2 center_rel_ang = angled-center_ang;
    vec2 box_ang = fract(aligned_pos_ang) * box_size_ang;

    bool polarity = ((int(aligned.x) % 2 == int(aligned.y) % 2));
    if (polarity) {
        col += 0.2*vec3(0.0,0.0,1.0);
    }
    if (abs(dist-rad)<1.0) {
        col = vec3(1.0);
    }
    if (polarity && abs(distance(center,offset_pos))<16.0) {
        col = vec3(0.2,0.4,1.0);
    }
    if (
        (box_ang.x < 1. || box_ang.x>box_size_ang-1. || box_ang.y > box_size_ang-1. || box_ang.y < 1.)) {
        col = vec3(1.0, 0.0,0.0);
    }
    if (abs(distance(center_ang,angled))<8.0) {
        col = vec3(1.0,0.4,0.2);
    }
    vec2 prime = rotate(center_rel_ang, iTime);
    col =  vec3(float(prime.x <= width.x && prime.x >= -width.x && prime.y <= width.y && prime.y > -width.y));

    // output color
    fragColor = vec4(col,1.0);
}

