vec2 cinv(vec2 z){return vec2(z.x, -z.y)/dot(z, z);}

vec2 csqr(vec2 z){return vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y);}

vec2 ccube(vec2 z){
    return vec2(z.x*z.x*z.x - 3.0*z.x*z.y*z.y, 3.0*z.x*z.x*z.y - z.y*z.y*z.y);
}

vec2 cmul(vec2 a, vec2 b){return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);}

vec2 m(float t){
    return vec2(16.0*iMouse.x/iResolution.x, 2.0*sin(t)*sin(t)*sin(t)*sin(t) + 3.0*sin(0.245*t) + sin(1.234*t));
}

vec2 f(vec2 z){return ccube(z) - m(iTime) + vec2(4);}

#ifdef evenMoreFracture
vec2 df(vec2 z){
    vec2 e = vec2(0.0001);
    return 10000.0*(f(z + e) - f(z));
}
#else
vec2 df(vec2 z){return 3.0*csqr(z);}
#endif

float newton(vec2 p){
    vec2 z = p, z1 = z - f(z)/df(z);
    vec2 d;
    for(int i = 0; i < fractalIters; i++){
        d = abs(z - z1);
        if(d.x < 0.05 || d.y < 0.05) break;
        z = z1;
        z1 = z1 - cmul(f(z1), cinv(df(z1)));
    }
    return smoothstep(0.0, 0.95, length(d));
}

vec3 render(vec2 p){
    vec2 coords = zoom*(2.0*p - iResolution.xy)/iResolution.y;
    if(length(coords) < 0.1) return vec3(0);
    float mo = iMouse.y/iResolution.y;
    coords = cmul(coords, vec2(cos(mo), sin(mo)));
    float t = 0.2*smoothstep(0.5, 3.0, length(coords));
    coords = cmul(coords, vec2(cos(t), sin(t)));
    return vec3(newton(coords))*smoothstep(0.1, 1.0, dot(coords, coords));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0);

#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ ){
        vec2 px = fragCoord + vec2(float(m),float(n))/float(AA);
    	col += render(px);    
    }
    col /= float(AA*AA);
#else
    col = render(fragCoord);
#endif
	fragColor = vec4( col, 1.0 );
}

#define M_PI 3.14159
vec3 circle(vec2 uv,float r,vec2 pos,float rBlur,float value){
    vec3 col;
    float l = length(uv-pos);
    col= vec3(smoothstep(r,r-rBlur,l))*((0.2+value)); 
    return col;    
}

vec2 normalizeCoords(vec2 coords,float prop){
    vec2 vecOut;
    vecOut = ((coords/iResolution.xy)-0.5);
    vecOut.x*=prop;
    return vecOut;
}         

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float prop = iResolution.x/iResolution.y;
    vec2 uv = normalizeCoords(fragCoord.xy,prop);
    //vec2 mousePos=normalizeCoords(iMouse.xy,prop); idea for a fun mouse input ?
    vec3 col;
    float rSpeed=0.5;
	float d =0.3 +0.06*sin(iTime*rSpeed);
    float speed = 3.;

    vec3 background= circle (uv,d*1.5,vec2(0.),d*5.,1.);
    background.xyz+=vec3(0.05,0.,0.15);

    background*=circle (uv,1.5,vec2(0.),1.4,1.);;

    vec3 planet = 1.-circle(uv, d*0.7,vec2 (0),0.02,1.);

    for (float i=1.;i<25.;i++){

    	float offset=.2;
     	float st = speed*iTime;

        // co is similar to the cos that define x coordinate
        // but with some offset and remap to use as a radius factor
		float co=(cos(st+offset*i*1.5+M_PI*.5)+1.)/2.;

        float x=d*cos(st+offset*i*1.5);
        float y=d*sin(st*0.6+offset*i*0.9);

        vec2 pos=vec2 (x,y);

        float r;

        r=0.02*co*co+0.0045*i; 

        float a=1.-0.09*(20.-i);

        vec3 colCircle=circle(uv, r,pos,0.03,co);
        vec3 colCircle2=circle(uv, r,pos,0.3,co);

        colCircle.x/=(2.*a);
        colCircle.y*=0.5;

        vec3 colCircleTot=(colCircle+colCircle2*a)*a;

        if(co<0.5)colCircleTot*=planet;


        col = max (col,colCircleTot);

    }
   	col+=background*planet;  

    // Output to screen
    fragColor = vec4(col,1);

}

