// map a visible wavelength[nm] to OpenGL's RGB representation
vec3 lambda2rgb(float lambda)
{
    const float ultraviolet = 400.0;
    const float infrared = 700.0;

    float a = (lambda - ultraviolet) / (infrared - ultraviolet);

    const float C = 7.0;
    vec3 b = vec3(a) -vec3(0.75, 0.5, 0.25);
    return max((1.0-C*b*b),0.0);
}

float fog(vec3 p, vec3 e){
    bool useFogCoordinate = true;
    float fdepth;
    if(useFogCoordinate)
        fdepth = length(p-e);
    else
        fdepth = abs(p.z);

    float fog;
    float density = 0.02;
    fog = exp(-density * fdepth);
    return clamp(fog, 0.0, 1.0);
}

float cube(vec3 p){
    float s = 1.0;
    vec3 d = abs(p) - vec3(s);
    return min(max(d.x, max(d.y,d.z)), 0.0)
        + length(max(d,0.0));
}

float sphere(vec3 p, float rad){
    return length(vec3(0.0) - p) - rad;
}

float plane(vec3 p){
