vec3 computeSurfaceNormal(vec3 p){
    float d = distanceToNearestSurface(p);
    return normalize(vec3(
        distanceToNearestSurface(p+deltax)-d,
        distanceToNearestSurface(p+deltay)-d,
        distanceToNearestSurface(p+deltaz)-d
    ));
}

vec2 computeSphereTexCoord(vec3 p){
    float longitude = acos(dot(vec2(0,-1), normalize(p.xz)));
    if(p.x > 0.0)
        longitude = 2.0*PI - longitude;
    longitude /= 2.0*PI;

    float latitude = acos(dot(vec2(1,0), normalize(p.yz)));
    latitude /= PI;

    return vec2(longitude, latitude);
}

vec3 computeLambert(vec3 p, vec3 n, vec3 l, vec3 e){


    vec3 lightVector = normalize(l-p);
    vec3 eyeVector = normalize(e-p);
    vec3 halfVector = normalize(lightVector + eyeVector);

    float nDotLV = max(0.0, dot(lightVector, n));
    float nDotHV = max(0.0, dot(halfVector, n));

    float attenuation = 1.0;
    float shininess = 10.0;
    float pf;
    if(nDotLV == 0.0)
        pf = 0.0;
 	else
        pf = pow(nDotHV, shininess);

    vec3 ambient = vec3(0.1) * attenuation;
    vec3 diffuse = vec3(1.0,1.0,1.0) * nDotLV * attenuation;
    vec3 specular = vec3(1.0,1.0,1.0) * pf * attenuation*1.0;
    return min(ambient + diffuse + specular, vec3(1.0));
}

vec3 intersectsWithWorld(vec3 p, vec3 dir){
  float dist = 0.0;
  for(int i = 0; i < 20; i++){
    float nearest = distanceToNearestSurface(p + dir*dist);
    if(nearest < 0.01)
    {
        //return (vec3(1.0)+computeSurfaceNormal(p+dir*dist))/2.0;
    	vec3 hit = p+dir*dist;
                //vec3 light = vec3(100.0,
                  //          30.0,
                    //        50.0);
        vec3 light = vec3(100.0*sin(iTime),
                            30.0*cos(iTime),
                            50.0*(1.5+cos(iTime)));
        vec3 normal = computeSurfaceNormal(hit);
        vec3 pixelColor = computeLambert(hit, normal, light, p);
        vec2 texcoord = computeSphereTexCoord(hit);

        vec3 L = normalize(light - hit);
        vec3 V = -dir;
        vec3 H = L+V;

        vec3 T = cross(normalize(hit),normal);
        float u = abs(dot(T,H));

        vec3 diffColor = vec3(0.0);

        const int numSpectralOrders = 3;

        for (int m=1; m<= numSpectralOrders; ++m)
        {
         	float lambda = gratingSpacing * u / float(m);
            diffColor += lambda2rgb(lambda);
        }

        float w = dot(normal, H);
        float e = surfaceRoughness * u / w;
        vec3 hilight = exp(-e*e)*vec3(1.0,1.0,1.0);

        const float diffAtten = 0.8;
        vec3 color = diffAtten*diffColor+hilight;
        return color;

    } 
    dist += nearest;
  }
  //return vec3(0.0);
}


