mat3 rotMatrixFinal(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    float a = 1.0 - c;
    float a2 = a*a;
    float c2 = c*c;
    float s2 = s*s;
   return mat3(c2*(a+c), -s*(a2+2.*c*a+c2), -s*(c*a + c2),
               -s2*a+s*c2-s2*c, c2*a+c2*c, -s*c*a - s*c2 - s2*c,
               s*c*a+s*c2+s2*c, s*c*a+s*c2, c2*a+c2*c-s2*s);
}
vec3 opRep( vec3 p, vec3 c ) {
    vec3 q = mod(p,c)-0.5*c;
    return q;
}
float map (vec3 p) {
    mat3 rot = rotMatrixFinal(iTime);
    vec3 p2 = opRep(p*rot, vec3(.5));
    float d1 = sdBox(p2, vec3(.7,.7,.7)*(audioLevel*0.09+0.1));
    return d1;
}
vec2 trace (vec3 o, vec3 r) {
	float t = 0.;
    float i;
    float precis = .01;
    for (i = 0.; i < 14.; ++i) {
    	vec3 p = o + r * t;
        float d = map(p);
        if (d<precis) break;
        t += d;
    }
    return vec2(t, i);    
}
