mat2 r2d(float a) {
    float sa=sin(a);
    float ca=cos(a);
    return mat2(ca,sa,-sa,ca);
}

vec2 amod(vec2 p, float m) {
    float a=mod(atan(p.x,p.y), m)-m*.5;
    return vec2(cos(a), sin(a))*length(p);
}

float soc(vec3 p) {
    vec3 n = normalize(sign(p+1e6));
    return min(min(dot(p.xy, n.xy), dot(p.yz, n.yz)), dot(p.xz, n.xz));
}

float map(vec3 p) {
    float t = (iTime * 12.) + sin(iTime * .9) * 20.;
    float a1 = -13. + sin(iTime * .8) * .3;
    float a2 = -14.5 + sin(iTime * .9) * .2;
    float d = 1.0; vec3 o = p;
    float a = mod(o.y+5., (20.))-10.; a = abs(o.y);
    p.yz *= r2d(sign(a)* .2);
    p.xz *= r2d(sign(a)*(t * .04));
    p.xz = amod(p.xz, 0.38205625 + sin(iTime * .7) * .13);
    p.xz = max(abs(p.xz)-14.552, -14.0311);
    p.z = mod(p.z, a1)-(a1 *.5);
    p.x = mod(p.x, a2)-(a2 *.5);
    p.y = mod(p.y+t, 12.)-5.;
    d = min(d, soc(max(abs(p) + 2., -2.)));
    return (length(p*-0.3637)-0.4866)*1.1851 - (d * (1.8873 + sin(iTime * 5.4) * .03));
}

vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.8;
    return normalize(
        e.xyy * map(p + e.xyy) +
        e.yyx * map(p + e.yyx) +
        e.yxy * map(p + e.yxy) +
        e.xxx * map(p + e.xxx));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = (fragCoord.xy/iResolution.xy)*2.5-1.;
    st.x *= 1.7;
    vec3 l = vec3(0, 0, -15);
    vec3 ro = vec3(st, -18. + sin(iTime * .8) * 1.5);
    vec3 rd = normalize(vec3(st+vec2(0.), 0.4 + sin(iTime * .6) * .14));
    vec3 mp; mp = ro;
    float md;
    for (int i=0; i<50; i++) {
        md = map(mp); if (md <.001) break; mp += rd*md;
    }
    vec3 p = ro + rd * (mp);
    vec3 normal = calcNormal(p);
    float dif = clamp(dot(normal, normalize(l - p)), 0., 1.);
    dif *= 5. / dot(l - p, l - p);
    vec3 c2 = vec3(pow(dif, .2424));
    c2 += vec3((mp.z * 2.5) * (md * .05), 0, 0 );
    fragColor = vec4(c2, 1.);
}

const float PI = 3.141592;
const float TWO_PI = PI * 2.;
const int split = 8;

float getPolygon(vec2 v, int split){
    float angle = atan(v.y, v.x) - PI / 2. +PI / float(split);
    float angle2 = mod(angle, TWO_PI / float(split));
    float r = length(v);
    float angle3 = PI * (0.5 - 1. / float(split));
    float len = r * sin(PI - angle3 - angle2) / sin(angle3);
    if(len < 0.05) return 0.;
    return max(0., mod(12.*len-iTime, 1.) - 0.3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord - .5 * iResolution.xy ) / iResolution.x;

    // Time varying pixel color
    float diff = getPolygon(uv, split);

    // Output to screen
    fragColor = vec4(diff, diff/8., 0.0, 0.4);
}

vec2 path(float t) {
    float a = sin(t*.2+1.5),b=sin(t*.2);
    return vec2(a*2., a*b);
}

mat2 r2d(float a) {
    float c=cos(a),s=sin(a);
    return mat2(c, s, -s, c);
}

void mo(inout vec2 p, vec2 d) {
    p.x = abs(p.x) - d.x;
    p.y = abs(p.y) - d.y;
    if(p.y>p.x)p=p.yx;
}

float g=0.;
float de(vec3 p) {

    vec3 q = p;
    q.x += q.z*.1;
    q.z += iTime*.1;
    q = mod(q-1., 2.)-1.;
    float s = length(q) - .001 + sin(iTime*30.)*.005;

    p.xy -= path(p.z);

    p.xy *= r2d(p.z*.9);
    mo(p.xy, vec2(.6, .12));
    mo(p.xy, vec2(.9, .2));

    p.xy *= r2d(p.z*.5);

    mo(p.zy, vec2(.1, .2));
    p.x = abs(p.x) - .4;
    float d = length(p.xy) - .02 - (.5+.5*sin(p.z))*.05;

    d = min(d, s);


    g+=.01/(.01+d*d);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy -.5;
    uv.x*=iResolution.x/iResolution.y;

    float dt = iTime * 6.;
    vec3 ro = vec3(0,0, -3. + dt);
    vec3 ta = vec3(0, 0, dt);

    ro.xy += path(ro.z);
    ta.xy += path(ta.z);

    vec3 fwd = normalize(ta -ro);
    vec3 left = cross(vec3(0,1,0),fwd);
    vec3 up = cross(fwd, left);

    vec3 rd = normalize(fwd + left*uv.x+up*uv.y);

    vec3 p;
    float ri,t=0.;
    for(float i=0.;i<1.;i+=.01) {
    	ri = i;
        p=ro+rd*t;
        float d = de(p);
        if(d<.001) break;
        t+=d*.2;
    }
	vec3 bg =  vec3(.2, .6, .6)*.2; 
    vec3 col = mix(vec3(.4, .5, .5), bg,ri);
    col += g*.02;

    col = mix(col, bg, 1.-exp(-.01*t*t));

    fragColor = vec4(col,1.0);
}

vec2 kale(vec2 uv, float angle, float base, float spin) {
	float a = atan(uv.y,uv.x)+spin;
	float d = length(uv);
	a = mod(a,angle*2.0);
	a = abs(a-angle);
	uv.x = sin(a+base)*d;
	uv.y = cos(a+base)*d;
    return uv;
}

vec4 dcAdd( vec4 a, vec4 b )
{
    return a + b;
}

vec4 dcMul( vec4 a, vec4 b )
{
    return vec4( a.x*b.x - a.y*b.y, 
				 a.x*b.y + a.y*b.x,
				 a.x*b.z + a.z*b.x - a.y*b.w - a.w*b.y,
				 a.x*b.w + a.w*b.x + a.z*b.y + a.y*b.z );
}

vec4 dcSqr( vec4 a )
{
    return vec4( a.x*a.x - a.y*a.y, 
				 2.0*a.x*a.y,
				 2.0*(a.x*a.z - a.y*a.w),
				 2.0*(a.x*a.w + a.y*a.z) );
}

