const float pi  = 4.0*atan(1.0);

#define fractalIters 7

vec4 qadd(vec4 a, float b)
{
    return vec4(a.xyz,a.w+b);
}

vec4 qmul(vec4 a, vec4 b)
{
	return vec4(
        b.x*a.w + b.w*a.x + b.z*a.y - b.y*a.z,
        b.y*a.w - b.z*a.x + b.w*a.y + b.x*a.z,
        b.w*a.w - b.x*a.x - b.y*a.y - b.z*a.z,
        b.z*a.w + b.y*a.x - b.x*a.y + b.w*a.z
    );
}

vec4 qmulj(vec4 q)
{
    return vec4(-q.z, q.w, q.x, -q.y);
}

vec4 qexp(vec4 q)
{
    float len = length(q.xyz);
    q.xyz /= len;
    return exp(q.w)*vec4(q.xyz*sin(len), cos(len));
}

vec3 project(vec4 n)
{
    return n.yzw*inversesqrt(n.x*2.0 + 2.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 z = 2.5*(vec4(fragCoord, iMouse.xy) * 2.0 - iResolution.xyxy) / iResolution.y;
    if (iMouse.xy == vec2(0.0,0.0)) z.zw = 0.1*sin(iTime + vec2(0.0, pi*0.5));
    z = z.xwyz;  //Comment for side view
    vec4 dz = vec4(0.0,0.0,0.0,1.0);
    for(int i = 0; i < fractalIters; i++)
    {
        vec4 pz, z4, nz;
        z4 = qadd(1.25*z, 0.5 );
        pz =-qexp(qmulj(pi*z));
        nz = qmulj(pi*z4);
        nz = qadd(    nz, 1.25);
        nz = qmul(    nz, pz  );
        nz = qadd(    nz, 1.75);
        dz = qmul(    nz, dz  );
        //if (dz != dz) break;
        z  = qadd(1.75*z, 0.5 );
        z += qmul(    pz, z4  );
    }
    vec3 col = project(normalize(z)) * 0.5 + 0.5;
    fragColor = vec4(1.0-pow(1.0-col,vec3(1.0-0.00000000000001*dot(z,z))),1.0);
}

void mainImage(out vec4 c, vec2 p)
{
	c = vec4(0);   
    for (float i = 5.; i < 55.; i*= 1.9)
