	    vec2 r = iResolution.xy, v = floor(p.xy * vec2(r.x/r.y, 1) / r.xy * i + sin(iTime / vec2(2,3)) * 9.);
        c+= vec4(mod(v.x + v.y, 2.)) * (1. - c.a) * vec4(pow(20./i, .9) * vec3(.3,.4,.1) * p.y/r.y, 1);
    }
}

/* 
   I wanted to make some basic material blending capable of blending materials using only 
   the material ID, without the actual colour. This is my clumsy first attempt. 
   Currently it can't blend more than two materials, and it's probably much more expensive
   than it shoud be. Artifacts are evident when it tries to blend three or more colours.

   The way it works is having two material IDs and a blend amount between them. The first id
   is always the dominant material of the surface, using the blend amount to mix between the
   materials. In the render function we can get the material info of the current surface and
   set the colour using the material ID. This is done for both materials, and then both colours
   are mixed in order to produce the final value.

 Sources:
 - http://mercury.sexy/hg_sdf/
 - http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm

*/
#define BLEND_SIZE 0.2
#define PI 3.14159

#define CAMSPEED 2.0

//#define VIEW_BLEND
//#define VIEW_SECOND_COL

float getGrey(vec3 p){ return p.x*0.299 + p.y*0.587 + p.z*0.114; }

// Tri-Planar blending function. Based on an old Nvidia tutorial.
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){

    n = max((abs(n) - 0.2)*7., 0.001); // n = max(abs(n), 0.001), etc.
    n /= (n.x + n.y + n.z );  

	return (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
}

// Texture bump mapping. Four tri-planar lookups, or 12 texture lookups in total.
vec3 doBumpMap( sampler2D tex, in vec3 p, in vec3 nor, float bumpfactor){

    const float eps = 0.001;
    float ref = getGrey(tex3D(tex,  p , nor));                 
    vec3 grad = vec3( getGrey(tex3D(tex, vec3(p.x-eps, p.y, p.z), nor))-ref,
                      getGrey(tex3D(tex, vec3(p.x, p.y-eps, p.z), nor))-ref,
                      getGrey(tex3D(tex, vec3(p.x, p.y, p.z-eps), nor))-ref )/eps;

    grad -= nor*dot(nor, grad);          

    return normalize( nor + grad*bumpfactor );

}

// SDF result struct
struct SDFRes
{
    float d; // distance
    float m; // material
    float m2; // secondary material
    float b; // blend amount: 0=m, 1=m2
};

float saturate(float v)
{
    return clamp(v, 0.0, 1.0);
}

float getBlend(float d1, float d2)
{
    float diff = -abs(d1 - d2);
    float blend = diff / BLEND_SIZE;
    blend = saturate((blend + 1.0) * 0.5);
    return blend;
}

// Domain modifiers

// Repeat space along one axis. Use like this to repeat along the x axis:
// <float cell = pMod1(p.x,5);> - using the return value is optional.
float pMod1(inout float p, float size) {
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

// Operations
float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

float add(float d1, float d2)
{
    return min(d1, d2);
}

float substract(float d1, float d2)
{
    return max(d1, -d2);
}

SDFRes addSDF(in SDFRes f1, SDFRes f2 )
{
    if (f1.d < f2.d)
    	return f1;
    return f2;
}

SDFRes substractSDF(SDFRes f1, SDFRes f2 )
{
    float t = substract(f1.d, f2.d);
    return SDFRes(t, (t < f2.d) ? f1.m : f2.m, 0.0, 0.0);
}

SDFRes blendSDF(SDFRes f1, SDFRes f2, float k)
{
    // Branching a lot :( Needs more work

   	SDFRes closest  = f1;
    SDFRes furthest = f2;

	float diff = float(f1.d > f2.d);

	closest.d =  mix(f1.d, f2.d,   diff);
	closest.m =  mix(f1.m, f2.m,   diff);
	closest.m2 = mix(f1.m2, f2.m2, diff);
	closest.b =  mix(f1.b, f2.b,   diff);

	furthest.d =  mix(f2.d, f1.d,   diff);
	furthest.m =  mix(f2.m, f1.m,   diff);
	furthest.m2 = mix(f2.m2, f1.m2, diff);
	furthest.b =  mix(f2.b, f1.b,   diff);

	// Dominant materials
	float mf1 = mix(closest.m2, closest.m, float(closest.b < 0.5));
	float mf2 = mix(furthest.m2, furthest.m, float(furthest.b < 0.5));

    // New distance
    float t  = smin(f1.d, f2.d, k);

    // New blend
    float bnew = getBlend(f1.d, f2.d);
    float b = max(closest.b, bnew);
    float bhigher = float(b > bnew);

	float m  = mix(mf1, closest.m,  bhigher);
	float m2 = mix(mf2, closest.m2, bhigher);

    return SDFRes(t, m, m2, b);
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdSphere(in vec3 p, in float size )
{
    return length(p) - size;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float sdFloor( vec3 p, vec2 b )
{
  	vec2 d = abs(p.xy) - b;
    float t = p.y - b.y;

    //float h = 0.7 + 0.12 * (sin(p.x*8.3 - 1.));
    float h = 0.7 + (sin(p.z) + 1.0) * 0.06;
    float tiles = max(
        	pow(abs(sin(p.x*17.0 + 0.2)) * h, 18.0),
        	pow(abs(sin(p.z*17.0 + 0.2)) * h, 18.0)
        );    
    t -= 0.05 - tiles;

	return t;
}

float sdPlane( vec3 p )
{
    return p.y;
}

vec3 path(in float t)
{
    vec3 ret = vec3(0.0);
    ret.x -= sin(t*0.5) * 0.8;
    //ret.y -= sin(t*0.5) * 0.0;
    return ret;
}

float sdTunnel( vec3 p )
{   
    p.y += sin(54.0*cos(p.x))*0.05;
    //p.x -= sin(p.z) * sign(p.x) * 0.03;

    float t = 1.0 - length(p.xy);

  	return t;
}

SDFRes map(in vec3 pos )
{   
    pos -= path(pos.z);
    SDFRes plane = SDFRes( sdTunnel(  pos - vec3(0.0, 0.0, 0.0) ), 0.5, 0.0 ,0.0);

    vec3 p = pos - vec3(0.0, 0.44, 3.0);
    pMod1(p.z, 1.0);

    plane = addSDF( plane,
                  SDFRes( udRoundBox(p, vec3(1.0, 0.05, 0.1), 0.01 ), 5.0, 0.0, 0.0)
                  );

    p = pos - vec3(0.0, -0.2, 3.0);
    pMod1(p.z, 1.5);

    plane = substractSDF( plane,
                  SDFRes( sdBox(p, vec3(1.6, 0.5, 0.65) ), 0.5, 0.0, 0.0)
                  );

    plane = blendSDF( plane,
                  SDFRes( sdFloor(pos - vec3(0.0, -1.0, 0.0), vec2(1.0, 0.2) ), 2.0, 0.0, 0.0)
                     , 16.0
                  );

    /*plane = addSDF( plane,
                  SDFRes( sdFloor(pos - vec3(0.0, -1.0, 0.0), vec2(1.0, 0.2) ), 2.0, 0.0, 0.0)
                  );*/

    return plane;
}

vec3 getNormal(in vec3 pos )
{
    vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).d - map(pos-eps.xyy).d,
	    map(pos+eps.yxy).d - map(pos-eps.yxy).d,
	    map(pos+eps.yyx).d - map(pos-eps.yyx).d );
	return normalize(nor);
}

SDFRes castRay(in vec3 ro, in vec3 rd, in float tmin)
{    
    float tmax = 30.0;    

    SDFRes p;
    p.d = tmin;
    p.m = 0.0;
    p.m2 = 0.0;
    p.b = 0.0;

	float precis = 0.00000001;

    for (int i=0; i<50; i++)
    {
        SDFRes s = map( ro + rd * p.d );
        if (s.d < precis || s.d > tmax)
        {
            break;
        }

        p.m = s.m;
        p.m2 = s.m2;
        p.b = s.b;
        p.d += s.d;
    }

    if (p.d > tmax)
        p.m = -1.0;

    return p;
}


float shadows(in vec3 ro, in vec3 rd, in float tmin)
{
    //return 1.0;
    const float tmax = 0.95;
    float res = 1.0;
    float k = 20.0;

    float t = tmin;
    for( int i=0; i<50; i++)
    {
        SDFRes h = map(ro + rd*t);
        if( h.d<0.0001 )
            return 0.0;
        if (t > tmax)
            return res*1.0;

        res = min( res, k*h.d/t );
        t += h.d;
    }
    return res;
}

float occlusion(in vec3 ro, in vec3 rd)
{
    float occ = 0.0;
    float sca = 1.4;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  rd * hr + ro;
        float dd = map( aopos ).d;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

