                col = vec3(0.0, 0.0, 1.0);
            }*/
        }
        else
        {
          if (abs(pos.x) > 8.9)
            {
                col = vec3(1.0);
                //col = vec3(getGrey(tex3D(iChannel0, opos, nor)));
                nor = doBumpMap( iChannel1, opos, nor, 0.01);

            }
            else if (abs(pos.x) > 7.2)
            {
                col = vec3(1.0, 0.0, 0.0);
                col = tex3D(iChannel2, opos, nor);
                nor = doBumpMap( iChannel2, opos, nor, 0.01);
                spow = 256.0;
            }
            else
            {
                col = vec3(0.4, 0.2 , 0.0);
                spow = 256.0;
            }
        }
    }

    if( m < 2.5 && m > 1.5 )
    {
        nor = doBumpMap( iChannel1, pos, nor, 0.01);

        pos -= path(pos.z);
        pos.z *= 0.5;
        pos *= 2.0;
        col = texture(iChannel0, pos.xz).xyz;
        vec3 col2 = texture(iChannel1, pos.xz).xyz * vec3(0.6, 0.6, 0.5);
        col = mix(col2, col, abs(cos(pos.x*1.0)));
        spow = 100.0;
        //spow = pow(((col.x + col.y + col.z) / 3.0), 2.0) * 256.0;
        col = tex3D(iChannel3, pos, nor);
        //col = vec3(1.0, 1.0, 1.0) * 0.9;
    }

    // 200, 245, 240
}

vec3 getFinalColour(in vec3 pos, in float m, in float m2, in float b, inout vec3 nor, inout float spow)
{
    vec3 col;

    getMaterialData(m, pos, nor, col, spow);
    vec3 col2;
    float spow2;
    vec3 nor2;
    getMaterialData(m2, pos, nor2, col2, spow2);

    #ifdef VIEW_SECOND_COL
    col = col2;
    #endif

    col = mix(col, col2, b);
    spow = mix(spow, spow2, b);
    nor = mix(nor, nor2, floor(b));

    #ifdef VIEW_BLEND
    col = mix(vec3(col), vec3(0.0, 1.0, 0.0), b*2.0);
    #endif

    return col;
}

vec3 getColour(in vec3 ro, in vec3 rd, in float tmin)
{
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;  
    vec3 dlig = normalize( vec3(0.6, 0.5, 0.7) );

    SDFRes ray = castRay(ro, rd, tmin);
    float t = ray.d;
    float m = ray.m;
    float m2 = ray.m2;
    float b = ray.b;
    float spow = 128.0;

    if( m >-0.5 )
    {
        vec3 pos = ro + rd * t;
        vec3 nor = getNormal(pos);
        vec3 ref = reflect( rd, nor );

        col = getFinalColour(pos, m, m2, b, nor, spow);

        // repeat the point light
        float ld = 10.0;
        float lp = round(pos.z / ld) * ld;        
        // Light in front of you
        lp = iTime*CAMSPEED + 3.0 + sin(iTime) * 2.0;
        vec3 lightPos = vec3( 0.0, 0.0, lp );
        lightPos -= path(lightPos.z + PI*2.0);
        vec3 lig = normalize(lightPos - pos);

        // point light
        float ldist = length(lightPos - pos);
        float kc = 1.0;
        float kq = 0.9;

        // light attenuation
        float att = 1.0 / ( kc + pow(kq*ldist, 4.0));
        float dif = att * dot(nor, lig) * 3.0;


        float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),spow);
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
        float dom = smoothstep( -0.1, 0.1, ref.y );

        float shadow = shadows(pos, lig, 0.1);
        float occ = occlusion(pos, nor);

        dif *= shadow;
        dom *= shadows(pos, nor, 0.01);        

        vec3 lin = vec3(0.0);
        lin += dif*occ; // I shouldn't use the occlusion here, but I like the result
        lin += spe*dif*occ;
        //lin += fre*0.03*occ;
        //lin += amb*0.02*occ;
        //lin += dom*0.1*occ;
        //lin = vec3(occ);

        //col = vec3(spe);

        col = col * lin;
    }

    //col /= ray.d;

    return vec3(col);
}


vec3 render(in vec3 ro, in vec3 rd )
{
    return getColour(ro, rd, 0.1);
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

    float time = 15.0 + iTime;

    // camera	
	vec3 ro = vec3( 0.0, 0.0, iTime*CAMSPEED );
    ro -= path(ro.z + PI*2.0);
	vec3 ta = ro + vec3(0.0, 0.0, 1.0);
    ta -= path(ro.z)*0.3;


    mat3 camera = setCamera( ro, ta, 0.0 );

    // ray direction
	vec3 rd = camera * normalize( vec3(p.xy, 2.0) );

    // render	
    vec3 col = render( ro, rd );

	col = pow( col, vec3(0.4545) ); // gamma

    fragColor=vec4( col, 1.0 );

}

