float colorSteps = 8.;
float scale = 0.25;
float timeFactor = 0.03;
float blur = 1.;

float rgbToGray(vec4 rgba) {
	const vec3 W = vec3(0.2125, 0.7154, 0.0721);
    return dot(rgba.xyz, W);
}

vec2 rotateCoord(vec2 uv, float rads) {
    uv *= mat2(cos(rads), sin(rads), -sin(rads), cos(rads));
	return uv;
}

float halftoneDots(vec2 uv, float rows, float curRadius, float curRotation, float invert) {
    // update layout params
    // get original coordinate, translate & rotate
    uv = rotateCoord(uv, curRotation);
    // calc row index to offset x of every other row
    float rowIndex = floor(uv.y * rows);		
    float oddEven = mod(rowIndex, 2.);
    // create grid coords
    vec2 uvRepeat = fract(uv * rows) - 0.5;		
    if(oddEven == 1.) {							// offset x by half
        uvRepeat = fract(vec2(0.5, 0.) + uv * rows) - vec2(0.5, 0.5);	
    }
    // adaptive antialiasing, draw, invert
    float aa = iResolution.y * rows * 0.00001; 	
    float col = smoothstep(curRadius - aa, curRadius + aa, length(uvRepeat));
    if(invert == 1.) col = 1. - col;			
    return col;
}

float halftoneLines(vec2 uv, float rows, float curThickness, float curRotation, float invert) {
    // get original coordinate, translate & rotate
    uv = rotateCoord(uv, curRotation);
    // create grid coords
    vec2 uvRepeat = fract(uv * rows);		
    // adaptive antialiasing, draw, invert
    float aa = iResolution.y * 0.0003; 	
    float col = smoothstep(curThickness - aa, curThickness + aa, length(uvRepeat.y - 0.5));
    if(invert == 1.) col = 1. - col;			
   	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coords
	vec2 uvDraw = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x = 1. - uv.x; // mirror

    // get texture color
	vec4 color = texture(iChannel0, uv);

    if(blur == 1.) {
    	// get samples around pixel for averaged color
    	float stepH = 1.;
    	float stepV = 1.;
		vec4 colors[9];
    	float stepX = stepH/iResolution.x;
    	float stepY = stepV/iResolution.y;
    	colors[0] = texture(iChannel0, uv + vec2(-stepX, stepY));
		colors[1] = texture(iChannel0, uv + vec2(0, stepY));
		colors[2] = texture(iChannel0, uv + vec2(stepX, stepY));
		colors[3] = texture(iChannel0, uv + vec2(-stepX, 0));
		colors[4] = texture(iChannel0, uv);
		colors[5] = texture(iChannel0, uv + vec2(stepX, 0));
		colors[6] = texture(iChannel0, uv + vec2(-stepX, -stepY));
		colors[7] = texture(iChannel0, uv + vec2(0, -stepY));
		colors[8] = texture(iChannel0, uv + vec2(stepX, -stepY));

    	// apply color steps to original color
    	color = vec4(0.);
    	for(int i=0; i < 9; i++) {
    	    color += colors[i];
    	}
    	color = vec4((color.rgb / 9.), 1.);
    }

    // color = vec4(vec3(1. - uv.x), 1.); // test gradient
    float luma = rgbToGray(color) * 1.;
    float lumaIndex = floor(luma * colorSteps);
   	float lumaFloor = lumaIndex / colorSteps;

    // time
    float time = iTime * timeFactor;

    // posterize -> halftone gradient configurations
    float halftoneCol = 0.;
    if(lumaIndex == 0.) {
		halftoneCol = halftoneDots(uvDraw, scale * 50., 0.1, 0.2 + time, 1.);
    } else if(lumaIndex == 1.) {
   		halftoneCol = halftoneLines(uvDraw, scale * 84., 0.08, 2. - time, 1.);
    } else if(lumaIndex == 2.) {
		halftoneCol = halftoneDots(uvDraw, scale * 120., 0.45, 0.8 + time, 0.);
	} else if(lumaIndex == 3.) {
		halftoneCol = halftoneDots(uvDraw, scale * 60., 0.37, 0.5 - time, 1.);
  	} else if(lumaIndex == 4.) {
		halftoneCol = halftoneLines(uvDraw, scale * 40., 0.18, 2. + time, 0.);
    } else if(lumaIndex == 5.) {
		halftoneCol = halftoneDots(uvDraw, scale * 60., 0.34, 0.5 - time, 0.);
    } else if(lumaIndex == 6.) {
		halftoneCol = halftoneLines(uvDraw, scale * 84., 0.15, 2. + time, 0.);
	} else if(lumaIndex >= 7.) {
		halftoneCol = halftoneDots(uvDraw, scale * 50., 0.1, 0.2 - time, 0.);
    } 
    fragColor = vec4(vec3(halftoneCol),1.0);
}

mat3 rot (vec3 s) {
    float 	sa = sin(s.x),
        ca = cos(s.x),
        sb = sin(s.y),
        cb = cos(s.y),
        sc = sin(s.z),
        cc = cos(s.z);
    return mat3 (
        vec3(cb*cc, -cb*sc, sb),
        vec3(sa*sb*cc+ca*sc, -sa*sb*sc+ca*cc, -sa*cb),
        vec3(-ca*sb*cc+sa*sc, ca*sb*sc+sa*cc, ca*cb)
    );
}
mat3 mm;
vec4 light;
vec3 grp;
float ui;
float map (vec3 p) {
    float d = length(p-light.xyz)-light.w;
    d = min(d,max(30.-p.z,0.));
    grp=p*0.01;

    p = mm*p;
    for (int i = 0; i < 5; i++) {
        float t = abs(p.y);
        p.y = p.x;
        p.x = t;
        p = mm*(p-.1);
    }
    float q = 5.;
    float a = 1.5*((atan(p.x,p.z)))+0.05*ui;
    mat2 mn = mat2(sin(a),cos(a),-cos(a),sin(a));
    vec2 u = mn*vec2(length(p.xz)-2.,p.y);
    float d1 = d;
    vec2 w = max(abs(u)-vec2(0.1,0.5),0.);
    d = min(d,length(w)-0.1-0.01*(sin(50.*(dot(u,u))+sin(20.*atan(p.x,p.z)))));
    if (d1 != d) grp = vec3(u,atan(p.x,p.z));
    return d;
}
vec3 norm (vec3 p) {
    vec2 e = vec2 (.001,0.);
    return normalize(vec3(
        map(p+e.xyy) - map(p-e.xyy),
        map(p+e.yxy) - map(p-e.yxy),
        map(p+e.yyx) - map(p-e.yyx)
    ));
}
vec3 dive (vec3 p, vec3 d) {
    for (int i = 0; i < 35; i++) {
        p += d*map(p);
    }
    return p;
}
vec3 color (vec3 no, vec3 p) {
    return vec3(.8+0.2*(sin(30.*p*p.zxy+no)*0.5+0.5))*0.9;
}
void mainImage (out vec4 fragColor, vec2 vv) {
    vec2 v = (vv/iResolution.xy)*2.-1.;
    v.x *= iResolution.x/iResolution.y;
    ui = 30.*iTime;
    vec3 r = vec3(0,0,-15.);
    light = vec4(10.*sin(0.005*ui),2,-23,1);
    vec3 d = normalize(vec3(v,3.));
    mm = rot((0.005*ui)+vec3(1,2,3));
    vec3 p = dive(r,d);
    d = normalize(light.xyz-p);
    vec3 no = norm(p);
    vec3 col = color(no,grp);
    vec3 bounce = dive(p+0.01*d,d);
    col *= dot(no, normalize(light.xyz-p));
    if (length(bounce-light.xyz) > light.w+0.1) col *= 0.2;
    if (length (p-r)>4e2) col *= 0.;
    fragColor = vec4(col,1);
}

const float PI = 3.141592;
const float TWO_PI = 2. * PI;
const float FOUR_PI = 4. * PI;

const vec3 BLACK = vec3(0);
const vec3 WHITE = vec3(1);
const vec3 LIGHT_GRAY = vec3(0.78);
const vec3 RED = vec3(1, 0, 0);
const vec3 GREEN = vec3(0, .78, 0);
const vec3 BLUE = vec3(0, 0, 1);
const vec3 YELLOW = vec3(1, 1, 0);
const vec3 PINK = vec3(.78, 0, .78);
const vec3 LIGHT_BLUE = vec3(0, 1, 1);

const float CIRCLE_RADIUS = 0.02;
const float OUTER_CIRCLE_GRAY_SCALE = 0.4;

// from Syntopia http://blog.hvidtfeldts.net/index.php/2015/01/path-tracing-3d-fractals/
vec2 rand2n(vec2 co, float sampleIndex) {
    vec2 seed = co * (sampleIndex + 1.0);
	seed+=vec2(-1,1);
    // implementation based on: lumina.sourceforge.net/Tutorials/Noise.html
    return vec2(fract(sin(dot(seed.xy ,vec2(12.9898,78.233))) * 43758.5453),
                fract(cos(dot(seed.xy ,vec2(4.898,7.23))) * 23421.631));
}

const float GAMMA_COEFF = 2.2;
const float DISPLAY_GAMMA_COEFF = 1. / GAMMA_COEFF;

// Suggestions by FabriceNeyret2.
#define gammaCorrect(rgb)  min( pow( rgb, vec3(DISPLAY_GAMMA_COEFF) ), 1.)
#define degamma(rgb)       min( pow( rgb, vec3(GAMMA_COEFF) ), 1.)
#define compProd(a,b)      mat2(a,-a.y,a.x) * b
#define compQuot(a,b)      mat2(a,(a).y,-(a).x)  * (b) / dot(b, b)

vec2 compFunc(vec2 position){
	return position = compQuot(position - vec2(1. + sin(iTime), 0.4 + cos(iTime)), compProd(position, position) + position + vec2(1, 0)); 
	//return position = compQuot(position - vec2(1., 0.), compProd(position, position) + position + vec2(1, 0));         
}

vec3 hsv2rgb(vec3 c) {
    const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

const float GRAY_SCALE = 0.6;
float computeGrayComponent(vec2 position, out float arg) {
    float absModulus = 2. * log(length(position));
    float modulusG = mix(1., fract(absModulus), GRAY_SCALE);

    arg = atan(position.y, position.x) / TWO_PI;
    float argG = mix(1., fract(arg * 10.), GRAY_SCALE);

    return modulusG * argG;
}

vec3 calcRay (const vec3 eye, const vec3 target,
              const vec3 up, const float fov,
              const float width, const float height, const vec2 coord){
    float imagePlane = (height * .5) / tan(radians(fov) * .5);
    vec3 v = normalize(target - eye);
    vec3 focalXAxis = normalize(cross(v, up));
    vec3 focalYAxis =  normalize(cross(v, focalXAxis ));
    vec3 center = v * imagePlane;
    vec3 origin = center - (focalXAxis * (width  * .5)) - (focalYAxis * (height * .5));
    return normalize(origin + (focalXAxis * coord.x) + (focalYAxis * (height - coord.y)));
}

const float EPSILON = 0.0001;
vec4 intersectSphere(int objId, int mtl,
                     vec3 sphereCenter, float radius,
                     vec3 rayOrigin, vec3 rayDir,
                     vec4 isect, inout int outObjId){
    vec3 v = rayOrigin - sphereCenter;
    float b = dot(rayDir, v);
    float c = dot(v, v) - radius * radius;
    float d = b * b - c;
    if(d >= 0.){
        float s = sqrt(d);
        float t = -b - s;
        if(t <= EPSILON) t = -b + s;
        if(EPSILON < t && t < isect.x){
            vec3 intersection = (rayOrigin + t * rayDir);
            outObjId = objId;
            return vec4(t, normalize(intersection - sphereCenter));
        }
    }
    return isect;
}

vec4 intersectPlane(int objId, int mtl,
                    vec3 p, vec3 n,
                    vec3 rayOrigin, vec3 rayDir, vec4 isect,
                   	inout int outObjId){
    float d = -dot(p, n);
    float v = dot(n, rayDir);
    float t = -(dot(n, rayOrigin) + d) / v;
    if(EPSILON < t && t < isect.x){
        outObjId = objId;
        return vec4(t, n);
    }
    return isect;
}

const float NO_HIT = 99999999.;
const int MTL_DIFFUSE = 0;
const int MTL_TRANSPARENT = 1;

const int OBJ_PLANE = 0;
const int OBJ_SPHERE = 1;

vec4 getIntersection(vec3 eye, vec3 ray, out int outObj){
    outObj = -1;
    vec4 isect = vec4(NO_HIT);
    isect = intersectPlane(OBJ_PLANE, MTL_DIFFUSE,
                           vec3(0, -2, 0), vec3(0, 1, 0),
                           eye, ray, isect, outObj);
    isect = intersectSphere(OBJ_SPHERE, MTL_DIFFUSE,
                            vec3(0), 1.,
                            eye, ray, isect, outObj);
    return isect;
}

vec3 diffuseLighting(const vec3 p, const vec3 n, const vec3 diffuseColor,
                     const vec3 lightPos, const vec3 lightPower){
    vec3 v = lightPos - p;
    float d = dot(n, normalize(v));
    float r = length(v);
    return (d > 0. )//&& visible(p + EPSILON * n, lightPos)) 
        ?
        (lightPower * (d / (FOUR_PI * r * r))) * diffuseColor
        : BLACK;
}

vec2 stereoProject(vec3 p){
	return vec2(p.x / (1. - p.y), p.z / (1. - p.y));
}

const vec3 LIGHT_DIR = normalize(vec3(0.0, 1., 0.5));
const vec3 LIGHT_POS = vec3(3, 5, 0);
const vec3 LIGHT_POWER = vec3(300.);
const vec3 AMBIENT_FACTOR = vec3(0.1);

vec3 castRay(vec3 eye, vec3 ray) {
    vec3 l = BLACK;
    int objId;
    vec4 isect = getIntersection(eye, ray, objId);
	if(isect.x != NO_HIT){
		vec3 matColor = WHITE;
        vec3 normal = isect.yzw;
		vec3 intersection = eye + isect.x * ray;
		if(objId == OBJ_PLANE){
			matColor = LIGHT_GRAY;
		}else if(objId == OBJ_SPHERE){
            vec2 position = stereoProject(intersection);
            if (abs(length(position) - 1.) < 0.02) {
                matColor = BLACK;
            } else {
            	position = compFunc(position); 

            	float arg;
				float g = computeGrayComponent(position, arg);
				matColor = degamma(hsv2rgb(vec3(arg, 1., g)));
            }
		}
		// diffuse lighting by directionalLight
		//vec3 diffuse = clamp(dot(normal, LIGHT_DIR), 0., 1.) * matColor;
		vec3 diffuse = diffuseLighting(intersection, normal, matColor,
									   LIGHT_POS, LIGHT_POWER);
		vec3 ambient = matColor * AMBIENT_FACTOR;
		l += (diffuse + ambient);
	}  
    return l;
}



const int SAMPLE_NUM = 20;
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 sum = vec3(0);
	float ratio = iResolution.x / iResolution.y / 2.0;

    const vec3 up = vec3(0, 1, 0);
    const float fov = 60.;

    for(int i = 0 ; i < SAMPLE_NUM ; i++){        
        if(fragCoord.x < iResolution.x/2.){
            float my = 3. * (iMouse.y - iResolution.y * 0.5) / iResolution.y + 0.5;
            float r =  1. + 2. * iMouse.x / iResolution.x;
            vec3 eye = vec3(r * sin(iTime), r * sin(atan(my, 1.)), r * cos(iTime));
    		vec3 target = vec3(0, 0, 0);
        	vec3 ray = calcRay(eye, target, up, fov,
                         	   iResolution.x/2., iResolution.y,
                         	   fragCoord.xy + rand2n(fragCoord.xy, float(i)));
        	sum += castRay(eye, ray);
        }else{
            vec2 size = iResolution.xy / 2.;
        	vec2 p = 2. * (vec2(fragCoord.x - size.x, fragCoord.y) - size * 0.5) / size.y
                     + 2. * rand2n(fragCoord.xy, float(i)) / size.y;
            if(p.y <= 1.){
                vec2 position = p * 1.1;
                if (abs(length(position) - 1.) < CIRCLE_RADIUS) continue;
                float s = length(position) > 1. ? OUTER_CIRCLE_GRAY_SCALE : 1.;

                position = compFunc(position);

                float arg;
				float g = computeGrayComponent(position, arg);
				sum += hsv2rgb(vec3(arg, 1., g * s));
            } else {
                p.y -= 2.;
				vec2 position = p * 1.1;
                if (abs(length(position) - 1.) < CIRCLE_RADIUS) continue;
                float s = length(position) > 1. ? OUTER_CIRCLE_GRAY_SCALE : 1.;

				position = compFunc(compQuot(vec2(1, 0), position)); // 1 / z

                float arg;
				float g = computeGrayComponent(position, arg);
                sum += hsv2rgb(vec3(arg, 1., g * s));
            }
        }

    }

    if(fragCoord.x < iResolution.x/2.){
    	fragColor = vec4(gammaCorrect(sum/float(SAMPLE_NUM)), 1.);
    } else {
    	fragColor = vec4((sum/float(SAMPLE_NUM)), 1.);
    }
}

