vec3 rotZ(vec3 v, float radians) {
  float c = cos(radians);
  float s = sin(radians);
  return vec3( v.x * s - v.y * c,
               v.x * c + v.y * s,
               v.z);
}

vec3 rotY(vec3 v, float radians) {
  float c = cos(radians);
  float s = sin(radians);
  return vec3( v.x * s - v.z * c,
               v.y,
               v.x * c + v.z * s);
}

vec4 sdfSphere(vec3 p) {
  float f = iGlobalTime * 0.1;
  p = rotY(p, f * 2.);
  vec3 q = p;
  const float overallDistortionMagnitude = 17.;
  const float mag2 = .2;
  const float freq = 20.;
  float mag = sin(iGlobalTime * 0.1) * overallDistortionMagnitude;
  q.x += cos(p.z         * mag * 0.9 + f) * 0.1;
  q.y += sin(p.z         * mag * 0.8 + f) * 0.1;
  q.z += sin((p.x * p.y) * mag * 1.0 + f * 10.) * 0.1;
  float dx = sin(p.z * freq) * mag2;
  float dy = sin(p.x * freq) * mag2;
  float dz = sin(p.y * freq) * mag2;
  dx *= dx * dx * dx;
  dy *= dy * dy * dy;
  dz *= dz * dz * dz;
  q.x += dx;
  q.y += dy;
  q.z += dz;
  return vec4(length(q) - 0.75, p.x, p.y, p.z);
}

vec4 sdfSceneSingle(vec3 p) {
  vec4 ret = sdfSphere(p);
  return ret;
}

vec4 sdfSceneDual(vec3 p) {
  float sep = 1.25;
  p.x += sep / 2.;
  vec4 ret1 = sdfSphere(p);
  p.x -= sep;
  vec4 ret2 = sdfSphere(p);
  vec4 ret;
  if (ret1.x < ret2.x) {
    ret = ret1;
  }
  else {
    ret = ret2;
  }
  return ret;
}

vec4 sdfScene(vec3 pt) {
  return sdfSceneSingle(pt);
}

vec3 sdfNormal(vec3 p) {
  const float epsilon = 0.001;
  vec3 v = vec3(
    sdfScene(vec3(p.x + epsilon, p.y, p.z)).x - sdfScene(vec3(p.x - epsilon, p.y, p.z)).x,
    sdfScene(vec3(p.x, p.y + epsilon, p.z)).x - sdfScene(vec3(p.x, p.y - epsilon, p.z)).x,
    sdfScene(vec3(p.x, p.y, p.z + epsilon)).x - sdfScene(vec3(p.x, p.y, p.z - epsilon)).x
  );

  return normalize(v);
}

bool approximatelyEqualEps(float a, float b, float epsilon) {
  float d = abs(a - b);
  return d < epsilon ? true : false;
}

bool approximatelyEqual(float a, float b) {
  const float epsilon = 0.0001;
  return approximatelyEqualEps(a, b, epsilon);
}

// todo: generalize to any plane
vec3 intersectRayXZPlane(vec3 pt, vec3 dir) {
  if (approximatelyEqual(dir.y, 0.)) {
    return vec3(kMaxFloat, 0, kMaxFloat);
  }

  float s = pt.y / dir.y;
  return pt + dir * s;
}

float subfloor(vec3 groundPt) {
  groundPt = rotY(groundPt, 45.0 * degToRad);
  groundPt += vec3(
    sin(iGlobalTime * 0.12  ) * 10.,
    1,
    sin(iGlobalTime * 0.1211) * 10.
  );
  float f = sin(groundPt.x * 1.2) * sin(groundPt.z * 1.2);
  f *= f * f;
  return f * 1.9 + 1.5;
}

float floorCheck(vec3 pt, vec3 rayDir) {
  rayDir = rotY(rayDir, iGlobalTime * 0.031);
  vec3 floorOffset = vec3(0, floorHeight, iGlobalTime * -4.1);
  vec3 groundPt = intersectRayXZPlane(pt + floorOffset, rayDir);
  float f = sin(groundPt.x) + sin(groundPt.z) - sin(iGlobalTime * 0.51) * 0.8;
  float rf;
  if (approximatelyEqualEps(f, 0., 0.07)) {
    rf = 0.18;
  }
  else if (f < 0.) {
    rf = 0.12;
  }
  else {
    rf = subfloor(groundPt) * 0.1;
  }

  return rf;
}

void otherMain(void)
{
  vec3  lightDir = normalize(vec3(iMouse.x / iResolution.x * 2.0 - 1.0, iMouse.y / iResolution.y * 2.0 - 1.0, -0.2));

  lightDir = normalize(vec3(
    cos(iGlobalTime * 1.13) * 2.0,
    sin(iGlobalTime * 1.13) * 2.0,
    sin(iGlobalTime * 0.5 ) * 1.2));

  vec3  camPos = vec3(0, 0, -4.0);
  float screenDist = 3.5;

  vec2 tmp = vTexcoord * 2.0 - 1.0;
  vec3 pxlPos;
  if (iAspect > 1.0) {
    pxlPos = vec3(tmp.x * iAspect, tmp.y, camPos.z + screenDist);
  }
  else {
    pxlPos = vec3(tmp.x, tmp.y / iAspect, camPos.z + screenDist);
  }

  vec3 rayDir = normalize(pxlPos - camPos);

  float maxZ = pxlPos.z + maxDist;

  vec3 pt = pxlPos;

  float finalDist;

  int steps = 0;

  bool hitSomething = false;

  const float skyBrightness = 0.2;

  oColor = vec4(skyBrightness);


  while (length(pt) < maxZ) {
    vec4 sdf = sdfScene(pt);
    finalDist = sdf.x;
    if (finalDist <= 0.0001) {
      hitSomething = true;
      vec3  n = sdfNormal(pt);
      float d = dot(n, lightDir);
      vec3 refl = reflect(rayDir, n);
      float edgeness = 1. - abs(dot(n, rayDir));
      float rf = 0.;
      if (refl.y < floorAmt) {
        rf = floorCheck(pt, refl) * 0.6;
      }
      oColor = vec4(
        (sin(sdf.w * 60.) * 0.2 + 0.7) * d,
        (sin(sdf.x * 80.) * 0.2 + 0.7) * d,
        (sin(sdf.y * 60.) * 0.2 + 0.6) * d,
        1.0);
      oColor = vec4(
        max(oColor.x, rf),
        max(oColor.y, rf),
        max(oColor.z, rf),
        1
      );
      float e = edgeness;
      e = e * e * e * e;
      e = e * e;
      oColor -= vec4(e);
      break;
    }
    const float minStep = 0.002;
    float step = max(finalDist * 0.5, minStep);
    // pt.z += step;
    pt += rayDir * step;
  }

  if (!hitSomething) {
    if (rayDir.y < floorAmt) {
      float rf = floorCheck(camPos, rayDir);
      oColor = vec4(rf, rf, rf, 1);

    }
  }


}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    iAspect     = iResolution.x / iResolution.y;
    iGlobalTime = iTime;
    vTexcoord = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    otherMain();
    fragColor = oColor;
}

////////////////////////////////////////////////////////////////////////////////
//
// Created by Matthew Arcus, 2018
//
// A tetrahedron contains an octahedron with parallel opposite
// edges (between the midpoints of the edges of the tetrahedron),
// and can also be inscribed uniquely in a parallelepiped
// sharing its vertices. The octahedron and parallelepiped
// are dual.

// Mouse to move, 'o' and 'p' toggle display of octahedron
// and parallelepiped.
//
// Raytraced spheres and cylinders, solved analytically
//
////////////////////////////////////////////////////////////////////////////////

bool dorotate = true;
bool dointernal = true;
bool doexternal = true;

const float PI = 3.14159;

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // (unnormalized) normal
  int id;       // what was hit
};

struct Sphere {
  float r2;      // radius squared
  vec3 p;       // centre
  int id;
};

struct Cylinder {
  float r2;      // radius squared
  // points s and t are end points of cylinder
  vec3 s,t;
  int id;
};

// Use vec2 return?
// Solve Ax^2 + 2Bx + C = 0
bool quadratic0(float A, float B, float C, out float x0, out float x1) {
   float D = B*B - A*C;
   if (D < 0.0) return false;
   D = sqrt(D);
   if (B < 0.0) D = -D;
   x0 = (-B-D)/A;
   x1 = C/(A*x0);
   if (B < 0.0) {
     // return smallest root first
     float t = x0; x0 = x1; x1 = t;
   }
   return true;
}

bool quadratic(float A, float B, float C, out float x) {
  float x1,x2;
  if (!quadratic0(A,B,C,x1,x2)) return false;
  if (x1 > 0.0) x = x1;
  else if (x2 > 0.0) x = x2;
  else return false;
  return true;
}

bool intersectSphere(Sphere s, Ray ray, inout Hit hit) {
  vec3 p = s.p;
  float r2 = s.r2;
  float c2 = dot(p,p);
  vec3 q = ray.q, d = ray.d;
  // |q + t*d - p|^2 = r^2
  float A = 1.0;
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+c2-r2;
  float t;
  if (!quadratic(A,B,C,t)) return false;
  if (t < 0.0 || t >= hit.t) return false;
  // Normal is the radial vector of sphere
  // We normalize it later
  hit = Hit(t, q+t*d-p, s.id);
  return true;
}

bool cylinder(vec3 q, vec3 s, float r2, vec3 p, vec3 r, out float t, out vec3 normal) {
  vec3 n = s-q; // Line direction
  float k2 = dot(n,n);
  vec3 p1 = p-q; // Move p to line space
  float rs = dot(r,n);
  float ps = dot(p1,n);
  float pr = dot(p1,r);
  float pp = dot(p1,p1);
  float A = 1.0 - rs*rs/k2;
  float B = pr - ps*rs/k2;
  float C = pp - ps*ps/k2 - r2;
  if (!quadratic(A,B,C,t)) {
    return false;
  } else {
    p1 += t*r; // Final point in line space
    float lambda = dot(p1,n)/k2;
    if (lambda < 0.0 || lambda > 1.0) return false;
    normal = p1-lambda*n;
    return true;
  }
}

bool intersectCylinder(Cylinder c, Ray ray, inout Hit hit) {
  vec3 s0 = c.s;
  vec3 s1 = c.t;
  float r2 = c.r2;
  vec3 q = ray.q, d = ray.d;
  float t;
  vec3 normal;
  if (!cylinder(s0,s1,r2,q,d,t,normal) || t >= hit.t) {
    return false;
  } else {
    // Normal is the radial vector of cylinder
    hit = Hit(t, normal, c.id);
    return true;
  }
}

vec2 rotate(vec2 p, float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  if (dorotate) {
    float t = iTime;
    p.zx = rotate(p.zx,t * 0.2);
  }
  return p;
}

// (p+P)(q+Q) = pq + pQ + qP + PQ
vec4 qmul(vec4 p, vec4 q) {
  vec3 P = p.xyz, Q = q.xyz;
  return vec4(p.w*Q+q.w*P+cross(P,Q),p.w*q.w-dot(P,Q));
}

bool intersectScene(Ray ray, out Hit hit) {
  vec4 A4 = vec4(1,0,0,0);
  vec4 B4 = vec4(0,1.5,0,0);
  vec4 C4 = vec4(0,0,1.1,0);
  vec4 D4 = vec4(0,0,0,1.2);
  // Quaternion, w is real part
  float t = 0.2*iTime;
  vec4 Q = vec4(sin(t)*vec3(1,0.618,0),cos(t));
  A4 = qmul(A4,Q);
  B4 = qmul(B4,Q);
  C4 = qmul(C4,Q);
  D4 = qmul(D4,Q);
  vec3 A = A4.xyz;
  vec3 B = B4.xyz;
  vec3 C = C4.xyz;
  vec3 D = D4.xyz;
  {
    // Put centroid at origin
    vec3 X = 0.25*(A+B+C+D);
    A -= X; B -= X; C -= X; D -= X; X -= X;
  }
  hit = Hit(1e8,vec3(0),0);
  float s2 = 0.04*0.04;
  float c2 = 0.015*0.015;
  // Draw centroid at origin
  if (dointernal) intersectSphere(Sphere(s2,vec3(0),1),ray,hit);
  vec3 points[4];
    points[0] = A;
    points[1] = B;
    points[2] = C;
    points[3] = D;
  for (int i = 0; i < 4; i++) {
    vec3 X = points[i];
    intersectSphere(Sphere(s2,X,0),ray,hit);
#if __VERSION__ < 300
      // My phone miscompiles this version
    for (int j = 0; j < 4; j++) {
      if (j <= i) continue; // WebGL 1.0
#else
    for (int j = i+1; j < 4; j++) {
#endif
      vec3 Y = points[j];
      intersectCylinder(Cylinder(c2,X,Y,1),ray,hit);
      if (dointernal) {
        vec3 XY = 0.5*(X+Y);
        intersectSphere(Sphere(s2,XY,2),ray,hit);
        for (int k = 0; k < 4; k++) {
          if (k == i || k == j) continue;
          // The lines on the faces between edge midpoints
          // These assemble into an octahedron
          vec3 Z = points[k];
          vec3 XZ = 0.5*(X+Z);
          vec3 YZ = 0.5*(Y+Z);
          intersectCylinder(Cylinder(c2,XY,XZ,4),ray,hit);
          intersectCylinder(Cylinder(c2,XY,YZ,4),ray,hit);
        }
      }
    }
  }
  // The diagonals
  if (dointernal) {
    intersectCylinder(Cylinder(c2,0.5*(A+B),0.5*(C+D),3),ray,hit);
    intersectCylinder(Cylinder(c2,0.5*(A+C),0.5*(B+D),3),ray,hit);
    intersectCylinder(Cylinder(c2,0.5*(A+D),0.5*(B+C),3),ray,hit);
  }

  if (doexternal) {
    // The containing parallelepiped. Just invert the points in
    // the origin (which is the centroid).
    for (int i = 0; i < 4; i++) {
      vec3 X = -points[i];
      intersectSphere(Sphere(s2,X,1),ray,hit);
      for (int j = 0; j < 4; j++) {
        if (i == j) continue;
        vec3 Y = points[j];
        intersectCylinder(Cylinder(c2,X,Y,3),ray,hit);
      }
    }
  }
  return hit.t < 1e8;
}

vec3 light;
float ambient;
float diffuse;

vec3 getColor(int i) {
  if (i == 0) return vec3(1,0,0);
  if (i == 1) return vec3(1,1,0);
  if (i == 2) return vec3(0,1,0);
  if (i == 3) return vec3(0,0,1);
  if (i == 4) return vec3(0,1,1);
  return vec3(1,1,1);
}

vec4 solve(Ray r) {
  Hit hit;
  if (!intersectScene(r,hit)) {
    return vec4(0,0,0,1);
  } else {
    vec3 n = normalize(hit.n);
    if (dot(r.d,n) > 0.0) n *= -1.0;
    vec3 baseColor = 0.7*getColor(hit.id);
    vec3 color = baseColor.xyz*(ambient+diffuse*max(0.0,dot(light,n)));
    float specular = pow(max(0.0,dot(reflect(light,n),r.d)),2.0);
    color += 0.5*specular*vec3(1.0,1.0,1.0);
    color *= clamp(1.0 - (hit.t-3.0)/5.0,0.0,1.0);
    return vec4(sqrt(color),1.0);
  }
}

void mainVR( out vec4 fragColor, in vec2 fragCoord,
             vec3 fragRayOrigin, vec3 fragRayDir) {
  float t = 0.5*iTime;
  light = normalize(vec3(0.5,1.0,-1.0));
  ambient = 0.5;
  diffuse = 1.0-ambient;
  fragColor = solve(Ray(fragRayOrigin,fragRayDir));
}


