  doexternal = !keypress(CHAR_P);
  vec2 uv = 2.0*fragCoord.xy/iResolution.xy - 1.0;
  vec3 p = vec3(0,0,-4.0);
  // "screen" coordinate
  vec3 s = 0.8*vec3(iResolution.x/iResolution.y * uv.x, uv.y, 0);
  vec3 d = s-p; // Direction from camera to screen point
  p = transform(p);
  d = transform(d);// normalize after rotation!
  d = normalize(d);
  mainVR(fragColor,fragCoord,p,d);
}

#define PI 3.14159265359
#define NUM_STRIPES 32.0
#define STRIPE_WIDTH .1
#define ANGLE_STRIPES PI * .25
#define CLAMP_TOP .315
#define CLAMP_BOTTOM .155
#define SMOOTH_WIDTH .04


mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float easeInExpo(float t) {
	return t == 0.0 ? t : pow(2.0, 10.0 * (t - 1.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 vUv = fragCoord/iResolution.xy;
    vec2 rotatedUv = vUv;

    // rotate context
    rotatedUv -= vec2(0.5);
    if ( rotatedUv.x < 0.0 ) {
    	rotatedUv *= rotate2d( ANGLE_STRIPES );
    } else {
        rotatedUv *= rotate2d( -ANGLE_STRIPES );
    }
    rotatedUv += vec2(0.5);

    vec2 stripeUv = rotatedUv;


    if ( stripeUv.y < CLAMP_BOTTOM || stripeUv.y > 1.0 - CLAMP_TOP ) discard;
    stripeUv.y = 1.0 - fract( stripeUv.y * NUM_STRIPES );
    // vUv.y = fract( vUv.y + iTime );

    float stripes = smoothstep( ( 1. - STRIPE_WIDTH ) - SMOOTH_WIDTH, ( 1. - STRIPE_WIDTH ) + SMOOTH_WIDTH, stripeUv.y);
    stripes -= 1.0 - smoothstep( 1.0, 1.0 - SMOOTH_WIDTH * 2.0, stripeUv.y);

    float gradient = 1.0 - fract( floor( ( rotatedUv.y ) * NUM_STRIPES ) / NUM_STRIPES + iTime );

    float col = stripes * gradient;


    // Output to screen
    fragColor = vec4( col );
}

/* "analytic quartic solver", by mattz

   License: Creative Commons Attribution ShareAlike 4.0
   https://creativecommons.org/licenses/by-sa/4.0/

   I hunted around a little bit on Shadertoy but couldn't find any
   examples of a standalone, single-function quartic solver 
   capable of returning all real roots of a 4th-degree polynomial.

   The result is the solve_quartic function below, feel free to 
   use it in your own projects under the license linked above.

   With respect to testing, I wanted to make sure to test
   all eight cases of root distribution (see generate_roots below).

*/

//////////////////////////////////////////////////////////////////////
// evaluate a quartic polynomial whose first coefficient is 1.

float poly4(vec4 p, float x) {
    return (((x + p[0])*x + p[1])*x + p[2])*x + p[3];
}

//////////////////////////////////////////////////////////////////////
// follows notes in http://web.cs.iastate.edu/~cs577/handouts/polyroots.pdf
// with some special-case code (because triple & quadruple roots are 
// really numerically unstable)
//
// the four quartic polynomial coefficients supplied are assumed to follow
// a leading 1 coefficient.
//
// returned boolean should have 0, 2, or 4 true's corresponding to # of real
// quartic roots.

bvec4 solve_quartic(in vec4 coeffs,
                    out vec4 roots) {

    const float TOL = 1e-4;

    float p = coeffs[0];
    float q = coeffs[1]; 
    float r = coeffs[2];
    float s = coeffs[3];

    ////////////////////////////////////////////////////////////
    // check for quadruple root

    float x = -p/4.;

    if (abs(poly4(coeffs, x)) < TOL &&
        abs(q - 6.*x*x) < TOL) {
        roots = vec4(x);
        return bvec4(true);
    }

    ////////////////////////////////////////////////////////////
    // check for triple root

    float d = 9.*p*p - 24.*q;

    if (d >= 0.) {

        float x1 = -p/4. - sqrt(d)/12.;
        float x2 = -p/4. + sqrt(d)/12.;

        float px1 = abs(poly4(coeffs, x1));
        float px2 = abs(poly4(coeffs, x2));

        x = px1 < px2 ? x1 : x2;

        float y = -p - 3.*x;

        roots = vec4(max(x,y),x,x,min(x,y));

        if (min(px1, px2) < TOL &&
            abs(poly4(coeffs, y)) < TOL &&
            abs(r + x*x*x + 3.*x*x*y) < TOL) {

            return bvec4(true);

        }

    }

    ////////////////////////////////////////////////////////////
	// form resolvent cubic and solve it to obtain one real root

    float i = -q;
    float j = p*r - 4.*s;
    float k = 4.*q*s - r*r - p*p*s;

    // coefficients of normal form
    float a = (3.*j - i*i) / 3.;
    float b = (2.*i*i*i - 9.*i*j + 27.*k) / 27.;

    float delta1 = b*b / 4.;
    float delta2 = a*a*a / 27.;

    float delta = delta1 + delta2;

    float z1;

    if (delta >= -TOL) {
        vec2 AB = -0.5*b + vec2(1,-1) * sqrt(max(delta, 0.));
        AB = sign(AB) * pow(abs(AB), vec2(1.0/3.0));
        z1 = AB.x + AB.y;
    } else {
        float phi = acos( -sign(b) * sqrt(delta1/-delta2) );
        z1 = 2. * sqrt(-a/3.) * cos( phi / 3.);
    }

    // shift back from normal form to root of resolvent cubic
    z1 -= i/3.;

    ////////////////////////////////////////////////////////////
	// now form quartic roots from resolvent cubic root

    float R2 = p*p/4. - q + z1; 

    bool R_ok = (R2 >= -TOL);

    float R = sqrt(max(R2, 0.));

    float foo, bar;

    if (R == 0.) { 
        float z124s = z1*z1 - 4.*s;
        R_ok = R_ok && (z124s >= -TOL);
        foo = 3.*p*p / 4. - 2.*q;
        bar = 2.*sqrt(max(z124s, 0.));

    } else {
        foo = 3.*p*p / 4. - R2 - 2.*q;
        bar = (4.*p*q - 8.*r - p*p*p) / (4.*R);
    }

    bool D_ok = R_ok && (foo + bar >= -TOL);
    bool E_ok = R_ok && (foo - bar >= -TOL);

    float D = sqrt(max(foo + bar, 0.));
    float E = sqrt(max(foo - bar, 0.));

    roots = vec4(-p/4.) + 0.5 * vec4(R+D, R-D, -(R-E), -(R+E));
    return bvec4(D_ok, D_ok, E_ok, E_ok);

}

//////////////////////////////////////////////////////////////////////
// evaluate a cubic polynomial with given coefficients

float poly3(vec4 p, float x) {
    return ((p[0]*x + p[1])*x + p[2])*x + p[3];
}

//////////////////////////////////////////////////////////////////////
// from Dave Hoskins' "Hash without sine"
// https://www.shadertoy.com/view/4djSRW

#define HASHSCALE1 .1031

float hash12(vec2 p) {
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

//////////////////////////////////////////////////////////////////////

float noise(vec2 xc) {

    float x0 = floor(xc.x);
    float t = fract(xc.x);

    float a = hash12(vec2(x0-1., xc.y));
    float b = hash12(vec2(x0, xc.y));
    float c = hash12(vec2(x0+1., xc.y));
    float d = hash12(vec2(x0+2., xc.y));

    float t2 = t*t;
    float t3 = t2*t;

    float h00 = (2.*t3 - 3.*t2 + 1.);
    float h01 = (t3 - 2.*t2 + t);
    float h10 = (-2.*t3 + 3.*t2);
    float h11 = (t3 - t2);

    return (h00*b + h01*(c-a) + h10*c + h11*(d-b))*2. - 1.;

}

//////////////////////////////////////////////////////////////////////
// generate coefficients for 4th degree quartic from roots
// note that c is the complex part of roots 0 & 1 and 2 & 3
// if c[0] != 0, then we expect r[0] == r[1] and similarly
// if c[1] != 0

vec4 poly_from_roots(vec4 r, vec2 c) {

    float s01 = r[0] + r[1];
    float p01 = r[0] * r[1] + c[0] * c[0];
    float s23 = r[2] + r[3];
    float p23 = r[2] * r[3] + c[1] * c[1];

    return vec4(-s01 - s23,
                p01 + s01*s23 + p23,
                -p01*s23 - s01*p23,
                p01*p23);

}

//////////////////////////////////////////////////////////////////////
// for a quartic with real coefficients, there are eight different
// cases for roots, which we cycle through over time:
//               
//   case timestep #real multiplicity
//     A  0-0.99       4   4 single           
//     B  1-1.99       4   1 double, 2 single 
//     C  2-2.99       4   2 double
//     D  3-3.99       4   1 triple, 1 single 
//     E  4-4.99       4   1 quad
//     F  5-5.99       2   2 single
//     G  6-6.99       2   1 double
//     H  7-7.99       0   no real roots
//
//        8-8.99   repeat case C to move smoothly back to A
//

void generate_roots(out vec4 r, out vec2 c) {

    float t = 0.1*iTime;
    float d = 2.2;

    r = vec4(noise(vec2(t, 10.)),
                  noise(vec2(t, 1.)),
                  noise(vec2(t, 12.)),
                  noise(vec2(t, 3.)))*d;

    vec3 cc = vec3(noise(vec2(t, 14.))+0.2*d,
                   noise(vec2(t, 5.))+0.2*d,
                   0);

    t = 0.05*iTime;

    float rep = floor(t/9.);

    float phase = mod(t, 9.);
    float u = smoothstep(0.8, 1.0, fract(phase));

    c = vec2(0);

    if (phase < 1.) { 
        r = mix(r, r.xxzw, u); // A -> B
    } else if (phase < 2.) {
        r = mix(r.xxzw, r.xxzz, u); // B -> C
    } else if (phase < 3.) {
        r = mix(r.xxzz, r.xxxz, u); // C -> D
    } else if (phase < 4.) {
        r = mix(r.xxxz, r.xxxx, u); // D -> E
    } else if (phase < 5.) {
        r = mix(r.xxxx, r.xxzw, u); // E -> F
        c = mix(cc.zz, cc.xz, u);
    } else if (phase < 6.) {
        r = mix(r.xxzw, r.xxzz, u); // F -> G
        c = cc.xz;
    } else if (phase < 7.) {
        r = r.xxzz; // G -> H
        c = mix(cc.xz, cc.xy, u);
    } else if (phase < 8.) {
        r = r.xxzz; // H -> C
        c = mix(cc.xy, cc.zz, u);
    } else {
        r = mix(r.xxzz, r, u); // C -> A
    }

}

//////////////////////////////////////////////////////////////////////
// given point p, function value at p.x and function derivative at
// p.x, determine estimated distance to curve of function plot.

float dist_to_plot(vec2 p, vec2 fdf) {

    vec2 p0 = vec2(p.x, fdf.x);
    vec2 n = normalize(vec2(-fdf.y, 1));
    return abs(dot(p - p0, n));

}

//////////////////////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float scl = 6. / iResolution.x;

    vec2 p = (fragCoord - 0.5 - vec2(0.5, 0.28)*iResolution.xy)*scl;

    vec4 r; vec2 c;
    generate_roots(r, c);

    vec4 poly = poly_from_roots(r, c);

    vec4 solved_roots; 
    bvec4 is_root = solve_quartic(poly, solved_roots);

    vec4 pder = vec4(4,3,2,1) * vec4(1, poly.xyz);

    const float Y_ZOOM = 0.5;

    vec2 fdf = Y_ZOOM*vec2(poly4(poly, p.x), 
                           poly3(pder, p.x));

    vec3 color = vec3(1, 0.97, .87);

    // grid lines
    vec2 gp0 = .5*floor(2.*p + 0.5);
    vec2 gp = fract(abs(p - gp0));
    color = mix(color, vec3(0.6, 0.75, 0.6), smoothstep(scl, 0., min(gp.x, gp.y)));

    // axis line
    vec2 ap = abs(p);
    color = mix(color, vec3(0.37, 0.55, 0.37)*.9, 0.8*smoothstep(scl, 0., min(ap.x, ap.y)-.75*scl));      

    // plot line
    float dline = dist_to_plot(p, fdf);
    color = mix(color, vec3(.5, .03, .03), smoothstep(scl, 0., dline-.5*scl));

    // known roots
    for (int i=0; i<4; ++i){
        int j = i/2;
        if (c[j] == 0.) {
            float dc = length(p - vec2(r[i], 0));
            color = mix(color, vec3(.25, .02, .02), smoothstep(scl, 0., dc-3.*scl));
        }
    }

    // solved roots
    for (int i=0; i<4; ++i){
        if (is_root[i]) {
            float dc = length(p - vec2(solved_roots[i], 0));
            color = mix(color, vec3(0, 0, 0.4), smoothstep(scl, 0., abs(dc-8.*scl)-.25*scl));
        }
    }

    // stole iq's vingette code
    vec2 q = fragCoord.xy / iResolution.xy;
    color *= pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.2 );       
    color *= 0.9 + 0.1*texture(iChannel0, fragCoord.xy/256.).x;

    color = pow(color, vec3(1.0/2.2));

    fragColor = vec4(color, 1);

}

