#define EPSILON1 0.1
#define EPSILON2 0.0001


float sdPlane(vec3 p, vec3 n, float distanceFromOrigin) {
	return dot(p, n) + distanceFromOrigin;
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

// The "Round" variant uses a quarter-circle to join the two objects smoothly:
float fOpUnionRound(float a, float b, float r) {
	vec2 u = max(vec2(r - a,r - b), vec2(0));
	return max(r, min (a, b)) - length(u);
}

// polynomial smooth min (k = 0.1);
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float map(vec3 p)
{
    float bgPlane = sdPlane(p, normalize(vec3(0,0,-1)), 1. );
    vec3 sp1Pos = p+ vec3(cos(iTime*0.3)*57.,sin(iTime*0.56)*36., 30. + sin(iTime*0.115)*100.); 
    float sp1 = sdSphere(sp1Pos, 100.);
    float o = smin(sp1, bgPlane, 0.9);
    o = min(o, fOpUnionRound(sp1, bgPlane, 0.2));


 	return o;   
}

vec3 getNorm(vec3 p) {
    return normalize(vec3(
        map(vec3(p.x + EPSILON2, p.y, p.z)) - map(vec3(p.x - EPSILON2, p.y, p.z)),
        map(vec3(p.x, p.y + EPSILON2, p.z)) - map(vec3(p.x, p.y - EPSILON2, p.z)),
        map(vec3(p.x, p.y, p.z  + EPSILON2)) - map(vec3(p.x, p.y, p.z - EPSILON2))
    ));
}


float ss( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0000;
    for( float t=mint; t < maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.0001 )
            return 0.0000;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 blue = vec4(0.03,0.,1.,1.);
    vec4 orange = vec4(0.8,0.39,0.387,1.);
    vec4 shcol = blue; 


	vec2 uv = ((fragCoord.xy*2.) / iResolution.xy) -1.;
    // Blue/ Orange blend - bit too pinched in the corners still
    float i1, i2, o1, o2;
    float inner = mix(0.7, 0.78, sin(iTime));
    float outer = mix(1.4, 1.2, sin (iTime));
    float mixvalX = smoothstep(inner, outer, length(uv.x));
    float mixvalY = smoothstep(inner, outer, length(uv.y));
    float mixval = mixvalX+mixvalY;
    fragColor = mix(orange, blue, mixval);


    // cam setup
    uv.x *= iResolution.x/iResolution.y;
    vec3 eye = vec3(0.,0., -550.);
    vec3 up = vec3(0.,1.,0.);
    vec3 interest = vec3 (0.);
    vec3 lookDir = normalize(eye - interest); 
    vec3 right = cross(up, lookDir);
    float f = 2.9;
    vec3 p = eye + f*lookDir + uv.x*right + uv.y*up;
    vec3 rayDir = normalize(eye - p);

    // interact
    float xm = iMouse.x - (iResolution.x/2.);
    float ym = iMouse.y - (iResolution.y/2.);
    xm *= -1.;
    ym *= 1.;
    if(iMouse.z < .5)
