        xm = sin(iTime*0.8)*380.;
        ym = cos(iTime*0.7)*300.;
    }

    vec3 light = vec3( xm, ym, -800.5);
    vec3 lightDir = normalize(light - interest);

    float far = 1000.;
    float near = 0.;
    float t = 0.;
    float d = 0.;
    int maxS = 190;

    // raymarch
    for(int i = 0; i < maxS; i++)
    {
        d = map(p + rayDir*t);
        if(d <= EPSILON1)
        {
            vec3 hit = p + rayDir*t; 
            vec3 n = getNorm(hit);
            float pn = ss(hit, lightDir, 0., 3000., 1.8);
            fragColor = mix(mix(fragColor, shcol, (1.-pn)), fragColor, pn);
            break;
        }

        t += d;
        if(t >= far)
        {
         	break;   
        }

    }

}

