    t += rand(id.x+id.y)*TwoPI; //fract(sin(id.x*72.45+id.y*1488.88)*765.23)*TwoPI; //6.284;

    float y = -sin(t+sin(t+sin(t)*.5))*.40;
    float x = .5* rand(id.y)-.24; //fract(sin(id.y*768.34)*7689.43)-.24;

    vec2 p1 = vec2(x, y);
    vec2 p1t = ((st-p1) / a) * r2((-y*2.)+t*.5);
