const float MYPI = 3.14159265359;
const float degToRad = MYPI * 2.0 / 360.0;
const float radToDeg = 360.0 / MYPI / 2.0;

vec3 rotZ(vec3 v, float radians) {
  float c = cos(radians);
  float s = sin(radians);
  return vec3( v.x * s - v.y * c,
               v.x * c + v.y * s,
               v.z);
}

vec3 rotY(vec3 v, float radians) {
  float c = cos(radians);
  float s = sin(radians);
  return vec3( v.x * s - v.z * c,
               v.y,
               v.x * c + v.z * s);
}

vec2 msdfUnion(vec2 A, vec2 B) {
  return A.x < B.x ? A : B;
}

vec2 msdfInter(vec2 A, vec2 B) {
  return A.x > B.x ? A : B;
}

vec2 msdfMinus(vec2 A, vec2 B) {
  return A.x > -B.x ? A : -B;
}

// cube of "radius" 1.
// a sphere of radius 1 fits inside.
float sdfCube(vec3 p) {
  const float radius = 0.5;
  float distxn  =  -p.x - radius;
  float distxp  =   p.x - radius;
  float distyn  =  -p.y - radius;
  float distyp  =   p.y - radius;
  float distzn  =  -p.z - radius;
  float distzp  =   p.z - radius;

  float distx   = max(distxn, distxp);
  float disty   = max(distyn, distyp);
  float distz   = max(distzn, distzp);

  float distxy  = max(distx , disty);
  float distxyz = max(distxy, distz);

  return distxyz;
}

float sdfSphere(vec3 p) {
  return length(p) - 1.0;
}

float sdfModSphere(vec3 p) {
  return sdfSphere(fract(p / 3.0));
}

float sdfWarpSphere(vec3 p) {
  float f = myTime * 0.1;
  vec3 q = p;
  const float overallDistortionMagnitude = 0.1;
  const float mag2 = .2;
  const float freq = 20.;
  float mag = sin(myTime * 0.1) * overallDistortionMagnitude;
  q.x += cos(p.z         * mag * 0.9 + f) * 0.1;
  q.y += sin(p.z         * mag * 0.8 + f) * 0.1;
  q.z += sin((p.x * p.y) * mag * 1.0 + f * 10.) * 0.1;
  float dx = sin(p.z * freq) * mag2;
  float dy = sin(p.x * freq) * mag2;
  float dz = sin(p.y * freq) * mag2;
  dx *= dx * dx * dx;
  dy *= dy * dy * dy;
  dz *= dz * dz * dz;
  q.x += dx;
  q.y += dy;
  q.z += dz;
  return length(q) - 0.75;
}

vec2 msdfSceneSingle(vec3 p) {
  vec2 ret = vec2(sdfCube(p), 1.0);
  return ret;
}

vec2 msdfScene(vec3 pt) {
  // return sdfSceneSingle(pt);
  pt = rotZ(pt, myTime * 0.11);
  pt = rotY(pt, myTime * 0.13);


  // return vec2(sdfModSphere(pt), 8.0);

  vec2 c1 = vec2(sdfCube(pt), 3.0);
  pt = rotY(pt, myTime * 0.321);
  pt = rotZ(pt, myTime * 0.4321);
  const float orbit = 0.65;
  const float orbitSpeed = 0.5;
  vec3 p2 = vec3(
    sin(myTime * orbitSpeed * 1.0),
    sin(myTime * orbitSpeed * 1.0),
    sin(myTime * orbitSpeed * 1.1)
  ) * orbit;
  vec2 c2 = vec2(sdfSphere((pt + p2) * 3.0) / 3.0, 2.0);
  vec2 d3 = msdfMinus(c1, c2);
  d3 = msdfInter(d3, vec2(sdfSphere(pt * 1.42) / 1.42, 5.0));
  d3 = msdfMinus(d3, vec2(sdfSphere(pt * 1.65) / 1.65, 7.0));
  d3 = msdfUnion(d3, vec2(sdfCube(rotY((pt + p2) * 8.0, myTime * 2.0)) / 8.0, 6.0));
  return d3;
}

vec3 sdfNormal(vec3 p) {
  const float epsilon = 0.001;
  vec3 v = vec3(
    msdfScene(vec3(p.x + epsilon, p.y, p.z)).x - msdfScene(vec3(p.x - epsilon, p.y, p.z)).x,
    msdfScene(vec3(p.x, p.y + epsilon, p.z)).x - msdfScene(vec3(p.x, p.y - epsilon, p.z)).x,
    msdfScene(vec3(p.x, p.y, p.z + epsilon)).x - msdfScene(vec3(p.x, p.y, p.z - epsilon)).x
  );

  return normalize(v);
}

bool approximatelyEqualEps(float a, float b, float epsilon) {
  float d = abs(a - b);
  return d < epsilon ? true : false;
}

bool approximatelyEqual(float a, float b) {
  const float epsilon = 0.0001;
  return approximatelyEqualEps(a, b, epsilon);
}

// todo: generalize to any plane
vec3 intersectRayXZPlane(vec3 pt, vec3 dir) {
  if (approximatelyEqual(dir.y, 0.)) {
    return vec3(kMaxFloat, 0, kMaxFloat);
  }

  float s = pt.y / dir.y;
  return pt + dir * s;
}

float subfloor(vec3 groundPt) {
  groundPt = rotY(groundPt, 45.0 * degToRad);
  groundPt += vec3(
    sin(myTime * 0.12  ) * 10.,
    1,
    sin(myTime * 0.1211) * 10.
  );
  float f = sin(groundPt.x * 1.2) * sin(groundPt.z * 1.2);
  f *= f * f;
  return f * 1.9 + 1.5;
}

float floorCheck(vec3 pt, vec3 rayDir) {
  rayDir = rotY(rayDir, myTime * 0.031);
  vec3 floorOffset = vec3(0, floorHeight, myTime * -4.1);
  vec3 groundPt = intersectRayXZPlane(pt + floorOffset, rayDir);
  float f = sin(groundPt.x) + sin(groundPt.z) - sin(myTime * 0.51) * 0.8;
  float rf;
  if (approximatelyEqualEps(f, 0., 0.07)) {
    rf = 0.18;
  }
  else if (f < 0.) {
    rf = 0.12;
  }
  else {
    rf = subfloor(groundPt) * 0.1;
  }

  return rf;
}

