#define PI 3.14

vec2 hash2(in vec2 uv)
{
  return fract(vec2(sin(uv.x*1834538.331),
               sin(uv.y*617678.44)));
}

float hash(in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

float hash(in vec3 p)
{
  return fract(sin(dot(p,
    vec3(12.6547, 765.3648, 78.653)))*43749.535);
}

float noise3(in vec3 p)
{
  vec3 pi = floor(p);
  vec3 pf = fract(p);

  pf = pf*pf*(3.-2.*pf);

  float a = hash(pi + vec3(0., 0., 0.));
  float b = hash(pi + vec3(1., 0., 0.));
  float c = hash(pi + vec3(0., 1., 0.));
  float d = hash(pi + vec3(1., 1., 0.));

  float e = hash(pi + vec3(0., 0., 1.));
  float f = hash(pi + vec3(1., 0., 1.));
  float g = hash(pi + vec3(0., 1., 1.));
  float h = hash(pi + vec3(1., 1., 1.));

  return mix(mix(mix(a,b,pf.x),mix(c,d,pf.x),pf.y),
  mix(mix(e,f,pf.x),mix(g,h,pf.x),pf.y), pf.z);
}


float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

float fbm(vec2 uv) {
  float f = .5*noise(uv);
  vec2 off = vec2(0.01, 0.01);
  f += .25*noise(uv*2.02 + off);
  f += .125*noise(uv*4.01 + off);
  f += .065*noise(uv*8.03 + off);
  f += .0325*noise(uv*16.012 + off);

  return f;
}

#define red vec3(1.,0.,0.)
#define yellow vec3(1.,1.,0.)
#define blue vec3(0.,0.,1.)
#define white vec3(1.,1.,1.)
#define ocean vec3(0.,0.6,0.93)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  vec2 tuv = iMouse.xy/iResolution.xy;

  uv.x *= iResolution.x/iResolution.y;
  vec2 sc = uv-vec2(0.7,0.7);
  float sr = dot(sc,sc);
  float af = atan(sc.y, sc.x);

  vec3 sun = vec3(1.,1.,1.)*(1.-smoothstep(sr, 0.00,0.01));
// sun = clamp(sun, 0.,1.);

  vec2 duv = uv;
    if (tuv.x > .5 && iMouse.z > 0.5) {
        duv = vec2(uv.x*2. - 1., 1.)/(uv.y+0.1*(1.-uv.y));
    }
  float f = fbm(duv+tuv*4.);
  float tt = 0.1*iTime;
  float anf = noise(vec2(af*10. + tt*5., tt*4.));
  float f2 = fbm(duv+2.*vec2(cos(f) + cos(tt), sin(f)+sin(tt)) );
  vec3 sky = vec3(0.,0.,max(blue.b*(1.-sr), 0.));
  vec3 col1 = mix(yellow, red, f);
  vec3 col2 = mix(sky, white,
    f2);
  vec3 col = mix(col2, col1, f*f2);

  //col = mix(vec3(0.,0.,0.), col, 2.*(  col.r*col.g));
  col += sun*(4.*(1.-col.g)*exp(-col.g*4.));
  //col = mix(col, sun, clamp(4.*(1.-col.g-col.r)*exp((-col.g-col.r)*4.), 0.,1.));
  fragColor = vec4(col, 1.);
}

float V;
float pi=radians(180.);
float sqrt2=sqrt(2.);

vec4 splat(vec2 D) {
    float a = 10. + abs(sin(iTime * .4)) * 100.;
    D /= V/a;            // splats radius 
    return exp(-.5*dot(D,D) ) * vec4(1,.7,.4,0)*2.; // colored Gaussian
}

void mainImage( out vec4 Out, vec2 In )
{
     float t=iTime+1.;
     vec2 R = iResolution.xy; vec2 P;
     V=max(R.x,R.y)/sqrt2;

     float M=floor(t);
     float K=t-M;
     for (float j=0.; j<14.; j++) {
         float F=2.*pi/(M+j);
         float U=V-(j-K)*V/14.;
         for (float i=0.; i<M+j; i++) {
             P.x=R.x/2.+cos(F*i+cos(iTime))*U;
             P.y=R.y/2.+sin(F*i+sin(iTime * .5))*U;
             Out += splat(In-P);
         }
     }
}

const float PI = 3.14159265359;
const float TWO_PI = 6.28318530718;

const vec3 ColorA = vec3(0.101961, 0.619608, 0.666667);
const vec3 ColorB = vec3(0.666667, 0.336667, 0.498039);

vec2 random2( vec2 p ) 
{
    return fract(sin(vec2(dot(p,vec2(0.1123, 0.7453)),dot(p,vec2(1.1235, 0.3123)))) * 0.5453);
}

vec3 PosToColor(vec2 st)
{
    return mix(ColorA, ColorB, vec3(random2(st).x));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = (fragCoord - .5 * iResolution.xy ) / iResolution.y;

    vec3 color = vec3(0.0);
    float d = 0.0;

    st *= 25.;

    vec2 st_i = floor(st);
    st = fract(st) - 0.4;


    // Number of sides of a shape
    int N = 3;

    // Angle and radius from the current pixel
    float a = atan(st.x, st.y) + PI;    
    float r = TWO_PI / float(N);

    // Shaping function that modulate the distance
    d = cos(floor(.5 + a / r) * r - a) * length(st * 2.);

    color = vec3(1.0 - smoothstep(.3, .5, d));
    color *= PosToColor(st_i * iTime * 0.1);

    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float xdivy=cos(2.0*iTime)*iResolution.x/iResolution.y;
    float R=0.6*iResolution.y*abs(cos(1.5*iTime));
    float r=0.4*abs(cos(3.0*iTime));
    float angle=1.0*cos(0.5*iTime);
    vec3 col=vec3(abs(cos(iTime)),uv.x,uv.y);//0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 center=vec2(0.5*(1.0+r*cos(4.0*iTime))*iResolution.x,0.5*(1.0+r*sin(4.0*iTime))*iResolution.y);
    float nx=fragCoord.x-center.x;
    float ny=fragCoord.y-center.y;
    float a=nx*cos(angle)+ny*sin(angle);
    float b=xdivy*(nx*sin(angle)-ny*cos(angle));
    float ci=abs(cos(iTime));
    if((nx*nx+ny*ny)<R*R)col=vec3(ci,uv.x,uv.y);else col=vec3(uv.x,ci,uv.y);
    if(a<b)col.x=1.0-col.x;
    if(a<-b)col.y=1.0-col.y;
    fragColor = vec4(col,1.0);
}

