float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrTorusDf (vec3 p, float ri, float rc);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 qHit;
float dstFar, tCur;
int idObj;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d, h, r, y, s;
  dMin = dstFar;
  h = 2.;
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (6. * ((length (q.xz) > 0.) ? atan (q.z, - q.x) /
     (2. * pi) : 0.) + 0.5) / 6.));
  q.x += 3.1;
  q.xy = Rot2D (q.xy, -0.33 * pi);
  r = length (q.xz);
  qq = q;
  for (float k = 0.; k < 2.; k ++) {
    q = qq;
    q.y -= 0.1 * step (0.5, k);
    q.xz = Rot2D (q.xz, sign (k - 0.5) * pi / 6.);
    q.xz = Rot2D (q.xz, 2. * pi * (floor (3. * ((r > 0.) ?
       atan (q.z, - q.x) / (2. * pi) : 0.) + 0.5) / 3.));
    y = 0.5 * (1. + q.y / h);
    s = 1. - 0.9 * cos (0.5 * pi * y);
    d = max (PrCylAnDf (q.xzy, 0.3 + 0.5 * y + s * (1. + 0.5 * s) - 0.01 * step (0.5, k), 0.01, h),
       abs (q.z) - 1.55 + 1.4 * (1. - (0.12 + y) * (0.88 - y)));
    DMINQ (1);
  }
  dMin *= 0.5;
  q = qq;  q.y += 1.75;
  d = max (PrSphDf (q, 0.6), q.y - 0.4);
  DMINQ (2);
  q = qq;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (12. * ((r > 0.) ? atan (q.z, - q.x) /
     (2. * pi) : 0.) + 0.5) / 12.));
  q.y += 1.;
  q.x = abs (q.x + 0.1 + 0.2 * q.y * (1. + q.y)) - 0.04;
  d = 0.3 * PrCylDf (q.xzy, 0.01, 0.4);
  DMINQ (3);
  q = qq;  q.xy += vec2 (0.5, 2.35);
  d = max (PrTorusDf (q, 0.05, 0.5), q.y);
  q.xy = Rot2D (q.xy, 0.25 * pi);
  d = max (d, - q.y);
  DMINQ (4);
  q.xy -= vec2 (0.5, -1.3);
  d = PrCylDf (q.xzy, 0.05, 1.3);
  DMINQ (4);
  q = p;  q.y += 4.4;
  d = PrCylDf (q.xzy, 1.5, 0.5 * (1. - 0.1 * length (q.xz) / 1.5));
  DMINQ (5);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 200; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 ltPos[3], ltCol[3], col, vn, ltDir, dfSum, spSum;
  vec2 vf;
  float dstObj, spec, cv, at, r, a;
  for (int k = 0; k < 3; k ++) {
    a = 2. * pi * float (k) / 3.;
    ltPos[k] = 10. * vec3 (cos (a), 0.5, sin (a));
  }
  ltCol[0] = vec3 (1., 1., 0.4);
  ltCol[1] = vec3 (1., 0.4, 0.4);
  ltCol[2] = vec3 (0.4, 0.4, 1.);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vf = vec2 (0.);
    spec = 0.;
    if (idObj == 1) {
      cv = (1. - 0.3 * (1. - 0.4 * (SmoothBump (0.4, 0.6, 0.05, mod (6. * (abs (qHit.z) -
         0.3 * abs (qHit.x) - 0.6 * qHit.x * qHit.x), 1.)) + 1. - smoothstep (0., 0.02,
         abs (qHit.z))))) * (1. - 0.3 * smoothstep (0.4, 0.5, abs (qHit.z) +
         0.1 * abs ((qHit.x + 0.3) * (1. - (qHit.x + 0.3))))) *
         (1. - 0.3 * smoothstep (-0.8, -0.5, qHit.x - 0.5 * abs (qHit.z)));
      col = HsvToRgb (vec3 (floor (6. * (atan (ro.z, - ro.x) / (2. * pi) + 0.5) + 0.5) / 6., 0.7, cv));
      spec = 0.1;
      vf = vec2 (128., 0.1);
    } else if (idObj == 2) {
       if (qHit.y < 0.35) {
        col = vec3 (0.3, 0.9, 0.2) * (1. - 0.3 * SmoothBump (0.4, 0.6, 0.05,
           mod (12. * ((length (qHit.xz) > 0.) ? atan (qHit.z, - qHit.x) / (2. * pi) : 0.), 1.)));
        spec = 0.1;
        vf = vec2 (64., 0.5);
      } else {
        r = length (qHit.xz);
        qHit.xz = Rot2D (qHit.xz, 5. * tCur);
        col = mix (vec3 (0.5, 0.5, 0.1), vec3 (0.4, 0.2, 0.1), SmoothBump (0.2, 0.8, 0.1,
           mod (16. * r + ((r > 0.) ? atan (qHit.z, - qHit.x) / (2. * pi) : 0.), 1.)));
        spec = 0.05;
        vf = vec2 (32., 2.);
      }
    } else if (idObj == 3) {
      col = mix (vec3 (0.9, 0.9, 0.2), vec3 (1., 0.1, 0.1), step (0.35, qHit.y));
      spec = 0.1 - step (0.35, qHit.y);
    } else if (idObj == 4) {
      col = mix (vec3 (0.2, 0.3, 0.2), vec3 (0.2, 0.7, 0.1), smoothstep (-1., 0., qHit.y));
      spec = 0.1;
      vf = vec2 (64., 0.5);
    } else if (idObj == 5) {
      if (length (ro.xz) < 1.4) {
        col = vec3 (0.25, 0.15, 0.1) * (0.5 + 0.5 * step (0.1, length (Rot2D (ro.xz, 2. * pi *
           (floor (6. * atan (ro.z, - ro.x) / (2. * pi) + 0.5) / 6.)) + vec2 (0.25, 0.))));
        vf = vec2 (32., 1.);
      } else {
        col = vec3 (0.7, 0.7, 0.9);
        vf = vec2 (128., 0.2);
      }
    }
    if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    if (spec >= 0.) {
      dfSum = vec3 (0.);
      spSum = vec3 (0.);
      for (int k = 0; k < 3; k ++) {
        ltDir = normalize (ltPos[k]);
        at = smoothstep (0.7, 0.8, dot (normalize (ltPos[k] - ro), ltDir));
        dfSum += ltCol[k] * at * max (dot (vn, ltDir), 0.);
        spSum += ltCol[k] * at * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
      }
      col = col * (0.2 + 0.7 * dfSum) + spec * spSum;
    }
  } else {
    col = vec3 (1., 1., 0.8) * (0.2 + 0.2 * (rd.y + 1.) * (rd.y + 1.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, uvs, ori, ca, sa;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.1 * pi;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= pi * mPtr.y;
  } else {
    az += 0.02 * pi * tCur;
  }
  el = clamp (el, 0.05 * pi, 0.4 * pi);
  ro = 20. * vec3 (cos (el) * cos (az), sin (el), cos (el) * sin (az));
  vd = normalize (vec3 (0., -1., 0.) - ro);
  vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / sqrt (1. - vd.y * vd.y),
     vec3 (- vd.y * vd.x, 1. - vd.y * vd.y, - vd.y * vd.z) / sqrt (1. - vd.y * vd.y), vd);
  zmFac = 4.4 - 0.8 * abs (el);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  dstFar = 40.;
  col = ShowScene (ro, rd);
  uvs *= uvs * uvs;
  col *= 0.8 + 0.2 * pow (1. - 0.5 * length (uvs * uvs), 4.);
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

// variant of https://shadertoy.com/view/XsBGDc

#define Q 20.       // random quantization on right
#define NB 1000     // number or gabor blobs
#define SIZE .22    // size of gabor blobs
                    // freq and dir tuned by mouse.x
#define PI 3.1416

#define gauss(x)  exp(-(x*x)/(SIZE*SIZE))

// chose texture vs procedural rand :
//#define Rnd(i,j) texture( iChannel1, (.5+vec2(i,j)) / iChannelResolution[1].x ).r
#define Rnd(i,j) fract(sin(dot(vec2(i,j), vec2(12.9898, 78.233))) * 43758.5453)

#define rnd(i,j) ( U.x < 0. ? Rnd(i,j) : round( Rnd(i,j) *Q ) / Q )

float gabor(vec2 pos, vec2 dir) {          // Gabor splat
    vec2 G = gauss(pos);
    float s = .5*sin( dot(pos,dir)*6.28 -10.*iTime );
	return G.x*G.y *s ;
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy/R, pos,dir;
         U = (U+U-R)/R.y;
    if (M==vec2(0)) M = vec2(.16);
    O -= O;  
    float f = 3.+20.* M.x,                 // frequency
          a = PI/2. * M.y;                 // direction
    dir = vec2(cos(a),sin(a));

    for (int i=0; i<NB; i++) {
        pos = ( 2.* vec2( rnd(i,0), rnd(i,1) ) - 1. ) * R/R.y;
        O += gabor(U-pos, f*dir );
    }
    O = .5+.5*O *8./sqrt(float(NB));

         if( O.r > 1.) O = vec4(1,0,0,0);  // show overshoots
    else if ( O.r <0.) O = vec4(0,0,1,0);
}

float Disition(vec3 ro, vec3 rd, vec3 p) {
 	return length(cross(p-ro, rd))/length(rd);   
}


float DrawPoint(vec3 ro, vec3 rd, vec3 p) {
    float d = Disition(ro, rd, p);
    d = smoothstep(0.06, 0.05, d);
    return d;
}

vec3 DrawRectangle(float k, float is, float ie, float js, float je, vec3 color, vec3 ro, vec3 rd) {
 	vec3 col = vec3(0.0);
    for(float i = is; i <= ie; i += 0.2) {
        for(float j = js; j <= je; j += 0.2) {
            if(i == is || i == ie || 
               j == js || j == je ||
               k == 0.0 || k == 1.0) {

                col += DrawPoint(ro, rd, vec3(i, j, k)) * color;
            }
        }       
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 uv = ( fragCoord - .5*iResolution.xy) / iResolution.y;

    vec3 ro = vec3(3.0*sin(t), sin(t), -8.0*cos(t));
    float zoom = 1.0;

    vec3 lookAt = vec3(0.5);
    vec3 f = normalize(lookAt - ro);
    vec3 r = cross(vec3(0.0, 1.0, 0.0), f);
    vec3 u = cross(f, r);

    vec3 c = ro + f*zoom;
    vec3 i = c + uv.x*r + uv.y*u;

    vec3 rd = i - ro;

    float d = 0.0;
    vec3 col = vec3(0.0);

    for(float k = 0.0; k <= 1.0; k += 0.2) {

        col += DrawRectangle(k, 0.0, 2.0, -1.0, 0.0, vec3(0.99, 0.20, 0.58), ro, rd);
        col += DrawRectangle(k, -1.0, 0.0, -2.0, 0.0, vec3(0.0, 0.75, 0.94), ro, rd);
		col += DrawRectangle(k, -2.0, 0.0, 0.0, 1.0, vec3(0.21, 0.81, 0.33), ro, rd);
		col += DrawRectangle(k, 0.0, 1.0, 0.0, 2.0, vec3(1.0, 0.91, 0.28), ro, rd);

    }   

    fragColor = vec4(col, 1.0);
}

vec2 rep(vec2 p, float n) {
	return fract(p*n)*2.-1.;
}
vec2 rep(vec2 p, vec2 n) {
    float r = n.x/n.y;
    vec2 a = (r<1. ? vec2(1./r,1.) : vec2 (1.,r));
	return fract(p*n)*a*2.-a;
}
vec4 rep4(vec2 p, float n) {
    vec2 pn = p * n;
 	return vec4(fract(pn) * 2.0 - 1.0, floor(pn));   
}
vec2 rot(vec2 xy, float ang) {
	float s = sin(ang);
    float c = cos(ang);
    return xy * mat2(c, -s, s, c);
}
float sdCircle(vec2 p, float r) {
	return length(p) - r;
}
float sdTri(vec2 p, float h) {
	vec2 q = abs(p);
    return max(q.x*0.866025+p.y*0.5,-p.y)-h*0.5;
}
float sdRect(vec2 p, vec2 d) {
    vec2 pd = abs(p)-d;
	return min(max(pd.x,pd.y),0.)+length(max(pd,0.));
}
float sdHex(vec2 p, vec2 h){
    vec2 q = abs(p);
    return max(q.x*0.866025+q.y*0.5,q.y)-h.x;
}
float udRoundRect(vec2 p, vec2 b, float r) {
  	return length(max(abs(p)-b,0.0))-r;
}
float udRect(vec2 p, vec2 d) {
   	return length(max(abs(p)-d, 0.));
}
float asLine(float d, float thickness) {
	return (1. - smoothstep(0.,thickness,abs(d)));
}
vec2 scale(vec2 xy, vec2 s) {
	return xy.xy/s.xy;
}
float correct(float d) {
      float e = 2.0/iResolution.y;
      return d<0.1 ? d/length(vec2(dFdx(d),dFdy(d))/e) : d;
}
vec2 mov(vec2 xy, vec2 t) {
	return xy - t;
}
float asFilled(float d) {
	return (1. - smoothstep(0.,.01,d));
} 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = vec2(iResolution.x/iResolution.y, 1.);
    vec2 uv = (fragCoord/iResolution.xy *2. - vec2(1.0)) * aspect;
    vec4 m = vec4((iMouse.xy / iResolution.xy*2.0 - vec2(1.0)) * aspect,(iMouse.zw / iResolution.xy*2.0 - vec2(1.0)) * aspect);
	vec4 uv4 = rep4(uv, 3.);    
    uv = uv4.xy;    
    float s = mod(uv4.z+uv4.w, 5.);
    float d = 0.0;
    if(s > 3.) {
        uv = rot(uv, iTime*-1.);
	    uv = rep(uv, sin(iTime)+1.5);
        d = sdCircle(uv, .75);
    	d = correct(d);
    	d = asLine(d, .02);
    } else if(s > 2.)  {
        uv = rot(uv, iTime);
    	d = sdRect(uv, vec2(.65));
        d = asLine(d, .13);
    } else if(s>1.) {
        uv = mov(uv,  vec2(0.,-.26));
    	uv = scale(uv, vec2(sin(iTime)*0.2+1.));
        d = sdTri(uv, .85);
        d = asLine(d, .13);
    } else if(s>0.) {
        uv = rot(uv, iTime);
        uv = rep(uv, sin(iTime)*0.5+0.75);
    	d = sdHex(uv, vec2(.75));
        d = asLine(d,.13);
    } else {
    	d = udRoundRect(uv, vec2(.7), .13);
        d = asLine(d, 0.13);
    }   
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(sin(m.x)*0.5+0.5,2.*uv4.z*(sin(iTime)*.5+.5),4.*uv4.w*(cos(m.x)*0.5+0.5)));
    fragColor = vec4(col,1.0)*d;
}

#define R       iResolution
#define P(i)    vec2( cos(k*(i)+T/10.), sin(k*(i)+T/10.) )           // heptastar vertex i
#define S(D,w)  smoothstep( 3., 0.,  length(D)*R.y - w )             // smooth draw
#define F(t,dt) smoothstep(.2,-.2, abs(t+.5-mod(T/3.-2.,14.))-dt/2.) // fading
float   T , k = 6.28*3./7.;                                // angle between heptastar vertices

float line(vec2 p, vec2 a, vec2 b) {                       // draw a line
    b -= a; p -= a;
    return S( p - b * clamp(dot(b,p)/dot(b,b), 0., 1.) , 0. );
}

vec2 point(float n) {                                      // point(n,t) on heptastar 
    float t = mod(T/5.+ n*7./12.,7.), i = floor(t);
    return mix( P(i), P(i+1.), .5-.5*cos(3.14*fract(t)) );
}

void mainImage( out vec4 O,  vec2 U )
{
    U = ( U+U - R.xy ) / R.y;
    T = iTime;
    O -= O;
    for (float i=0.; i<7.; i++)
        O.rga += line( U, P(i), P(i+1.) ) * F(9.,7.);      // yellow lines

    for (float n=0.; n<12.; n++) {  
        vec2 P0 = point(n),
             P1 = point(n+4.),
             P2 = point(n+3.);

        O.a += S( P0-U, 8. ) ;                             // dots
        O.ra += line( U, P0, P1 ) * (F(2.,1.)+F(9.,3.));   // triangles
        O.ba += line( U, P0, P2 ) * (F(4.,1.)+F(9.,5.));   // squares
    }
    O += (1.-O.a) * .7;                                    // background
}

vec3 random3(vec3 st)
{
    st = vec3( dot(st,vec3(127.1,311.7,211.2)),
            	dot(st,vec3(269.5,183.3, 157.1)), dot(st,vec3(269.5,183.3, 17.1))  );
   	return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

float noise3D(vec3 st) 
{
	vec3 i = floor(st) ;
  	vec3 f = fract(st);

    vec3 u = smoothstep(0.,1.,f);

	float valueNowxy01 =mix( mix( dot( random3(i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ),
                    		 	 dot( random3(i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                		mix( dot( random3(i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ),
                     		 	 dot( random3(i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y);
	float valueNowxy02 =mix( mix( dot( random3(i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ),
                    		 	 dot( random3(i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                		mix( dot( random3(i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ),
                     		 	 dot( random3(i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y);

    return abs(mix(valueNowxy01, valueNowxy02, u.z));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float _Time = iTime /*+ smoothstep(0.0, 0.25, abs(uv.x - abs(fract(iTime * 0.1)*2.-1.)))*/;

    // Time varying pixel color
    float refNoise = noise3D(vec3(uv.xy*7., _Time*0.8)) ;

    vec2 N=vec2(refNoise,refNoise);



    float index=0.1;
    float dotProduct=dot(N,uv);

    float constant1=1.-pow(index, 2.1)*(1.-pow(dotProduct,2.1));
    uv= index*uv  - (index*dotProduct+sqrt(constant1))*N;


    vec3 col = clamp(vec3(noise3D(vec3(uv.xy*2. + 100., _Time/4.))*1.,
                    noise3D(vec3(uv.xy*2. + 5220., _Time/4.))*1.0,
                    noise3D(vec3(uv.xy*2. + 6200., _Time/5.)))*2.+0.5,0.,1.);
    col.y = min(col.z, max(col.y, col.x))*col.x;


    // Output to screen
    fragColor = vec4(col,1.0);
}

