float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

#define N_SPHR 8

vec3 sunDir, vnCylOut;
float htSphr[N_SPHR], rSphr[N_SPHR], dstFar, tCur, tCyc, sFac, rHole, bCylRad, bCylHt, dCylOut;
int idObj;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

void SphrSetup ()
{
  float t, s, g, h0, v0, hMax;
  tCyc = 10.;
  t = mod (tCur, tCyc);
  sFac = (t < 0.5 * tCyc) ? 0.3 : 0.1;
  rHole = 0.01 + 0.3 * SmoothBump (0.45, 0.55, 0.05, mod (t / tCyc + 0.5, 1.));
  h0 = -5.1;
  for (int k = 0; k < N_SPHR; k ++) {
    s = float (k) + 2.;
    hMax = - h0 + 0.5 * pow (s, 1.5);
    g = 8. * hMax / (tCyc * tCyc);
    v0 = sqrt (2. * g * hMax); 
    htSphr[k] = 0.4 * (h0 + v0 * t - 0.5 * g * t * t);
    rSphr[k] = 0.4 * (0.2 + 0.03 * pow (s, 1.5));
  }
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  p.y -= - (bCylHt - 1.75);
  q = p;  q.y -= 0.2;
  d = PrCylDf (q.xzy, 3., 0.2);
  q = p;
  d = - SmoothMin (- d, PrSphDf (q - vec3 (0., 0.18, 0.), rHole), 0.5 * rHole);
  for (int k = 0; k < N_SPHR; k ++) {
    q = p;  q.y -= htSphr[k];
    d = SmoothMin (d, PrSphDf (q, rSphr[k]), sFac * rSphr[k]);
  }
  DMIN (1);
  q = p;  q.y -= 0.22;
  d = PrRoundBox2Df (vec2 (length (q.xz) - 3.1, q.y), vec2 (0.1, 0.22), 0.03);
  DMIN (2);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

void InCylHit (vec3 ro, vec3 rd)
{
  vec3 s;
  float a, b, w, ws, srdy;
  dCylOut = dstFar;
  vnCylOut = vec3 (0.);
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - bCylRad * bCylRad);
  if (w > 0.) {
    ws = sqrt (w);
    srdy = sign (rd.y);
    if (a > 0.) {
      dCylOut = (- b + ws) / a;
      s = ro + dCylOut * rd;
    } else s.y = bCylHt;
    if (abs (s.y) < bCylHt) vnCylOut.xz = - s.xz / bCylRad;
    else {
      dCylOut = (- srdy * ro.y + bCylHt) / abs (rd.y);
      vnCylOut.y = - srdy;
    }
  }
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  rd.y = abs (rd.y) + 0.001;
  return mix (vec3 (0.2, 0.3, 0.65) + 0.2 * pow (max (dot (rd, sunDir), 0.), 256.),
     vec3 (0.8), clamp (3. * (Fbm2 (0.02 * (rd.xz * (100. - ro.y) / rd.y + ro.xz + tCur)) -
     0.4) * rd.y + 0.2, 0., 1.));
}

vec3 ShStagGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.5 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 40; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += min (0.3, 3. * h);
    if (sh < 0.001) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 vn, col, rg;
  vec2 qw;
  float hy, hhy, hw, ww, f, b, aa, sRotH, sRotV, r, s, sh;
  InCylHit (ro + vec3 (0., - bCylHt + 1.25, 0.), rd);
  if (vnCylOut.y == 0.) {
    ro += dCylOut * rd;
    vn = vnCylOut;
    hy = (ro.y - 1.75) / bCylHt;
    hhy = abs (hy) - 0.43;
    aa = atan (vn.x, - vn.z) / pi;
    sRotH = mod (64. * 0.5 * (1. + aa) + 0.5, 1.) - 0.5;
    hw = 0.29;
    ww = 0.28;
    qw = abs (vec2 (sRotH, hhy));
    if (qw.x < ww && qw.y < hw) {
      qw = abs (qw - 0.5 * vec2 (ww, hw)) - vec2 (0.44 * ww, 0.47 * hw);
      if (max (qw.x, qw.y) < 0.) col = 0.7 * SkyCol (ro, reflect (rd, vn));
      else col = vec3 (0.5, 0.5, 0.4) * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) +
         0.5 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 128.);
    } else {
      col = vec3 (0.7, 0.72, 0.6);
      qw -= vec2 (ww, hw);
      if (abs (hy) > (1. - 0.85/16.) || max (qw.x, qw.y) < 0.02) {
        col *= 0.9;
        vn.xz = Rot2D (vn.xz, - pi * aa);
        if (abs (hy) > (1. - 0.85/16.)) {
          sRotV = (1. - abs (2. * SmoothBump (1. - 0.9/16., 0.97, 0.03, abs (hy)) - 1.));
        } else {
          sRotV = (1. - abs (2. * SmoothBump (hw, hw + 0.02, 0.02, abs (hhy)) - 1.)) * sign (hhy);
          vn.xz = Rot2D (vn.xz, 0.4 * pi * sign (sRotH) *
             (1. - abs (2. * SmoothBump (- (ww + 0.02), ww + 0.02, 0.04, sRotH) - 1.)));
        }
        vn.yz = Rot2D (vn.yz, -0.2 * pi * sRotV * sign (hy));
        vn.xz = Rot2D (vn.xz, pi * aa);
      } else {
        qw = 16. * vec2 (12. * aa, hy);
        rg = ShStagGrid (qw);
        col *= rg.y * (1. - 0.3 * Noisefv2 (8. * qw));
        rg.xz *= sign ((abs (vn.x) > 0.5) ? vn.x : vn.z);
        if (abs (vn.x) > 0.5) {
          if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
          else vn.xz = Rot2D (vn.xz, rg.x);
        } else {
          if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
          else vn.zx = Rot2D (vn.zx, rg.x);
        }
      }
      col = col * (0.4 + 0.6 * max (dot (vn, sunDir), 0.)) +
         0.1 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 64.);
    }
  } else if (vnCylOut.y > 0.) {
    ro += dCylOut * rd;
    r = length (ro.xz);
    vn = vec3 (0., 1., 0.);
    if (r < 1.) col = SkyCol (ro, reflect (rd, vn));
    else {
      b = 1. - smoothstep (-0.1, -0.01, rd.y) * smoothstep (0.4, 0.8, dCylOut / (1.6 * bCylRad));
      col = vec3 (0.75, 0.72, 0.72);
      s = b * SmoothBump (0.45, 0.55, 0.04, mod (2. * r, 1.));
      col *= 1. - 0.05 * s;
      vn.y = 1. - 0.05 * s;
      vn.xz = - sign (s) * sqrt (1. - vn.y * vn.y) * ro.xz / r;
      col *= (1. - 0.2 * b * Noisefv2 (128. * ro.xz));
      sh = ObjSShadow (ro, sunDir);
      col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
         0.1 * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 64.);
    }
  } else col = SkyCol (ro, rd);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, cRefl;
  float dstObj, s, r, sh;
  dstObj = ObjRay (ro, rd);
  cRefl = vec3 (1.);
  if (dstObj < dstFar && idObj == 1) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (ro.y < 0.41 - (bCylHt - 1.75)) {
      r = length (ro.xz);
      s = 0.001 * sin (2. * pi * (4. * r - 0.5 * tCur)) / (r * r);
      vn.y = 1. - abs (s);
      vn.xz = - sign (s) * sqrt (1. - vn.y * vn.y) * ro.xz / r;
      vn = VaryNf (8. * ro + 0.2 * tCur * vec3 (0., 0., 1.), vn, 0.05);
    } else cRefl *= vec3 (1., 0.95, 1.);
    rd = reflect (rd, vn);
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
  }
  if (dstObj < dstFar && idObj == 1) {
    cRefl *= vec3 (1., 0.95, 1.);
    ro += dstObj * rd;
    vn = ObjNf (ro);
    rd = reflect (rd, vn);
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) col = SkyCol (ro, reflect (rd, vn));
    else if (idObj == 2) {
      col = vec3 (0.6, 0.5, 0.5) * (1. - 0.2 * SmoothBump (0.45, 0.55, 0.03,
         mod (32. * atan (ro.z, - ro.x) / pi, 1.)));
      vn = VaryNf (32. * ro, vn, 1.);
      sh = ObjSShadow (ro, sunDir);
      col *= 0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.);
    }
  } else {
    col = BgCol (ro, rd);
  }
  col *= cRefl;
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec3 ro, rd, vd;
  vec2 canvas, uv;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  SphrSetup ();
  bCylRad = 16.;
  bCylHt = 3.;
  ro = vec3 (0.2, 6., -12.);
  vd = normalize (vec3 (0., max (0.4 * htSphr[N_SPHR - 1], -0.2), 0.) - ro);
  vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / sqrt (1. - vd.y * vd.y),
     vec3 (- vd.y * vd.x, 1. - vd.y * vd.y, - vd.y * vd.z) / sqrt (1. - vd.y * vd.y), vd);
  rd = vuMat * normalize (vec3 (uv, 4.5));
  dstFar = 50.;
  sunDir = normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

