#define PI				3.1415926535
#define LIGHT_DIR		normalize(vec3(1., 2., 1.5))

//#define CAM_PARAM		smoothstep( 0., 1., max(mod(iTime*.2, 1.)-.8, 0.)*5.)
//#define CAM_PARAM		(iTime*.3)
#define CAM_PARAM		0.
#define CAM_POS 		vec3(2./sqrt(2.)*1.5*cos(CAM_PARAM*2.*PI+PI*.25), 1.5, 2./sqrt(2.)*1.5*sin(CAM_PARAM*2.*PI+PI*.25))

#define WIDTH	.1
#define SIZE	1.25

#define CAM_CTRL	false

#define NB_SIDES	4.
#define NB_LOOPS	.5

#define AA		2.


float polyDist(vec2 p, float n, float rad, out vec2 norm, out float mat)
{    
    float alpha = atan(p.y, p.x)*.5/PI+.5;
    float alpha0 = (floor(alpha*n)/n-.5)*2.*PI;
    float alpha1 = (ceil(alpha*n)/n-.5)*2.*PI;

    vec2 p0 = rad*vec2(cos(alpha0), sin(alpha0));
    vec2 p1 = rad*vec2(cos(alpha1), sin(alpha1));

	vec2 pa = p-p0, ba = p1-p0;
    norm = normalize(ba.yx)*vec2(-1., 1.);
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    float d = length( pa - ba*h )*sign(dot(pa, norm));

    mat = alpha0/PI*.5+.5;

    if(texelFetch(iChannel0, ivec2(32, 2), 0).x < .5)
    	return -d;

    vec2 pp = rad*vec2(cos((alpha0+alpha1)*.5), sin((alpha0+alpha1)*.5));
    norm = -normalize(p-pp);
    return length(p-pp)-0.03;
}



vec2 sdBeam(vec3 p, out vec3 n)
{
    vec3 p_circle = normalize(p*vec3(1., 0., 1.));
    vec3 dif = p-p_circle;

    n = normalize(dif);
    float d = length(dif)-.2;

    vec3 X = normalize(p_circle);
    vec3 Y = vec3(0., 1., 0.);
    vec3 Z = cross(X, Y);

    float nb_side = 1.+ceil(0.01+iMouse.x/iResolution.x*7.);
    float nb_loops = round(2.*iMouse.y/iResolution.y*nb_side)/nb_side;

    if(CAM_CTRL)
    {
        nb_side  = NB_SIDES;
        nb_loops = NB_LOOPS;
    }
    else if(length(iMouse.xy)<1.)
    {
        nb_side  = 4.;
        nb_loops = 3./4.;
    }

    float alpha = atan(p_circle.z, p_circle.x)*nb_loops+iTime*.3;
    X = cos(alpha)*X+sin(alpha)*Y;
    Y = cross(Z, X);

    vec2 norm2d;
    float mat;
    d = polyDist(vec2(dot(dif, X), dot(dif, Y)), nb_side, .2, norm2d, mat);

    n = -norm2d.x*X-norm2d.y*Y;

    if(nb_loops*nb_side > .5)
      mat += (alpha/nb_loops/PI*.5+.5)*nb_loops;

    return vec2(d, mat);
}


vec2 map(vec3 p, out vec3 n )
{
    vec2 res = vec2( 1000., 1.0 );

    res = sdBeam(p,  n);

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd, out vec3 n )
{
    float tmin = 0.1;
    float tmax = 20.0;

    float t = tmin;
    float m = -1.0;
    for( int i=0; i<64; i++ )
    {
	    float precis = 0.0005*t;
	    vec2 res = map( ro+rd*t, n );
        if( res.x<precis || t>tmax ) break;
        t += res.x*.75;
        //t += 0.05;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;

    return vec2( t, m );
}

vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 colorMap(float t)
{
    return hsv2rgb(vec3(t, 1., 1.));
}


vec3 render(in vec3 ro, in vec3 rd)
{
    vec3 col = .6+.4*vec3(pow(max(0., dot(rd, LIGHT_DIR)), 2.));

    vec3 nor;
    vec2 res = castRay(ro,rd, nor);
    if( res.y>-0.5 )
    {
        col = colorMap(res.y);
        //col = vec3(1.);
        col = (.1+.9*max(0., dot(nor, LIGHT_DIR))) * (.2+.8*col);

        //col = nor;
    }

    return col;
}


mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
    //*
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
	/*/
    vec3 cv = normalize(cp - dot(cp, cw)*cw);
    vec3 cu = vec3(1., 0.,0.);
    cu = normalize(cu-dot(cu, cv)*cv-dot(cu, cw)*cw);
    cu *= -determinant(mat3( cu, cv, cw ));
    //*/
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/ iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

    // ray computation
    vec3 ro = vec3(0.,1.,0.) + 2.*vec3(cos(mo.x*PI*2.)*cos(mo.y*.5*PI), sin(mo.y*.5*PI), sin(mo.x*PI*2.)*cos(mo.y*.5*PI));
    if(!CAM_CTRL)
        ro = CAM_POS;

    vec3 ta = vec3(0.);
    mat3 m = setCamera(ro, ta, 0.0);
    vec3 rd = m*normalize(vec3(p, 2.));

    ro = ro + m[0]*p.x + m[1]*p.y;
    rd = m[2];

    // scene rendering (using oversampling)
    vec3 col;


    for(float ii=0.; ii<AA; ii+=1.)
    for(float jj=0.; jj<AA; jj+=1.)
    {
    	col += render( ro, rd+(m[0]*ii+m[1]*jj)/AA/iResolution.y/2.);
    }
    col /= AA*AA;

    // gamma correction
    col = sqrt(col);


    fragColor = vec4(col, 1.0);
}

//Setup=========
const int n = 12;
const float rotSpeed = 0.5;
const float circleBorder = 0.8;
const float lineBorder = 0.0025;
const vec3 backCol = vec3(0.0, 0.0, 0.0);
const vec3 lineCol = vec3(0.0, 0.75, 0.0);
const vec3 circCol = vec3(0.75, 0.0, 1.0);
//==============

const float nf = float(n);
const float r = 1.0 / float(n * 2);
const float lengthLine = r * 0.9;
const float rSqr = r * r;
const float rSqr2 = rSqr * circleBorder;

#define M_PI 3.141593

vec3 drawCircles(in vec2 uv)
{
    vec3 col = backCol;

    // Time based radius
    float rtime = abs(cos(iTime * rotSpeed)) * rSqr;
    float rtime2 = abs(cos(iTime * rotSpeed)) * rSqr2;

    // Shift the coordinate system by the radius
    // Otherwise everything is drawn in the bottom left corner instead of the center of the circle
    uv.x = uv.x - r;
    uv.y = uv.y - r;

    // Get the x coord of center of the circle closest to our current coord
    float i = round(uv.x * nf);
    float a = i / nf;

    // Get the y coord of center of the circle closest to our current coord
    float j = round(uv.y * nf);
    float b = j / nf;

    // Circle Equation
    float ax = uv.x - a;
    float by = uv.y - b;
    float circle = by * by + ax * ax;

    //Line Coords
    float x = ax;
    float y = by;

    //Controls the rotational offset of the lines
    float iTime2 = iTime + M_PI / 2.0;
    float iTime3 = iTime + M_PI / 4.0;
    float iTime4 = iTime - M_PI / 4.0;

    //Line Equations
    float lineEq1 =   sin(iTime) * x - cos(iTime) * y
                    + cos(iTime) * x + sin(iTime) * y;

    float lineEq2 =   sin(iTime2) * x - cos(iTime2) * y
                    + cos(iTime2) * x + sin(iTime2) * y;

    float lineEq3 =   sin(iTime3) * x - cos(iTime3) * y
                    + cos(iTime3) * x + sin(iTime3) * y;

    float lineEq4 =   sin(iTime4) * x - cos(iTime4) * y
                    + cos(iTime4) * x + sin(iTime4) * y;

    // Circle color
    col += circCol * max(sign(rtime - circle ), 0.0) 
                   * max(sign(circle - rtime2), 0.0);

    // Line Color
    col += lineCol * max(sign(rtime2 - circle), 0.0) 
        *  (  max(sign(lineBorder - abs(lineEq1)), 0.0)
           +  max(sign(lineBorder - abs(lineEq2)), 0.0)
           +  max(sign(lineBorder - abs(lineEq3)), 0.0)
           +  max(sign(lineBorder - abs(lineEq4)), 0.0));

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = drawCircles(uv);

    fragColor = vec4(col,1.0);
}

// The MIT License
// Based on Inigo Quilez Menger Sponge
// http://www.iquilezles.org/www/articles/menger/menger.htm

#define levels 7.
#define steps 250

// light
vec3 light = normalize(vec3(1.0, 0.9, 0.3));

float msize = pow(3., (levels - 3.));

float maxcomp(in vec3 pos) { return max(pos.x, max(pos.y, pos.z));}
int msteps = steps;

float sdBox(vec3 pos, vec3 size)
{
  vec3 delta = abs(pos) - size;
  float mc = maxcomp(delta);
  return min(mc, length(max(delta, 0.0)));
}

vec4 map(in vec3 pos)
{
    float d = sdBox(pos, vec3(msize));
    vec4 res = vec4( d, 1.0, 0.0, 0.0 );

    float size = (-3. / msize);
    for( float lvl = 0.; lvl < levels; lvl++ )
    {
        // 
        vec3 a = mod( pos * size, 2.0 ) - 1.0;
        // Increase size by 3 each level
        size *= 3.0;
        vec3 r = abs(3.0 - 3.0 * abs(a));
        float da = max(r.x, r.y);
        float db = max(r.y, r.z);
        float dc = max(r.z, r.x);
        float c = (min(da, min(db, dc)) - 1.0) / size;

        // if point is outside box
        if( c > d )
        {
          d = c;
          res = vec4( d, min(res.y, 0.2 * da * db * dc), (1.0 + float(lvl)) / levels, 0.0 );
        }
    }

    return res;
}

vec4 intersect( in vec3 ro, in vec3 rd )
{
    float t = 0.0;
    vec4 res = vec4(-1.0);
	vec4 h = vec4(1.0);
    for( int i=0; i < msteps; i++ )
    {
		if( h.x < 0.001 || t > msize ) break;
        h = map(ro + rd*t);
        res = vec4(t,h.yzw);
        t += h.x;
    }
	if( t > msize ) res=vec4(-1.0);
    return res;
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float k )
{
    float res = 1.0;
    float t = mint;
	float h = 1.0;
    for( int i = 0; i < msteps; i++ )
    {
        h = map(ro + rd * t).x;
        res = min(res, k * h / t);
		t += clamp(h, 0.0005, 0.1);
    }
    return clamp(res,0.0,1.0);
}

vec3 calcNormal(in vec3 pos)
{
    vec3 eps = vec3(0.001, 0.0, 0.0);
    vec3 nor;
    nor.x = map(pos+eps.xyy).x - map(pos-eps.xyy).x;
    nor.y = map(pos+eps.yxy).x - map(pos-eps.yxy).x;
    nor.z = map(pos+eps.yyx).x - map(pos-eps.yyx).x;
    return normalize(nor);
}

vec3 render(in vec3 ro, in vec3 rd)
{
    // background color
    float f = rd.y * 0.5 + 0.5;
    vec3 col = mix( vec3(0.8, 0.3, 0.2), vec3(0.7, 0.7, 0.2), f);
    vec2 r = rd.xy;
    r.x = abs(abs(r.y) - abs(r.x));
    col *= vec3(texture(iChannel0, r.xy).x);

    vec4 tmat = intersect(ro, rd);
    if( tmat.x > 0.0 )
    {
        vec3  pos = ro + tmat.x * rd;
        vec3  nor = calcNormal(pos);

        float occ = tmat.y;
		float sha = softshadow(pos, light, 0.1, 128.0);

		float dif = max(0.1 + 0.9 * dot(nor, light), 0.0);
		float sky = 0.5 + 0.5 * nor.y;
        float bac = max(0.4 + 0.6 * dot(nor, vec3(-light.x, light.y, -light.z)), 0.0);

        vec3 lin  = vec3(0.0);
        lin += 1.00 * dif * vec3(1.10, 0.85, 0.60) * sha;
        lin += 0.50 * sky * vec3(0.10, 0.20, 0.40); // * occ;
        lin += 0.10 * bac * vec3(1.00, 1.00, 1.00) * (0.5 + 0.5 * occ);
        lin += 0.25 * occ * vec3(0.15, 0.17, 0.20);	 

        vec3 matcol = vec3(
            0.6 + 0.5 * cos(2.0 + 2.0 * tmat.z),
            0.5 + 0.5 * cos(1.0 + 1.0 * tmat.z),
            0.5 + 0.5 * cos(0.0 + 0.0 * tmat.z) );
        col = matcol * lin;
    }

    //return col;
    return pow( col, vec3(0.4545) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    float ctime = iTime;
    // camera
    vec3 ro = 10. * vec3(0.5 * sin(0.25 * ctime), 1.0 + 1.0 * cos(.13 * ctime), 0.5 * cos(0.25 * ctime));
    vec3 ww = normalize(vec3(0.0) - ro);
    vec3 uu = normalize(cross( vec3(0.0, 1.0, 0.0), ww ));
    vec3 vv = normalize(cross(ww, uu));
    vec3 rd = normalize( p.x * uu + p.y * vv + 2.5 * ww );

    vec3 col = render( ro, rd );

    fragColor = vec4(col, 1.0);
}

