  qq.xy = Rot2D (qq.xy, antEl);
  q = qq;  q.y -= 0.17;
  d = max (PrFlatCylDf (q.yxz, 0.17, 0.18, 0.15), 0.05 - abs (q.z));
  DMINQ (idJnt);
  qq.y -= rAnt.y + 0.45;
  r = length (qq);
  if (r > 0.) {
    q = qq;
    q.xz = Rot2D (q.xz, 2. * pi * (floor (netAz * atan (q.z, - q.x) / (2. * pi) + 0.5) / netAz));
    d = abs (q.z) - w;
    q = qq;
    q.xz = Rot2D (q.xz, pi / netAz);
    q.xz = Rot2D (q.xz, 2. * pi * (floor (netAz * atan (q.z, - q.x) / (2. * pi) + 0.5) / netAz));
    q.xy = Rot2D (q.xy, 0.5 * pi * (floor (netEl * asin (q.y / r) / (0.5 * pi) + 0.5) / netEl));
    d = min (d, abs (q.y) - w);
    q = qq;
    d = max (max (d, PrEllips2AnDf (q.xzy, rAnt, w)), q.y - w);
    q.y -= 2.5 * w;
    d = min (d, PrTorusDf (q.xzy, 1.5 * w, rAnt.x));
    DMINQ (idMesh);
    q = qq;
    q.xz = Rot2D (q.xz, 2. * pi * (floor (3. * atan (q.z, - q.x) / (2. * pi) + 0.5) / 3.));
    q.x -= - rAnt.x;
    q.xy = Rot2D (q.xy, 0.3 * pi);
    q.y -= 1.5;
    d = PrCylDf (q.xzy, 0.03, 1.5);
    DMINQ (idLegR);
    q = qq;  q.y -= 1.8;
    d = PrCylAnDf (q.xzy, 0.25, 0.03, 0.05);
    DMINQ (idRefl);
    q = qq;  q.y -= 0.02 - rAnt.y;
    d = PrCylDf (q.xzy, 0.5, 0.07 * (1. - dot (q.xz, q.xz) / 0.3));
    DMINQ (idDsk);
  }
  return dMin;
}

void SetGrdConf ()
{
  antAz = pi * sin (0.1 * tCur);
  antEl = 0.25 * pi * (1. + 0.8 * sin (tCur));
  if (gId.x == 0. && gId.y == 0.) {
    antEl = 0.1 * pi;
    antAz *= 0.5;
  } else if (gId.y == 0.) {
    antAz *= -1.;
  } else if (gId.x + gId.y == 0.) {
    antEl = 0.5 * pi - antEl;
  }
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.y == 0.) rd.y = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
  s = Minv3 (hv);
  gIdP = vec2 (-999.);
  dHit = 0.;
  for (int j = 0; j < 250; j ++) {
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (max (max (abs (gId.x), abs (gId.y)), abs (gId.x + gId.y)) <= hgLim) {
      if (gId.x != gIdP.x || gId.y != gIdP.y) {
        gIdP = gId;
        SetGrdConf ();
      }
      d = ObjDf (p);
    } else d = dstFar;
    if (dHit + d < s) dHit += d;
    else {
      dHit = s + 0.001;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
    }
    if (d < 0.0005 || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= 0.0005) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  gIdP = vec2 (-999.);
  d = 0.1;
  for (int j = 0; j < 60; j ++) {
    p = ro + d * rd;
    gId = PixToHex (p.xz / hgSize);
    if (max (max (abs (gId.x), abs (gId.y)), abs (gId.x + gId.y)) > hgLim) break;
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetGrdConf ();
    }
    h = ObjDf (p);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += min (0.18, 3. * h);
    if (h < 0.001) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 ShStagGrid (vec2 p, vec2 g)
{
  vec2 q, sq, ss;
  q = p * g;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.3 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 SkyCol (vec3 rd)
{
  vec3 col, rds, mDir, vn;
  float mRad, bs, ts;
  if (isNt) {
    mDir = normalize (vec3 (-1., 0.1, 0.));
    mRad = 0.01;
    col = vec3 (0.02, 0.02, 0.04) + vec3 (0.06, 0.04, 0.02) *
       pow (clamp (dot (rd, mDir), 0., 1.), 16.);
    bs = dot (rd, mDir);
    ts = bs * bs - dot (mDir, mDir) + mRad * mRad;
    if (ts > 0.) {
      ts = bs - sqrt (ts);
      if (ts > 0.) {
        vn = normalize ((ts * rd - mDir) / mRad);
        col += 0.8 * vec3 (1., 0.9, 0.5) * clamp (dot (vec3 (0.5, 0.4, -0.77), vn) *
           (1. - 0.3 * Noisefv3 (4. * vn)), 0., 1.);
      }
    }
    if (ts < 0.) {
      rds = floor (2000. * rd);
      rds = 0.00015 * rds + 0.1 * Noisefv3 (0.0005 * rds.yzx);
      for (int j = 0; j < 19; j ++) rds = abs (rds) / dot (rds, rds) - 0.9;
      col += smoothstep (0.01, 0.04, rd.y) * vec3 (0.8, 0.8, 0.6) *
         min (1., 0.5e-3 * pow (min (6., length (rds)), 5.));
    }
  } else {
    rd.y = abs (rd.y) + 0.001;
    col = mix (vec3 (0.2, 0.3, 0.5) + 0.3 * pow (max (dot (rd, sunDir), 0.), 8.), vec3 (1.),
       0.2 + 0.8 * rd.y * Fbm2 (2. * rd.xz / rd.y));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, rg, ltDir;
  float dstObj, spec, a, r, h, sh, atten;
  bool isBg, isLit;
  netAz = 84.;
  netEl = 24.;
  dstObj = ObjRay (ro, rd);
  isBg = true;
  isLit = false;
  if (dstObj < dstFar) {
    isBg = false;
    ro += dstObj * rd;
    gId = PixToHex (ro.xz / hgSize);
    vn = ObjNf (ro);
    spec = 0.1;
    r = length (qHit.xz);
    a = atan (qHit.z, - qHit.x) / (2. * pi);
    if (idObj == idBase) {
      col = vec3 (0.6, 0.3, 0.1);
      h = smoothstep (-0.45, -0.35, qHit.y);
      if (vn.y < 0.01) {
        rg = ShStagGrid (vec2 (a, qHit.y), vec2 (32., 8.));
        col *= rg.y * (1. - 0.2 * Noisefv2 (16. * vec2 (8. * a, qHit.y) + 17. * gId));
        if (rg.x == 0.) {
          vn.y = rg.z * h;
          vn.xz *= sqrt (1. - vn.y * vn.y * h);
        } else vn.xz = Rot2D (vn.xz, - rg.x * h);
      } else col *= 0.5 + 0.5 * SmoothBump (0.05, 0.95, 0.02, mod (6. * r, 1.));
      col = mix (vec3 (0.45, 0.4, 0.1) * (1.-
         0.3 * Noisefv2 (64. * vec2 (8. * a, qHit.y))), col, h);
      vn = VaryNf (32. * qHit, vn, 0.5);
      spec = 0.05;
    } else if (idObj == idRng) {
      col = vec3 (0.4, 0.43, 0.4) * (0.5 +
         0.5 * SmoothBump (0.05, 0.95, 0.02, mod (36. * a, 1.)));
    } else if (idObj == idLegB) {
      col = vec3 (0.4, 0.43, 0.4) * (0.5 +
         0.5 * SmoothBump (0.05, 0.95, 0.02, mod (8. * qHit.y, 1.)));
    } else if (idObj == idJnt) {
      col = vec3 (0.5, 0.5, 0.55);
      if (abs (qHit.z) > 0.24 && length (qHit.xy - vec2 (0., 0.27)) < 0.08) col *= 0.5;
    } else if (idObj == idMesh) {
      col = vec3 (0.6, 0.6, 0.65);
      spec = 0.2;
    } else if (idObj == idLegR) {
      col = vec3 (0.6, 0.5, 0.4) * (0.5 +
         0.5 * SmoothBump (0.1, 0.9, 0.02, mod (8. * qHit.y, 1.)));
    } else if (idObj == idRefl) {
      if (isNt) {
        col = vec3 (1., 0.7, 0.2) * (0.3 +
           0.7 * SmoothBump (0.4, 0.6, 0.02, mod (18. * a, 1.)));
        isLit = true;
      } else col = vec3 (0.6, 0.5, 0.4) * (0.5 +
         0.5 * SmoothBump (0.05, 0.95, 0.02, mod (36. * a, 1.)));
    } else if (idObj == idDsk) {
      col = vec3 (0.7, 0.6, 0.4);
      if (qHit.y > 0.) col *= 0.7 +
         0.3 * SmoothBump (0.1, 0.9, 0.02, mod (16. * r + a, 1.));
    }
  } else if (rd.y < 0.) {
    isBg = false;
    dstObj = - ro.y / rd.y;
    ro += dstObj * rd;
    col = 0.7 * mix (vec3 (0.35, 0.4, 0.2), vec3 (0.45, 0.4, 0.1),
       smoothstep (0.3, 0.7, Fbm2 (ro.xz)));
    spec = 0.;
    vn = VaryNf (ro, vec3 (0., 1., 0.), 2.);
  }
  if (isBg) {
    col = SkyCol (rd);
  } else if (! isLit) {
    if (isNt) {
      ltDir = ltPos - ro;
      atten = 1.2 * smoothstep (0.1, 0.2, dot (ltAx, - ltDir)) /
         (1. + 0.002 * pow (length (ltDir), 1.5));
      ltDir = normalize (ltDir);
    }
    sh = (dstObj < dstFar) ? ObjSShadow (ro, (isNt ? ltDir : sunDir)) : 1.;
    if (isNt) {
      col = mix (col, Maxv3 (col) * vec3 (1., 1., 0.8), 0.5);
      col = atten * (col * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
         2. * spec * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.));
    } else {
      col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
         spec * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
      col = mix (col, SkyCol (rd), smoothstep (0.5, 1., dstObj / dstFar));
    }
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvd, ori, ca, sa, mMid, ut, mSize;
  float el, az, asp, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  isNt = (mod (0.05 * tCur, 2.) < 1.);
  asp = canvas.x / canvas.y;
  mSize = (1./6.) * vec2 (asp, 1.);
  mMid = vec2 (1. / mSize.y, 1. / mSize.y - 1.) * mSize * vec2 (1. - 1./6., -1.);
  ut = abs (uv - mMid) - mSize;
  if (max (ut.x, ut.y) < 0.) {
    uv = (uv - mMid) / mSize.y;
    isNt = isNt = ! isNt;
  }
  hgSize = 10.;
  hgLim = 7.;
  az = 0.;
  el = -0.17 * pi;
  if (mPtr.z > 0. && (mPtr.x < 0.5 - 1./6. || mPtr.y > -0.5 + 1./6.)) {
    az += 3. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.01 * pi * tCur;
    el += 0.15 * pi * cos (0.02 * pi * tCur);
    if (mPtr.z > 0. && ! (mPtr.x < 0.5 - 1./6. || mPtr.y > -0.5 + 1./6.)) isNt = ! isNt;
  }
  el = clamp (el, -0.4 * pi, 0.4 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  zmFac = 6. - 4. * abs (el);
  ro = vuMat * vec3 (0., 0., -40.);
  if (ro.y < 1.) {
    ro.y = 1.;
    ro.xz *= sqrt (1599.) / length (ro.xz);
  }
  dstFar = 300.;
  sunDir = normalize (vec3 (cos (0.02 * pi * tCur), 1., sin (0.02 * pi * tCur)));
  ltPos = ro + vuMat * vec3 (3., 3., 0.);
  ltAx = vuMat * vec3 (0., 0., 1.);
#if ! AA
  rd = vuMat * normalize (vec3 (uv, zmFac));
  col = ShowScene (ro, rd);
#else
  col = vec3 (0.);
  for (int iy = 0; iy <= 1; iy ++) {
    for (int ix = 0; ix <= 1; ix ++) {
      uvd = (0.5 / canvas.y) * (2. * vec2 (float (ix), float (iy)) - 1.);
      rd = vuMat * normalize (vec3 (uv + uvd, zmFac));
      col += ShowScene (ro, rd);
    }
  }
  col *= 0.25;
#endif
  if (max (ut.x, ut.y) < 0. && min (abs (ut.x), abs (ut.y)) * canvas.y < 2.)
     col = vec3 (0.1, 0.1, 0.8);
  fragColor = vec4 (col, 1.);
}

float PrEllips2AnDf (vec3 p, vec2 r, float w)
{
  return max ((length (p / (r.xxy + w)) - 1.) * (min (r.x, r.y) + w),
            - (length (p / (r.xxy - w)) - 1.) * (min (r.x, r.y) - w));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrFlatCylDf (vec3 p, float rhi, float rlo, float h)
{
  float d;
  d = length (p.xy - vec2 (rhi * clamp (p.x / rhi, -1., 1.), 0.)) - rlo;
  if (h > 0.) d = max (d, abs (p.z) - h);
  return d;
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec3 HexGrid (vec2 p)
{
  vec2 q;
  p = p - HexToPix (PixToHex (p));
  q = abs (p);
  return vec3 (p, 0.5 * sqrt3 - q.x + 0.5 * min (q.x - sqrt3 * q.y, 0.));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

// from Syntopia http://blog.hvidtfeldts.net/index.php/2015/01/path-tracing-3d-fractals/
vec2 rand2n(vec2 co, float sampleIndex) {
    vec2 seed = co * (sampleIndex + 1.0);
	seed+=vec2(-1,1);
    // implementation based on: lumina.sourceforge.net/Tutorials/Noise.html
    return vec2(fract(sin(dot(seed.xy ,vec2(12.9898,78.233))) * 43758.5453),
                fract(cos(dot(seed.xy ,vec2(4.898,7.23))) * 23421.631));
}

vec3 hsv2rgb(vec3 c) {
    const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

const float GAMMA_COEFF = 2.2;
const float DISPLAY_GAMMA_COEFF = 1. / GAMMA_COEFF;

// Suggestions by FabriceNeyret2. Thank you.
#define gammaCorrect(rgb)  min( pow( rgb, vec3(DISPLAY_GAMMA_COEFF) ), 1.)
#define degamma(rgb)       min( pow( rgb, vec3(GAMMA_COEFF) ), 1.)
#define compProd(a,b)      mat2(a,-a.y,a.x) * b
#define compQuot(a,b)      mat2(a,(a).y,-(a).x)  * (b) / dot(b, b)
#define hue(h)  ( .6 + .6 * cos( 6.3 *  (h)  + vec4(0,23,21,0)  ) )

