const float PI = 3.141592;
const float TWO_PI = 2. * PI;
const int SAMPLE_NUM = 20;
const float GRAY_SCALE = 0.6;
const float CANVAS_SCALE = 4.;
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 sum = vec3(0);
    vec2 initPosition = 2. * ( (fragCoord.xy - (iResolution.xy * 0.5)) / iResolution.y );

    for(int i = 0 ; i < SAMPLE_NUM ; i++){
        vec2 position = initPosition + 2. * rand2n(fragCoord.xy, float(i)) / iResolution.y;
        position = position * CANVAS_SCALE;

        position = compQuot(position - vec2(1. + sin(iTime), 0.4 + cos(iTime)),
                            compProd(position, position) + position + vec2(1, 0)); 
        //position = compQuot(position - vec2(1., 0.), compProd(position, position) + position + vec2(1, 0)); 

        float absModulus = 2. * log(length(position));
        float modulusG = mix(1., fract(absModulus), GRAY_SCALE);

        float arg = atan(position.y, position.x) / TWO_PI;
        float argG = mix(1., fract(arg * 10.), GRAY_SCALE);
        sum += hsv2rgb(vec3(arg, 1., argG * modulusG));
    }
    fragColor = vec4((sum/float(SAMPLE_NUM)), 1.);
}

/*
Visualize convergence of Halley's method on the real axis.
Top row is reference color, lower rows are consecutive halley iterations.
Vertical lines are roots of the derivatives and outer root bounds.
Convergence basin's of Halley's method seem to be exactly determined by the roots
of the derivatives.
Newton's method and also all higher Householder Method's don't share this property.
Comment out in line 562 and following to try other methods.
*/

const float eps = .000005;
const float pi = 3.1416;
const float zoom = 3.5;
const float it_num = 30.;

vec3 x_to_col(float x){
	return .5+.5*vec3(sin(x),sin(x+2./3.*pi),sin(x+4./3.*pi));
}

//lagrange positive real root upper bound
//see for example: https://doi.org/10.1016/j.jsc.2014.09.038
float upper_bound_lagrange5(float a0, float a1, float a2, float a3, float a4){

	vec4 coeffs1 = vec4(a0,a1,a2,a3);

	vec4 neg1 = max(-coeffs1,vec4(0));
	float neg2 = max(-a4,0.);

	const vec4 indizes1 = vec4(0,1,2,3);
	const float indizes2 = 4.;

	vec4 bounds1 = pow(neg1,1./(5.-indizes1));
	float bounds2 = pow(neg2,1./(5.-indizes2));

	vec2 min1_2 = min(bounds1.xz,bounds1.yw);
	vec2 max1_2 = max(bounds1.xz,bounds1.yw);

	float maxmin = max(min1_2.x,min1_2.y);
	float minmax = min(max1_2.x,max1_2.y);

	float max3 = max(max1_2.x,max1_2.y);

	float max_max = max(max3,bounds2);
	float max_max2 = max(min(max3,bounds2),max(minmax,maxmin));

	return max_max + max_max2;
}

//lagrange upper bound applied to f(-x) to get lower bound
float lower_bound_lagrange5(float a0, float a1, float a2, float a3, float a4){

	vec4 coeffs1 = vec4(-a0,a1,-a2,a3);

	vec4 neg1 = max(-coeffs1,vec4(0));
	float neg2 = max(-a4,0.);

	const vec4 indizes1 = vec4(0,1,2,3);
	const float indizes2 = 4.;

	vec4 bounds1 = pow(neg1,1./(5.-indizes1));
	float bounds2 = pow(neg2,1./(5.-indizes2));

	vec2 min1_2 = min(bounds1.xz,bounds1.yw);
	vec2 max1_2 = max(bounds1.xz,bounds1.yw);

	float maxmin = max(min1_2.x,min1_2.y);
	float minmax = min(max1_2.x,max1_2.y);

	float max3 = max(max1_2.x,max1_2.y);

	float max_max = max(max3,bounds2);
	float max_max2 = max(min(max3,bounds2),max(minmax,maxmin));

	return -max_max - max_max2;
}

void sort_roots4(inout vec4 roots){
	vec4 tmp;

	float min1=min(roots[0],roots[1]);
	float min2=min(roots[2],roots[3]);

	float max1=max(roots[0],roots[1]);
	float max2=max(roots[2],roots[3]);

	float maxmin=max(min1,min2);
	float minmax=min(max1,max2);

	tmp[0]=min(min1,min2);
	tmp[1]=min(maxmin,minmax);
	tmp[2]=max(minmax,maxmin);
	tmp[3]=max(max1,max2);

	roots=tmp;
}

void sort_roots3(inout vec3 roots){
	vec3 tmp;

	tmp[0]=min(roots[0],min(roots[1],roots[2]));
	tmp[1]=max(roots[0],min(roots[1],roots[2]));
	tmp[2]=max(roots[0],max(roots[1],roots[2]));

	roots=tmp;
}

void sort_roots2(inout vec2 roots){
	vec2 tmp;

	tmp[0]=min(roots[0],roots[1]);
	tmp[1]=max(roots[0],roots[1]);

	roots=tmp;
}

float eval_poly5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;

	return f;
}

float eval_poly4(vec4 coeffs, float x){

	float f = (((x + coeffs[3]) * x + coeffs[2]) * x + coeffs[1]) * x + coeffs[0];

	return f;
}

float eval_poly3(vec3 coeffs, float x){

	float f = ((x + coeffs[2]) * x + coeffs[1]) * x + coeffs[0];

	return f;
}

float eval_poly2(vec2 coeffs, float x){

	float f = (x + coeffs[1]) * x + coeffs[0];

	return f;
}

float eval_poly1(float coeff, float x){

	float f = x + coeff;

	return f;
}

float sixth_householder_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;
	float f2 = ((20. * x + 12. * a4) * x + 6. * a3) * x + 2. * a2;
	float f3 = (60. * x + 24. * a4) * x + 6. * a3;
	float f4 = 120. * x + 24. * a4;
	float f5 = 120.;

	return x - 6.*(120.*f*f1*f1*f1*f1*f1 - 240.*f*f*f1*f1*f1*f2 + 90.*f*f*f*f1*f2*f2 - 10.*f*f*f*f*f1*f4 + f*f*f*f*f*f5 + 20.*(3.*f*f*f*f1*f1 - f*f*f*f*f2)*f3)/(720.*f1*f1*f1*f1*f1*f1 - 1800.*f*f1*f1*f1*f1*f2 + 1080.*f*f*f1*f1*f2*f2 - 90.*f*f*f*f2*f2*f2 + 20.*f*f*f*f*f3*f3 + 12.*f*f*f*f*f1*f5 + 120.*(4.*f*f*f1*f1*f1 - 3.*f*f*f*f1*f2)*f3 - 30.*(3.*f*f*f*f1*f1 - f*f*f*f*f2)*f4);
}

float fifth_householder_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;
	float f2 = ((20. * x + 12. * a4) * x + 6. * a3) * x + 2. * a2;
	float f3 = (60. * x + 24. * a4) * x + 6. * a3;
	float f4 = 120. * x + 24. * a4;
	float f5 = 120.;

	return x - 5.*(24.*f*f1*f1*f1*f1 - 36.*f*f*f1*f1*f2 + 6.*f*f*f*f2*f2 + 8.*f*f*f*f1*f3 - f*f*f*f*f4)/(120.*f1*f1*f1*f1*f1 - 240.*f*f1*f1*f1*f2 + 90.*f*f*f1*f2*f2 - 10.*f*f*f*f1*f4 + f*f*f*f*f5 + 20.*(3.*f*f*f1*f1 - f*f*f*f2)*f3);
}

float fourth_householder_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;
	float f2 = ((20. * x + 12. * a4) * x + 6. * a3) * x + 2. * a2;
	float f3 = (60. * x + 24. * a4) * x + 6. * a3;
	float f4 = 120. * x + 24. * a4;

	return x-4.*(6.*f*f1*f1*f1 - 6.*f*f*f1*f2 + f*f*f*f3)/(24.*f1*f1*f1*f1 - 36.*f*f1*f1*f2 + 6.*f*f*f2*f2 + 8.*f*f*f1*f3 - f*f*f*f4);
}

float third_householder_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;
	float f2 = ((20. * x + 12. * a4) * x + 6. * a3) * x + 2. * a2;
	float f3 = (60. * x + 24. * a4) * x + 6. * a3;

	return x-3.*(2.*f*f1*f1 - f*f*f2)/(6.*f1*f1*f1 - 6.*f*f1*f2 + f*f*f3);
}

float laguerre_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;
	float f2 = ((20. * x + 12. * a4) * x + 6. * a3) * x + 2. * a2;

	float G=f1/f;
	float H=G*G-f2/f;
	float a=5./(G+sign(G)*sqrt(abs(4.*(5.*H-G*G))));

	return x - a;
}

float second_irrational_householder_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;
	float f2 = ((20. * x + 12. * a4) * x + 6. * a3) * x + 2. * a2;
	float f3 = (60. * x + 24. * a4) * x + 6. * a3;

	float b=(3.*f1*f2-f*f3)/(3.*f2);

	return x - 2.*f/(b+sign(b)*sqrt(abs(b*b-4.*((3.*f2*f2-2.*f1*f3)/(f2*6.))*f)));
}

float third_irrational_householder_iteration5(float coeffs[5], float x){

	float f = ((((x + coeffs[4]) * x + coeffs[3]) * x + coeffs[2]) * x + coeffs[1]) * x + coeffs[0];
	float f1 = (((5. * x + 4. * coeffs[4]) * x + 3. * coeffs[3]) * x + 2. * coeffs[2]) * x + coeffs[1];
	float f2 = ((20. * x + 12. * coeffs[4]) * x + 6. * coeffs[3]) * x + 2. * coeffs[2];
	float f3 = (60. * x + 24. * coeffs[4]) * x + 6. * coeffs[3];
	float f4 = 120. * x + 24. * coeffs[4];

	float q1=(f*f4-2.*f2*f3)/(6.*f2*f2-4.*f1*f3);
	float q2=(3.*f2*f3-4.*f3*f3)/(24.*f1*f3+36.*f2*f2);

	float a=f2/2.+f1*q1+f*q2;
	float b=f1+f*q1;
	float c=f;

	return x - 2.*f/(b+sign(b)*sqrt(abs(b*b-4.*a*c)));
}

float irrational_halley_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;
	float f2 = ((20. * x + 12. * a4) * x + 6. * a3) * x + 2. * a2;

	return x-(2.*f)/(f1+sign(f1)*sqrt(abs(f1*f1-2.*f*f2)));
}

float halley_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;
	float f2 = ((20. * x + 12. * a4) * x + 6. * a3) * x + 2. * a2;

	return x-(2.*f*f1)/(2.*f1*f1-f*f2);
}

float halley_iteration4(vec4 coeffs, float x){

	float f = (((x + coeffs[3]) * x + coeffs[2]) * x + coeffs[1]) * x + coeffs[0];
	float f1 = ((4. * x + 3. * coeffs[3]) * x + 2. * coeffs[2]) * x + coeffs[1];
	float f2 = (12. * x + 6. * coeffs[3]) * x + 2. * coeffs[2];

	return x - (2. * f * f1) / (2. * f1 * f1 - f * f2);
}

float newton_iteration5(float a0, float a1, float a2, float a3, float a4, float x){

	float f = ((((x + a4) * x + a3) * x + a2) * x + a1) * x + a0;
	float f1 = (((5. * x + 4. * a4) * x + 3. * a3) * x + 2. * a2) * x + a1;

	return x-f/f1;
}

// Modified from http://tog.acm.org/resources/GraphicsGems/gems/Roots3And4.c
// Credits to Doublefresh for hinting there
int solve_quadric(vec2 coeffs, inout vec2 roots){

    // normal form: x^2 + px + q = 0
    float p = coeffs[1] / 2.;
    float q = coeffs[0];

    float D = p * p - q;

    if (D < 0.){
		return 0;
    }
    else if (D > 0.){
		roots[0] = -sqrt(D) - p;
		roots[1] = sqrt(D) - p;

		return 2;
    }
}

int solve_cubic(vec3 coeffs, inout vec3 r){

	float a = coeffs[2];
	float b = coeffs[1];
	float c = coeffs[0];

	float p = b - a*a / 3.0;
	float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
	float p3 = p*p*p;
	float d = q*q + 4.0*p3 / 27.0;
	float offset = -a / 3.0;
	if(d >= 0.0) { // Single solution
		float z = sqrt(d);
		float u = (-q + z) / 2.0;
		float v = (-q - z) / 2.0;
		u = sign(u)*pow(abs(u),1.0/3.0);
		v = sign(v)*pow(abs(v),1.0/3.0);
		r[0] = offset + u + v;	

		//Single newton iteration to account for cancellation
		float f = ((r[0] + a) * r[0] + b) * r[0] + c;
		float f1 = (3. * r[0] + 2. * a) * r[0] + b;

		r[0] -= f / f1;

		return 1;
	}
	float u = sqrt(-p / 3.0);
	float v = acos(-sqrt( -27.0 / p3) * q / 2.0) / 3.0;
	float m = cos(v), n = sin(v)*1.732050808;

	//Single newton iteration to account for cancellation
	//(once for every root)
	r[0] = offset + u * (m + m);
    r[1] = offset - u * (n + m);
    r[2] = offset + u * (n - m);

	vec3 f = ((r + a) * r + b) * r + c;
	vec3 f1 = (3. * r + 2. * a) * r + b;

	r -= f / f1;

	return 3;
}

int solve_quartic(vec4 coeffs, inout vec4 s){

	float a = coeffs[3];
	float b = coeffs[2];
	float c = coeffs[1];
	float d = coeffs[0];

    /*  substitute x = y - A/4 to eliminate cubic term:
	x^4 + px^2 + qx + r = 0 */

    float sq_a = a * a;
    float p = - 3./8. * sq_a + b;
    float q = 1./8. * sq_a * a - 1./2. * a * b + c;
    float r = - 3./256.*sq_a*sq_a + 1./16.*sq_a*b - 1./4.*a*c + d;

	int num;

	/* doesn't seem to happen for me */
    //if(abs(r)<eps){
	//	/* no absolute term: y(y^3 + py + q) = 0 */

	//	vec3 cubic_coeffs;

	//	cubic_coeffs[0] = q;
	//	cubic_coeffs[1] = p;
	//	cubic_coeffs[2] = 0.;

	//	num = solve_cubic(cubic_coeffs, s.xyz);

	//	s[num] = 0.;
	//	num++;
    //}
    {
		/* solve the resolvent cubic ... */

		vec3 cubic_coeffs;

		cubic_coeffs[0] = 1.0/2. * r * p - 1.0/8. * q * q;
		cubic_coeffs[1] = - r;
		cubic_coeffs[2] = - 1.0/2. * p;

		solve_cubic(cubic_coeffs, s.xyz);

		/* ... and take the one real solution ... */

		float z = s[0];

		/* ... to build two quadric equations */

		float u = z * z - r;
		float v = 2. * z - p;

		if(u > -eps){
			u = sqrt(abs(u));
		}
		else{
			return 0;
		}

		if(v > -eps){
			v = sqrt(abs(v));
		}
		else{
			return 0;
		}

		vec2 quad_coeffs;

		quad_coeffs[0] = z - u;
		quad_coeffs[1] = q < 0. ? -v : v;

		num = solve_quadric(quad_coeffs, s.xy);

		quad_coeffs[0]= z + u;
		quad_coeffs[1] = q < 0. ? v : -v;

		vec2 tmp=vec2(1e38);
		int old_num=num;

		num += solve_quadric(quad_coeffs, tmp);

		//s[old_num] = tmp[0];
		//s[old_num+1] = tmp[1];

		if(old_num == 0){
            s[0] = tmp[0];
            s[1] = tmp[1];
        }
        else{//old_num == 2
            s[2] = tmp[0];
            s[3] = tmp[1];
        }

    }

    /* resubstitute */

    float sub = 1./4. * a;

	/* single halley iteration to fix cancellation */
	for(int i=0;i<4;i+=2){
		if(i < num){
			s[i] -= sub;
			s[i] = halley_iteration4(coeffs,s[i]);

			s[i+1] -= sub;
			s[i+1] = halley_iteration4(coeffs,s[i+1]);
		}
	}

    return num;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv-=.5;
	uv.x *= iResolution.x / iResolution.y;

	vec2 mouse = iMouse.xy / iResolution.xy;
	mouse-=.5;
	mouse.x *= iResolution.x / iResolution.y;

    float border = 2./iResolution.y;

	border *= zoom;
	uv *= zoom;
	mouse *= zoom;

	float a0 = -6. / 89.;
	float a1 = 158. / 267.;
	float a2 = 34. / 89.;
	float a3 = -464. / 267.;
	float a4 = -65. / 267.;

	float t0 = mod(iTime,6.*pi);

	a0 += sin(t0)*.3;
	a1 += sin(t0*2./3.)*.7;

    //mouse condition copied from mattz (https://www.shadertoy.com/view/4dyyR1)
    if(max(iMouse.x, iMouse.y) > 20.){
        a0=mouse.y;
        a1=mouse.x;
    }

	vec4 c1 = vec4(a1,2.*a2,3.*a3,4.*a4)/5.;
	vec3 c2 = vec3(c1[1],2.*c1[2],3.*c1[3])/4.;
	vec2 c3 = vec2(c2[1],2.*c2[2])/3.;
	float c4 = c3[1]/2.;

	float val = eval_poly5(a0,a1,a2,a3,a4,uv.x);
	float drv = eval_poly4(c1,uv.x)*5.;

	//float val = eval_poly4(c1,uv.x);
	//float drv = eval_poly3(c2,uv.x)*4.;

	//float val = eval_poly3(c2,uv.x);
	//float drv = eval_poly2(c3,uv.x)*3.;

	//float val = eval_poly2(c3,uv.x);
	//float drv = eval_poly1(c4,uv.x)*2.;

	float d0 = abs(val-uv.y)/length(vec2(drv,1.));

	d0 = min(d0,abs(uv.y));

	int num_intervals = 0;

	vec4 roots_drv=vec4(1e38);

	float ub = upper_bound_lagrange5(a0,a1,a2,a3,a4);
	float lb = lower_bound_lagrange5(a0,a1,a2,a3,a4);

	d0 = min(d0,abs(uv.x - ub));
	d0 = min(d0,abs(uv.x - lb));

	int num_roots_drv = solve_quartic(c1,roots_drv);
	sort_roots4(roots_drv);

	for(int i=0;i<4;i++){
		if(i < num_roots_drv){
			d0 = min(d0,abs(uv.x - roots_drv[i]));
		}
	}
	if(num_roots_drv != 4){
		vec3 roots_snd_drv=vec3(1e38);
		int num_roots_snd_drv=solve_cubic(c2,roots_snd_drv);
		for(int i=0;i<3;i++){
			if(i < num_roots_snd_drv){
				d0 = min(d0,abs(uv.x - roots_snd_drv[i]));
			}
		}
		if(num_roots_snd_drv != 3){
			vec2 roots_trd_drv = vec2(1e38);
			int num_roots_trd_drv = solve_quadric(c3,roots_trd_drv);

			for(int i=0;i<2;i++){
				if(i < num_roots_trd_drv){
					d0 = min(d0,abs(uv.x - roots_trd_drv[i]));
				}
			}
		}
	}

	float x = uv.x;

	float j = floor((1.-(uv.y/zoom+.5))*it_num);

	for(float i=0.;i<it_num;i+=1.){
		if(i<j){
			//x = newton_iteration5(a0,a1,a2,a3,a4,x);
			x = halley_iteration5(a0,a1,a2,a3,a4,x);
			//x = irrational_halley_iteration5(a0,a1,a2,a3,a4,x);
			//x = laguerre_iteration5(a0,a1,a2,a3,a4,x);
			//x = second_irrational_householder_iteration5(a0,a1,a2,a3,a4,x);
			//x = third_irrational_householder_iteration5(a0,a1,a2,a3,a4,x);
			//x = third_householder_iteration5(a0,a1,a2,a3,a4,x);
			//x = fourth_householder_iteration5(a0,a1,a2,a3,a4,x);
			//x = fifth_householder_iteration5(a0,a1,a2,a3,a4,x);
			//x = sixth_householder_iteration5(a0,a1,a2,a3,a4,x);
		}
	}

	vec3 col = x_to_col(x);

	fragColor = vec4(mix(vec3(0),col,smoothstep(0., border, d0)),1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;

    uv = vec2(length(uv)*(8.+2.*sin(iTime*.1)), sin(atan(uv.x,uv.y)*floor(5.+mod(iTime*.2,6.)) + iTime*.3) );

    vec3 col = vec3(0);

    uv+=sin(uv.yx*(sin(iTime*.3)*2.)+iTime*.6*vec2(1,.9));

    col += 1.-smoothstep(0.1,.2, length(uv-vec2(uv.x,0)));
    col += 1.-smoothstep(0.2,.3, length(uv-vec2(floor(uv.x+.5),uv.y)));

    col *= .5+.5*cos(6.28*vec3(0,.33,.66)+uv.y+iTime + col*2. );

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Popping isocubes by @duvengar-2018
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
///////////////////////////////////////////////////////////////////////////////////////////
// I've finaly found some time to play with the Minimal Hexagonal Grid example from @Shane.
// https://www.shadertoy.com/view/Xljczw


#define M(a) mat2(cos(a), -sin(a), sin(a), cos(a))
#define SAT(a) clamp(.0, .1, a)
#define T iTime
#define PI acos(-1.)
#define TWO_PI (PI * 2.)

const float LOWRES = 100.;

float hash2(vec2 p)
{
    // hash2 taken from Dave Hoskins 
    // https://www.shadertoy.com/view/4djSRW
	vec3 p3  = fract(vec3(p.xyx) * .2831);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

float rem(vec2 iR)
{
    float slices = 17. * floor(iR.y / LOWRES);

    return  sqrt(slices);
}

// taken from:
// Minimal Hexagonal Grid - Shane
///////////////////////////////////////////////////////////////////////////////////////////////////

// Helper vector.
// 30-60-90 triangle, which has sides of 1, sqrt(3) and 2.

const vec2 s = vec2(1, 1.7320508);

// The 2D hexagonal isosuface function
float hex(in vec2 p)
{

    p = abs(p);
    return max(dot(p, s *.5), p.x ); // Hexagon.

    //equivalent to:
    //return max(p.x *.5 + p.y *.866025, p.x); 
}

// This function returns the hexagonal grid coordinate for the grid cell, and the corresponding 
// hexagon cell ID - in the form of the central hexagonal point. That's basically all you need to 
// produce a hexagonal grid.

vec4 getHex(vec2 p)
{  
    // The hexagon centers: Two sets of repeat hexagons are required to fill in the space, and
    // the two sets are stored in a "vec4" in order to group some calculations together. The hexagon
    // center we'll eventually use will depend upon which is closest to the current point. Since 
    // the central hexagon point is unique, it doubles as the unique hexagon ID.
    vec4 hC = floor(vec4(p, p - vec2(.5, 1)) / s.xyxy) + .5;

    // Centering the coordinates with the hexagon centers above.
    vec4 h = vec4(p - hC.xy*s, p - (hC.zw + .5)*s);

    // Nearest hexagon center (with respect to p) to the current point. In other words, when
    // "h.xy" is zero, we're at the center. We're also returning the corresponding hexagon ID -
    // in the form of the hexagonal central point. Note that a random constant has been added to 
    // "hC.zw" to further distinguish it from "hC.xy."
    //
    // On a side note, I sometimes compare hex distances, but I noticed that Iomateron compared
    // the squared Euclidian version, which seems neater, so I've adopted that.
    return dot(h.xy, h.xy)<dot(h.zw, h.zw) ? vec4(h.xy, hC.xy) : vec4(h.zw, hC.zw + vec2(.5, 1));

}
/////////////////////////////////////////////////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // store global sound amplitude


    // Normalized domains

    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec2 uv2 = ( fragCoord.xy -.5  / iResolution.xy) / iResolution.y;

    // initialize some variables
    float motion = 325.543 + T * .5;		   // a speed
    vec3 col = vec3(.7, .66, 1.55) + .1;       // a weird base color
    float blr = .002;				           // a blur value
 	// scale and twist and rotate the domain
    uv *= M(.4 * cos(PI) * 1.5 * length(uv));
    uv *= 1.35;
   	uv *= M(PI);
    // spherize
    if(length(uv) < 1.)
    {    
        float d = mix(1.,1. + ( 1. - dot(uv, uv)),  1.); 
        blr = blr * d; 
   		uv = uv / d;   
    }
	// get a screen scaling ratio (for the stripe pattern scaling along resolutions);
    float dens = rem(iResolution.xy) ;

    // Finally make some the Hexagons
    vec2 pos = vec2(.0,.0);
    pos  -= motion + .1 * iMouse.xy / 10.;  // add motion and mouse interaction
    vec4 h = getHex( pos + 5. * uv + s.yx); // hexagons center
    float eDist = hex(h.xy);                // hexagone Edge distance.

	//float cDist = length(h.xy);           // @Shane: cDist = dot(h.xy, h.xy);


    float tilt  = hash2(h.zw*2376.345791);  // random value depending on cel ids
    // float yoyo = .0;				        // animation factor to rescale 

    // rotate hexa tiles depending on their unique random number
    // and animate the unique hexagon radius at the same time;
    // initialize color, flip et texture attribute.

    float red, flip, tex = .0;

    // vertical stripes pattern
    /////////////////////////////////////////////////////////////////////////////////
    //vec2 pat =   h.xy + motion;

    vec2 pat = h.xy;
	// hashed pattern with diferent angle;
    // vertical one
    float p2 = .08*smoothstep(.2, .1-blr,  length(fract(pat.x * dens * 1.75)-.5));
    // two obliques
    pat *= M(PI*.33);
    float p3 = .08*smoothstep(.2, .1-blr,  length(fract(pat.x * dens * 1.75)-.5));
    pat *= M(PI*.33);
    float p4 = .08*smoothstep(.2, .1-blr,  length(fract(pat.x * dens * 1.75)-.5));

    // sorting the cubes
    ///////////////////////////////////////////////////////////////////////////////////


    // wich tile are flipped?
    if( cos(5.* motion * sin(h.z - h.w)*tilt) > .0)
    {
       flip = 1.;
       h.xy *= M(PI);   
    } 
    // wich tile are orange?

    if(tilt > .7 && flip != .0){
        red = 1.;
    }


    // get polar coordinates in order cut the 3 faces form the hagagon
    // in order to create the cubes/(Rhombic effect) inside the tiling.
    vec2 pol  = vec2(atan(h.x, h.y) / TWO_PI + .5, length(uv));
    vec2 ang = vec2(.3333, .6666);

    //////////////////////////////////////////////////////////////////

    // Paint orange or gray cube

    if(red == 1. && flip != .0)
    {
        col += vec3(.7,-.1,-.35);
        col -= .1 * smoothstep(.45,.45-blr, eDist);
        tex = 1.;

    } else 
    {
        col -= .3;     
    }


    // shading the cubes faces
    // face 01 (left)
    if(pol.x <=  ang.x)
    {
    	col -= .2; 
        col += tex == 1. ? .0 : p2;
    }
    // face 02 (right)
    if(pol.x >= ang.y)
    {    
        col -= .375;
        col += tex == 1. ? .0 : p2;
    }
    // face 03 (top)
    if(pol.x > ang.x && pol.x < ang.y)
    {
     col += tex == 1. ? .0 : .25 * (p4+p3);
    }

    // face 04 (inside)
    vec2 ang2 = ang + vec2(-.1665,.1665);
    if(pol.x  <= ang2.x  || pol.x >= ang2.y ){

        col += red == 1. && flip == 1. ? .075 : .0; // only for red cubes
    }

    // Edges

    // drawing some tiny dark lines along the inner cubes edges and
    // simulate three partialy visible cubes.
    // and drawing the same three lines for the outer cubes to make their edges shinier
    if( flip == .0){
       col -= h.y < .0 ? .3 * smoothstep(.02,.0,length(h.x)) : .0; 
       vec2 hr = M(PI*.333) * h.xy;
       col -= h.y > .0 ? .3 * smoothstep(.02,.0,length(hr.x)) : .0; 
       hr = M(PI*.666) * h.xy;
       col -= h.y > .0 ? .3 * smoothstep(.02,.0,length(hr.x)) : .0; 

    } else if( red == 1. && flip == 1. ){
       col += h.y < .0 ? .3 * smoothstep(.015,.0,length(h.x)) : .0; 
       vec2 hr = M(PI*.333) * h.xy;
       col += h.y > .0 ? .3 * smoothstep(.015,.0,length(hr.x)) : .0; 
       hr = M(PI*.666) * h.xy;
       col += h.y > .0 ? .3 * smoothstep(.015,.0,length(hr.x)) : .0; 

    }




    // postprocessing

    col -= .2 * smoothstep(.49,.5,length(eDist));               // draw a light outline on hexagons
    if( flip == 1. && red == .0){
    col -= .2 * smoothstep(.48,.5,length(eDist)); 
    }
    vec3 fao = vec3((smoothstep(.0,2.,eDist)));                  // Fake lightning gradient 
    col -= flip > .0 ?  fao+.25 :  .5 - fao;                     // apply it as a shadow or light on the cubes
    col -= flip > .0 ? .05*smoothstep(.0,.5,length(eDist)) : .0;  
    col -= vec3(-.7, -.85, .0) + smoothstep(.0,4.0,length(uv));  // color correction gradualy from the center 
    col *= 1. - .1 * length(dot(uv,uv));                         // vigneting 
    col  = pow(col, vec3(1.5));                                  // gamma 

    // color output
    //col -= vec3(p2+p3+p4);
    fragColor = vec4(col,1.0);
}

