      } else if (ro.z < 0.) {
        if (abs (s) > flHt - 0.46) {
          col4 = vec4 (0.6, 0.3, 0.2, 0.1);
          vn.xz = Rot2D (vn.xz, 0.1 * pi * (1. + sin (2. * pi * mod (32. * ro.x, 1.))));
        } else col4 = mix (vec4 (1., 0., 0., 0.1), vec4 (0.1, 0., 0., 0.1),
           step (0.6, mod (16. * (abs (ro.x) - 0.8), 1.)));
      } else if (ro.z < 0.5) {
        wdPos = ro.x;
      }
      if (wdPos != -99.) col4 = mix (vec4 (0.4, 0.1, 0., 0.1), vec4 (0.3, 0.1, 0., 0.1),
         smoothstep (0.4, 0.5, Fbm2 (h + 2. * vec2 (16., 4.) * vec2 (wdPos, s))));
    } else if (idObj == idLt) {
      col4 = vec4 (0.9, 0.9, 0.5, -2.);
    } else if (idObj == idRob) {
      col4 = vec4 (HsvToRgb (vec3 (Hashff (33.3 * floor ((ro.y - sign (ro.x) *
         (spd + 32. * flHt) + flHt) / (2. * flHt))), 0.8, 0.8)), 0.2);
    } else if (idObj == idEye) {
      col4 = mix (vec4 (0., 1., 0., -2.), vec4 (0.7, 0., 0.3, -2.),
         smoothstep (0.9, 0.95, - dot (vn, rd)));
    }
    if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    col = col4.rgb;
    if (col4.a >= 0.) {
      ao = ObjAO (ro, vn);
      dfSum = 0.;
      spSum = 0.;
      for (int k = 0; k < 3; k ++) {
        ltDir = normalize (ltPos[k]);
        at = smoothstep (0.3, 0.5, dot (normalize (ltPos[k] - ro), ltDir));
        dfSum += at * max (dot (vn, ltDir), 0.);
        spSum += at * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
      }
      col = ao * (col * (0.1 + 0.4 * dfSum) + col4.a * step (dot (vn, rd), -0.05) * spSum);
    } else if (col4.a == -2.) col *= 0.7 - 0.3 * dot (vn, rd);
  } else col = vec3 (0.1);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd;
  vec2 canvas, uv;
  float az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = -0.5 * pi;
  if (mPtr.z > 0.) az -= 0.4 * pi * mPtr.x;
  az = clamp (az + 0.5 * pi, - 0.15 * pi, 0.15 * pi) - 0.5 * pi;
  ro = vec3 (4.8 * cos (az), 0.4, 4.8 * sin (az));
  vd = normalize (vec3 (0., 0.2, 0.) - ro);
  ro.x += 0.8 * SmoothBump (0.25, 0.75, 0.1, mod (0.1 * tCur, 1.)) *
     (2. *  floor (mod (0.1 * tCur, 2.)) - 1.);
  vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / sqrt (1. - vd.y * vd.y),
     vec3 (- vd.y * vd.x, 1. - vd.y * vd.y, - vd.y * vd.z) / sqrt (1. - vd.y * vd.y), vd);
  rd = vuMat * normalize (vec3 (uv, 2.7));
  dstFar = 30.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

//krshock 2018
float clr(vec2 uv, float uno, float dos){
    vec2 c = vec2(0,0);
    return cos(distance(c, uv)*uno) * cos(dos*(uv.x*uv.y));
}


vec3 cmap(float val, vec3 c1, vec3 c2, float f1, float f2){
    if(val>f1)
        return c1;
    if(val>f2)
        return c2;
    return vec3(0.1,0.0,0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy + vec2(-.5, -.5);
    uv.y *= iResolution.y/iResolution.x;
    uv.xy *= 1.0;
    float t = iTime*.1;
    float w1 = sin(1.0+t)*+cos(1.5+t*3.5);           //wave 1
    float w2 = 1.0+sin(2.0+t*5.0)+cos(4.0+t*7.0)*.5; //wave 2
	float uno=100.0+w1*50.0;    //
    float dos = 200.0+100.0*w2;
    float value = sin(t+cos(t))*.2+clr(uv, uno,dos);
	vec3 col = 0.8-cmap(value*value, vec3(.0,.0,.0), vec3(.7,.08,.08), .7,.4)*value;

    fragColor = vec4(col,1.0);
}

// "One-Pass Voronoi" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
void HexVorInit ();
vec4 HexVor (vec2 p);
vec3 HsvToRgb (vec3 c);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);

float tCur;
const float pi = 3.14159, sqrt3 = 1.7320508;

vec3 ShowScene (vec2 p)
{
  vec4 vc, col4;
  vec3 col, ltDir, vn;
  ltDir = normalize (vec3 (0., 1., 0.7));
  ltDir.xz = Rot2D (ltDir.xz, -0.2 * tCur);
  HexVorInit ();
  vc = HexVor (p - 0.2 * tCur);
  vc.yz *= smoothstep (0.1, 0.15, vc.x);
  vn = normalize (vec3 (vc.y, 0.6, vc.z));
  col4 = vec4 (HsvToRgb (vec3 (mod (0.1 * tCur + atan (vn.z, - vn.x) / (2. * pi), 1.), 0.9, 1.)), 0.2);
  col4 = mix (vec4 (0.3, 0.5, 0.3, 0.5), col4, smoothstep (0.1, 0.2, vc.x));
  col4 *= 0.6 + 0.4 * mix (SmoothBump (0.25, 0.75, 0.05, mod (12. * vc.x, 1.)),
     SmoothBump (0.25, 0.75, 0.05, mod (2. * vc.x / (1. - 0.8 * vn.y), 1.)), smoothstep (0.1, 0.2, vc.x));
  col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
     col4.a * pow (max (dot (normalize (ltDir + vn), vn), 0.), 32.);
  return col;
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  vec3 col;
  vec2 canvas, uv, p;
  float pSize;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  tCur += 20.;
  pSize = canvas.x / 200.;
  p = pSize * uv;
  fragColor = vec4 (ShowScene (p), 1.);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec2 gVec[7], hVec[7];

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.65;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 0.5 * pi * (u.y - 0.5) * tCur;
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.2) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

// inspired from "rainbow noise" by violetforest. https://shadertoy.com/view/4dyyDW

#define MOD int(mod(iTime/3.,4.))

#define hash(p) fract(sin(dot(p,vec3(127.1,311.7, 74.7)))*43758.5453123)

float noise (vec3 p) {
    vec3 i = floor(p);
    vec3 f = fract(p); f = f*f*(3.-2.*f); // smoothstep

    float v= mix( mix( mix(hash(i+vec3(0,0,0)),hash(i+vec3(1,0,0)),f.x),
                       mix(hash(i+vec3(0,1,0)),hash(i+vec3(1,1,0)),f.x), f.y), 
                  mix( mix(hash(i+vec3(0,0,1)),hash(i+vec3(1,0,1)),f.x),
                       mix(hash(i+vec3(0,1,1)),hash(i+vec3(1,1,1)),f.x), f.y), f.z);
	return   MOD==0 ? v
	       : MOD==1 ? 2.*v-1.
           : MOD==2 ? abs(2.*v-1.)
                    : 1.-abs(2.*v-1.);
}

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float fbm (vec3 p) {
    float v = 0.,  a = .5;
    mat2 R = rot(.37 + iTime/1e4);

    for (int i = 0; i < 9; i++, p*=2.,a/=2.) 
        p.xy *= R, p.yz *= R,
        v += a * noise(p);

    return v;
}

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.y;
    O = .5+ .55* cos( 9.*fbm(vec3(U,iTime/3.))+ vec4(0,23,21,0)); // hue
  //O *= abs(fract(3.*fbm(vec3(U,iTime/3.)+.5))*2.-1.);           // intensity
  //O *= abs(fract(3.*fbm(vec3(U,iTime/3.)))*2.-1.);              // intensity
  //O *= .5+fbm(vec3(U,iTime/3.)+.5);                             // intensity
    if ( length(U*4.-vec2(.1,.2*float(MOD+1)))<.1 ) O--;          // MOD
}
