#define M_PI 3.1415926535897932384626433832795
#define border(B,X) smoothstep(B, B+1e-4, X)
#define saturate(X) clamp(X,0.0,1.0)

#define drawPointX 0.75

vec4 screenColor(float x) {
	return vec4(x*0.75,x,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord/iResolution.xy;

	float tx = uv.x + iTime * 0.3;
	float dy = uv.y + 0.2*sin(20.0*tx);
	float sinLine = saturate(border(0.9999, sin(M_PI * dy)) - (0.75 - uv.x));
	if(uv.x > drawPointX) { sinLine = 0.0; }

	float hLines = border(0.99, sin(M_PI * uv.y * 20.0 + 0.1));
	float vLines = border(0.99, sin(M_PI * uv.x * 32.0 + 0.1));
	float grid = 0.5 * saturate(hLines + vLines);

	float drawDot = smoothstep(.88, .89, .9 - length(vec2(uv.x,dy)-vec2(drawPointX,0.5)));

	// Output to screen
	fragColor = screenColor(saturate(sinLine + grid + drawDot));
}

#define NUM_OCTAVES 6

vec3 rnd33( vec3 p ) {
    vec3 q = vec3( dot(p,vec3(127.1,311.7, 109.2)),
                   dot(p,vec3(269.5,183.3, 432.6)),
                   dot(p,vec3(419.2,371.9, 304.4)) );
    return fract(sin(q)*43758.5453);
}

float rnd13( vec3 p ) {
    return fract(43758.5453*sin(dot(p,vec3(127.1,311.7, 109.2))));
}

float worley3D(vec3 u) {
    float d = 1e4, a;
    float acc = 0., acc_w = 0.;
    vec3 k =  floor(u), f = u-k, p, q = k;
    const int r = 3;
    for(int i = -r; i < r; i++) {
        for(int j = -r; j < r; j++) {
            for(int l = -r; l < r; l++) {
	            vec3 p_i = vec3(i, j, l),
                     p_f = rnd33(k+p_i);
        	    float d = length(p_i - f + p_f);
            	float w = exp(-8. * d) * (1.-step(sqrt(float(r*r)),d));
            	acc += w * rnd13(k+p_i);
            	acc_w += w;
    	} } }
    return acc / acc_w;
}

float fbm3D(vec3 u) {
    float v = 0.;
    for(int i = 0; i < NUM_OCTAVES; i++) {
        v += pow(.5, float(i+1)) * worley3D(u);
        u = 2. * u + 1e3;
    }
    return v;
}

