{
    vec2 uv = fragCoord/iResolution.y;
	vec2 p = uv * 3.;
    vec3 col = vec3(0.);
    float t = iTime/3.;
    float f;
    #if 0
    	col += fbm3D( vec3(p, iTime/3.) );
    #elif 0
    	col += fbm3D( vec3(p, t) + 
            	   vec3( fbm3D(vec3(p, t)+1e3),
                       	 fbm3D(vec3(p, t)-1e3),
