#define S(d,r) smoothstep( r*2./R.y, 0., d)   // antialiased draw. r >= 1.5

// --- line segment with disc ends: seamless distance to segment
float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);   // proj coord on line
    return length(p - b * h);                         // dist to segment
    // You might directly return smoothstep( 3./R.y, 0., dist),
    //     but more efficient to factor all lines.
    // Indeed we can even return dot(,) and take sqrt at the end of polyline:
    // p -= b*h; return dot(p,p);
}

// --- macro version  ( attention: a,b unprotected )
vec2 _d;
#define L(a,b) ( _d=b-a, length( U-a - _d * clamp( dot(U-a, _d) / dot(_d, _d), 0., 1.)))


// --- line segment without disc ends
// ( sometime useful with semi-transparency )
float line0(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    float h = dot(p, b) / dot(b, b),                  // proj coord on line
          c = clamp(h, 0., 1.);
    return h==c ? length(p - b * h) : 1e5;            // dist to strict segment
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U = 1.2* ( U+U - R ) / R.y;

    float d = 1e5, t=iTime-4.;
#define f(x) vec2( sin(6.*x-t) , cos(8.*x-t) ) -.2*sin(x) // func to draw
    vec2 _P, P=f(0.);
    for (float x = 0.; x < 4.; x += .03 )
        d = min(d, line( U, _P = P, P = f(x)) );

 // d = sqrt(d);
    float width = 1.5 + 10.*(.5-.5*cos(t) );
    O = sin(t)>0. ? vec4( S( d, width ) )                  // thick smooth line
                  : vec4( S( d - (width-1.5)/R.y , 1.5) ); // thick dense line
    O.b += .5*sin(100.*d);                           // bg isovalues
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float bx=6.0+3.0*sin(iTime);
    float bw=0.04+0.01*cos(iTime*10.0);
    float col=0.0;
    float bd=1.0/bx;
    float cpx=mod(uv.x-2.0*sin(iTime/2.0),bd);
    float cpy=mod(uv.y-2.0*cos(iTime/2.0),bd);
    float fx=1.0/(bw/2.0);
	if(cpx<(bw/2.0))
    {
        col=1.0-(cpx*fx);
    }
    else if(cpx>(bd-bw/2.0))
    {
        col=1.0-(bd-cpx)*fx;
    }
    if(cpy<(bw/2.0))
    {
        col+=1.0-(cpy*fx);
    }
    else if(cpy>(bd-bw/2.0))
    {
        col+=1.0-(bd-cpy)*fx;
    }
    // Output to screen
    fragColor = vec4(col,col,col,1.0);
}

