const float pi = acos(-1.);
const vec2 c = vec2(1.,0.);

float rand(vec2 a0)
{
    return fract(sin(dot(a0.xy ,vec2(12.9898,78.233)))*43758.5453);
}

float smoothstep_noise(float x)
{
    float r1 = -1.+2.*rand(floor(x)*c.xx), r2 = -1.+2.*rand(ceil(x)*c.xx);
    return mix(r1, r2, smoothstep(.25, .75, fract(x)));
}

float mfsmoothstep_noise(float x, float f0, float f1, float phi)
{
    float sum = 0.;
    float a = 1.;

    for(float f = f0; f<f1; f = f*2.)
    {
        sum = a*smoothstep_noise(f*x) + sum;
        a = a*phi;
    }

    return sum;
}

vec2 rot(vec2 x, float p)
{
    return mat2(cos(p), sin(p), -sin(p), cos(p))*x;
}

mat3 rot(vec3 p)
{
    vec3 cp = cos(p), sp = sin(p);
    mat3 m = mat3(cp.y*cp.x, cp.x*sp.z+cp.z*sp.x*sp.y, sp.x*sp.z-cp.x*cp.z*sp.y, 
           -cp.y*sp.z, cp.x*cp.z-sp.x*sp.y*sp.z, cp.z*sp.x+cp.x*sp.y*sp.z, 
           sp.y, -cp.y*sp.x, cp.x*cp.y);
    return m;
}

float rect(vec2 x, vec2 b)
{
    return length(max(abs(x)-b,0.));
}

vec3 synthcol(float scale, float phase)
{
    vec3 c2 = vec3(207.,30.,102.)/255.,
        c3 = vec3(245., 194., 87.)/255.;
    mat3 r1 = rot((5.e-1*phase)*vec3(1.1,1.3,1.5));
    return 
        (
            1.1*mix
            (
                -(cross(c2, r1*c2)),
                -(r1*c2), 
                scale
            )
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy-.5-.33*c.xy;
    uv = rot(uv, .25*iTime);

    vec4 sdf = vec4(0., c.yyy);

    float N = 128.,
        r_inner = .2;

    for(int i=0; i<12; ++i)
    {
        uv = rot(uv, 1.1);
        vec2 p = vec2(length(uv), atan(uv.y/uv.x)-float(i)*.1*iTime),
        q = vec2(p.x-.05*float(i), mod(p.y, 2.*pi/N)-pi/N),
        q0 = vec2(r_inner, q.y);

    	float index = (p-q).y;

        r_inner = .2+float(i)*.005+ .0005*12.;

        float dr = .1*mfsmoothstep_noise(index-iTime-4.*float(i), 1., 100., .45) + .05*rand(index*c.xx+.2*c.yx),
            len = abs(.005*float(i)+dr),
            width = abs(.015+.005*rand(index*c.xx+.4));

        vec4 sda = vec4(rect(q-r_inner*c.xy, len*c.xy+width*c.yx), synthcol(((q.x-r_inner)/.05+(q.y/2./pi)), iTime+sin(iTime+1.*float(i))));
        sdf = mix(sdf, sda, step(sda.x, sdf.x));
    }

    vec3 col = sdf.gba*step(sdf.x, 0.);

    fragColor = vec4(col,1.0);
}

const float fPi = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 pMid = vec2(-1.0, -2.0) + uv * vec2(6.0, 4.0);

    float fTime = iTime + 25.0;

	vec2 pPixel = pMid * 100.0;
	vec2 pPixelRaw = pPixel;

	pPixel.y *= 1.0;
	vec2 dotPos;
	float fNearestDot = 100.0;

    float animCycle = fract(fTime * 0.333);
    float animCycles = floor(fTime * 0.333);
    animCycles -= floor(animCycles / 21.0) * 21.0;
    animCycles -= 5.0;

	float moveStep01 = 0.5 * (1.0 + cos(fPi + fPi * smoothstep(0.3, 1.0, animCycle)));
	float moveStep = 0.85 * max(0.0, sin(fPi * moveStep01));
    pPixel.x -= moveStep01 * 39.0 + animCycles * 39.0;

	// Standard-Bend + idle-Animation + extra value while walking (=more dynamic walking animation)
	float fBreath = 0.25 + 0.065 * sin(fTime * 1.0 + 2.5 * sign(moveStep01));

	float fDot01 = 0.0;

	float fHair = 0.0;
	float fAllHairs = 0.0;
	float sinStep = 0.0;

	float hairBend = 0.0;

	float rotateAlpha = 0.6;
	float weightedAlpha = 0.0;

	vec2 dotPixel;
	vec2 v2Step;


	for (int i = 0; i < 8; i++)
	{

		fDot01 = float(i) / 7.0;


		dotPos = vec2(-80.0 + float(i) * 20.0, 0.0);


		// MoveStep Main Curve
		//sinStep = 60.0 * (fBreath + moveStep) * cos(-fPi * 0.5 + (i - 0.24) * 0.45) 
		//		 + 9.0 * (fBreath * 1.5 + moveStep) * cos(i * 0.5 + moveStep * 1.3);
		sinStep = 60.0 * (fBreath + moveStep) * cos(-fPi * 0.5 + (fDot01 - 0.03) * fPi) 
				 + 9.0 * (fBreath * 1.5 + moveStep) * cos(fDot01 * 3.5 + moveStep * 1.3);
		v2Step = vec2(0.0, sinStep);
		// MoveStep delayed front-up
		v2Step += vec2(7.0, 14.0) * sin(fPi * smoothstep(0.4, 1.0, moveStep01)) * sin(max(0.0, 0.5 * fPi - (1.0 - fDot01) * 5.0));
		v2Step += vec2(6.0, 15.0) * sin(fPi * smoothstep(0.4, 1.0, 1.0 - moveStep01)) * sin(max(0.0, 0.5 * fPi - fDot01 * 4.0));
		// MoveStep, Contraction
		dotPos += v2Step;
		dotPos.x *= 1.0 - 0.3 * moveStep;

		dotPixel = pPixel - dotPos;



		// Haare
		hairBend = moveStep * 1.30 * (-fDot01 + 0.5);
		fHair = 1.0 * (max(0.0, -abs(dotPixel.x + hairBend * dotPixel.y) + 5.0) / 4.5) *
				(max(0.0, -abs(dotPixel.y - 16.0) + 13.0) / 10.0);
				//(clamp((50.0 - max(-10.0, dotPixel.y)) * 0.05, 0.0, 1.0) ) * sign(max(0.0, dotPixel.y));

		fAllHairs = max(fAllHairs, min(1.0, fHair));

		// Leg
		dotPixel += vec2(0.0, max(0.0, -abs(dotPixel.x) + 6.5) * ( clamp(-dotPixel.y - 6.0, 1.00, 4.0)) * 0.250);

		fNearestDot = min(fNearestDot, length(dotPixel));
	}

	float fSkin = smoothstep(-25.0, -7.0, -fNearestDot);
	float fInnen = smoothstep(-13.0, -2.0, -fNearestDot);

	vec3 colSkin = vec3(0.25, 0.6, 0.1);
	vec3 colInnen = vec3(1.0);
	vec3 colHair = vec3(0.6, 0.2, 0.05);
	vec3 colFin = mix(colSkin - vec3(0.5) * smoothstep(-4.5, 0.20, -fNearestDot), colHair, fAllHairs) + colInnen * 0.3 * fInnen;

	float fValue = max(fAllHairs, fSkin);

    vec3 colLine = vec3(0.5) * smoothstep(-0.05, 0.0, -abs(pMid.y + 0.25));
	fragColor = vec4(colLine + colFin * fValue, 1.0);

}

#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))
#define pi 3.14159265359

float rr(vec2 uv, float halfExtent, float borderRadius, float anim)
{
    halfExtent += sin(iTime * 6.) * .05 * anim;
    return smoothstep(.02, .01, 
                      length(max(abs(uv) - halfExtent, 0.)) - borderRadius);
}

float outerNotch(vec2 uv, float angle) 
{
    const float notchSize = .045;
    const float skew = 1.3;
    uv *= rot(angle);
    uv.y -= .55 + sin(iTime * 12.) * .095;
    uv.x /= ((uv.y + notchSize) / (2. * notchSize)) * (skew - 1.) + 1.;
    uv.y /= 2.75;
    return smoothstep(.018, .01, length(max(abs(uv) - notchSize, 0.)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float inner = rr(uv, .18, .02, 1.) * (1. - rr(uv, .12, .02, 1.));
    float dawt = rr(uv, .03, .02, 0.);
    float notch = outerNotch(uv, 0.) + outerNotch(uv, pi / 2.)
        + outerNotch(uv, pi) + outerNotch(uv, 1.5 * pi);
    fragColor.rgb = (notch + dawt + inner + rr(uv, .35, .05, 1.) * (1. - rr(uv, .275, .05, 1.))
        * step(.15, min(abs(uv.x), abs(uv.y)))) * vec3(0., 1., 0.);
}

