// Distance to Bezier
// inspired by [iq:https://www.shadertoy.com/view/ldj3Wh]
// calculate distance to 2D bezier curve on xy but without forgeting the z component of p
// total distance is corrected using pytagore just before return
vec2 B(vec2 m, vec2 n, vec2 o, vec3 p) {
	vec2 q = p.xy;
	m-= q; n-= q; o-= q;
	float x = U(m, o), y = 2. * U(n, m), z = 2. * U(o, n);
	vec2 i = o - m, j = o - n, k = n - m, 
		 s = 2. * (x * i + y * j + z * k), 
		 r = m + (y * z - x * x) * vec2(s.y, -s.x) / dot(s, s);
	float t = clamp((U(r, i) + 2. * U(k, r)) / (x + x + y + z), 0.,1.); // parametric position on curve
	r = m + t * (k + k + t * (j - k)); // distance on 2D xy space
	return vec2(sqrt(dot(r, r) + p.z * p.z), t); // distance on 3D space
}



float smin(float a, float b, float k){
    float h = clamp(.5+.5*(b-a)/k, 0., 1.);
    return mix(b,a,h)-k*h*(1.-h);
}


//#define METHOD_2

void basis(in vec3 n, out vec3 xp, out vec3 yp)
{
#if defined(METHOD_2)
  // method 2a variant
  float sz = n.z >= 0.0 ? 1.0 : -1.0;
  float a  =  n.y/(1.0+abs(n.z));
  float b  =  n.y*a;
  float c  = -n.x*a;

  xp = vec3(n.z+sz*b, sz*c, -n.x);
  yp = vec3(c, 1.0-b, -sz*n.y);
#else

  // method 1: ignoring n approaching -z
#if 1
  float a =  n.y/(1.0+n.z);
  float b =  n.y*a;
  float c = -n.x*a;

  xp = vec3(n.z+b, c, -n.x);
  yp = vec3(c, 1.0-b, -n.y);

#else

  // nimitz's impl of frisvad
  float a = 1./(1. + n.z);
  float b = -n.x*n.y*a;
  xp = vec3(1. - n.x*n.x*a, b, -n.x);
  yp = vec3(b, 1. - n.y*n.y*a , -n.y);
#endif
#endif
}

vec3 computeLambert(vec3 p, vec3 n, vec3 l, vec3 e, vec3 h, vec3 surfaceColor, vec3 lightColor){

    vec3 lightVector = l;
    vec3 eyeVector = e;
    vec3 halfVector = h;

    float nDotLV = max(0.0, dot(lightVector, n));
    float nDotHV = max(0.0, dot(halfVector, n));

    float attenuation = 1.0;
    float shininess = 10.0;
    float pf;
    if(nDotLV == 0.0)
        pf = 0.0;
 	else
        pf = pow(nDotHV, shininess);

    vec3 ambient = vec3(0.1) * attenuation;
    vec3 diffuse = vec3(1.0,1.0,1.0) * nDotLV * attenuation;
    vec3 specular = vec3(1.0,1.0,1.0) * pf * attenuation*1.0;
    return min(((ambient + diffuse)*surfaceColor + specular * vec3(1.0)) * lightColor, vec3(1.0));
}

vec3 computeBRDF(vec3 n, vec3 l, vec3 h, vec3 r, vec3 t, vec3 b, vec2 p, vec2 a, vec3 s, vec3 color){

    float e1, e2, E, cosThetaI, cosThetaR, brdf, intensity;

    e1 = dot(h,t)/a.x;
    e2 = dot(h,b)/a.y;
    E = -2.0*((e1*e1+e2*e2)/(1.0+dot(h,n)));

    cosThetaI = dot(n,l);
    cosThetaR = dot(n,r);

    brdf = p.x * ONE_OVER_PI + p.y * (1.0 / sqrt(cosThetaI * cosThetaR)) * ( 1.0 / (4.0 * PI * a.x * a.y)) * exp(E);

    intensity = s[0] * p.x * ONE_OVER_PI + s[1] * p.y * cosThetaI * brdf + s[2] * dot(h,n) * p.y;
    return intensity * color;
}   

float cube(vec3 p){
    float s = 1.0;
    vec3 d = abs(p) - vec3(s);
    return min(max(d.x, max(d.y,d.z)), 0.0)
        + length(max(d,0.0));
}

float sphere(vec3 p){
