void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 lEyeShapes = vec4(0);
    vec4 rEyeShapes = vec4(0);
    float lEyeTween = 0.;
    float rEyeTween = 0.;

    // This doesn't *really* focus the eyes, but it makes them slide around together
    // which I guess is close enough
    focusPoint = vec3(sin(iTime * 1.4) * .08, cos(iTime * 0.6) * .07, 0.);
    pupilDilation = abs(sin(iTime * 0.2 + 1.4)) * 0.5 + 0.7; 

    // Blink every 4 seconds
    if (int(floor(iTime)) % 4 == 0){
        rEyeTween = fract(iTime);
        lEyeTween = fract(iTime);
        if (rEyeTween <= blinkDuration){
            blinkStageDuration *= blinkDuration;
            while (rEyeTween > blinkStageDuration){
                rEyeTween -= blinkStageDuration;
            }
            rEyeTween /= blinkStageDuration;
            if (fract(iTime) <= blinkStageDuration)
            {
                lEyeShapes.x = 0.;
                lEyeShapes.y = 1.;
                rEyeShapes.x = 0.;
                rEyeShapes.y = 1.;
            }
            else if (fract(iTime) <= 2. * blinkStageDuration)
            {
                lEyeShapes.x = 1.;
                lEyeShapes.y = 2.;
                rEyeShapes.x = 1.;
                rEyeShapes.y = 2.;
            }
            else if (fract(iTime) <= 3. * blinkStageDuration)
            {
                lEyeShapes.x = 2.;
                lEyeShapes.y = 3.;
                rEyeShapes.x = 2.;
                rEyeShapes.y = 3.;
            }
            else if (fract(iTime) <= 4. * blinkStageDuration)
            {
                lEyeShapes.x = 3.;
                lEyeShapes.y = 3.;
                rEyeShapes.x = 3.;
                rEyeShapes.y = 3.;
            }
            else if (fract(iTime) <= 5. * blinkStageDuration)
            {
                lEyeShapes.x = 3.;
                lEyeShapes.y = 2.;
                rEyeShapes.x = 3.;
                rEyeShapes.y = 2.;
            }
            else if (fract(iTime) <= 6. * blinkStageDuration)
            {
                lEyeShapes.x = 2.;
                lEyeShapes.y = 1.;
                rEyeShapes.x = 2.;
                rEyeShapes.y = 1.;
            }
            else {
                lEyeShapes.x = 1.;
                lEyeShapes.y = 0.;
                rEyeShapes.x = 1.;
                rEyeShapes.y = 0.;
            }
        }
    }

    //lEyeShapes.xy = vec2(3., 3.);
    //rEyeShapes.xy = vec2(3., 3.);
    //rEyeTween = 0.;
    //lEyeTween = 0.;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;

    // Initial colour
    vec3 pixelColour = skinColour;

    // Add in the full iris shape in flat white
    if (paintScleras(uv))
    {
    	pixelColour = vec3(1.0);   
    }

    // Add in full pupil
    // TODO: bring in the gradient from the original game
    if (paintPupil(uv, lPupilCenter, lEyeSize) ||
        paintPupil(uv, rPupilCenter, rEyeSize)){
    	pixelColour = vec3(0.0);
    }

    // Mask the iris and pupil to create the proper shape of the eye
    if ((uv.x < 0.5 && restrictPupil(uv, int(lEyeShapes.x), int(lEyeShapes.y), lEyeTween)) ||
        (uv.x > 0.5 && restrictPupil(uv, int(rEyeShapes.x), int(rEyeShapes.y), rEyeTween)))
    {
     	pixelColour = skinColour;   
    }

    // Paint the eyelids on
    if (paintEyelids(uv, false, int(lEyeShapes.x), int(lEyeShapes.y), lEyeTween) ||
        paintEyelids(uv, true,  int(rEyeShapes.x), int(rEyeShapes.y), rEyeTween)){
        pixelColour = vec3(0.0);
    }

    // TODO: Maybe actually add in extra shapes so we can tween for real sometime
    if (paintEyebrow(uv, false, int(lEyeShapes.z), int(lEyeShapes.w), 0.) ||
        paintEyebrow(uv, true, int(lEyeShapes.z), int(lEyeShapes.w), 0.)){
		pixelColour = vec3(0.0);
    }


    fragColor = vec4(pixelColour, 1.0);
}

float hypot(vec2 p) {
    return sqrt(p.x*p.x+p.y*p.y);
}

float pi = 3.1415926;

float cross2(vec2 u, vec2 v) {
    return u.x*v.y-u.y*v.x;
}

vec2 path(float t) {
    float theta = 2.0*pi*(t-0.5);
    float alpha = 0.2*iTime;
    mat2 m = mat2(cos(alpha), -sin(alpha), sin(alpha), cos(alpha));

    vec2 xy = vec2(0.75*cos(theta), sin(theta));
    //vec2 xy = vec2(theta, 1.5*theta*theta-1.0);
    //return vec2(-theta*theta, theta);
    //return vec2(-cos(theta), sin(theta));
    //vec2 xy = vec2(-cos(theta)*cos(theta)*cos(theta), sin(theta)*sin(theta)*sin(theta));

    return 1.0*m*xy;
}

// float l(vec2 xy, float theta) {
//     return -path(theta).x+hypot(xy-path(theta));
// }

float gain = 0.70;
//float wavelength = 0.03;

float integrate(vec2 xy) {
    float tx = 0.0;
    float ty = 0.0;
    int n = 2000;
    vec2 last_p = path(0.0);

    float wavelength = 0.02+0.02*iMouse.x/iResolution.x;

    for (int i = 0; i < 2000; ++i) {
        float t = float(i)/float(n);
        vec2 p = path(t);
        float d = hypot(xy-p);

        float dt = p.y-last_p.y;

        if (dt < 0.0 && cross2(p-last_p, xy-p) > 0.0) {
            float path_length = d-p.x;
            float s = 2.0*pi/wavelength*path_length;
            tx += cos(s)*dt/d;
            ty += sin(s)*dt/d;
        }

//        path_length = d+p.x;
//        s = -2.0*pi/wavelength*path_length;
//        tx += cos(s)*dt/d;
//        ty += sin(s)*dt/d;

        last_p = p;
    }
    return hypot(vec2(tx, ty));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = vec2(0.5*iResolution.x, 0.5*iResolution.y);
    vec2 uv = fragCoord.xy-center;
    uv = 2.0*uv/iResolution.y;
    float c = gain*integrate(uv);
    fragColor = vec4(1.2*sqrt(c), c, 0.5*c, 1.0);
}

#define PI 3.14
#define FOV PI * 0.5
#define COSTIME cos(iTime * 0.1)
#define SINTIME sin(iTime * 0.1)

#define SHAPE_DIVISION 4
#define SHAPE_SPACE 2.1

#define RAY_STEPS 64
#define RAY_MIN_DIST 0.001
#define RAY_MAX_DIST 1024.0

#define AO_SAMPLES 32

#define NORMAL_SAMPLE_LENGTH 0.005

#define LIGHT_AMBIENT vec3(0.5, 0.5, 0.5);
#define LIGHT_INTENSITY 0.5
#define LIGHT_RAY_STEPS 64
#define LIGHT_RAY_DIST 0.0001

#define SPECULAR_POWER 4.0
#define SPECULAR_INTENSITY 1.0

float sat(float a) {

    return min(1.0, max(0.0, a));
}
float opU(float a, float b) { return min(a, b); }
float opS(float a, float b) { return max(a, -b); }
vec3 opT(vec3 p, vec3 o) { return p - o; }
vec3 opR(vec3 p, vec3 c) { return mod(p, c) - 0.5 * c; }
float box(vec3 p, vec3 b) { vec3 d = abs(p) - b; return max(d.x, max(d.y, d.z)); }
float ibox(vec3 p, vec3 b) { vec3 d = abs(p) - b; return -max(d.x, max(d.y, d.z)); }
float map(vec3 p) {

    float d = RAY_MAX_DIST;
    vec3 rp = p;
    rp = opR(rp, vec3(SHAPE_SPACE, SHAPE_SPACE, SHAPE_SPACE));
    float fd = box(rp, vec3(1.0, 1.0, 1.0));
    float sd = RAY_MAX_DIST;
    for (int i = 0; i < SHAPE_DIVISION; ++i) {

        vec3 tp = rp;
        float s = pow(3.0, float(i ));
        tp = opT(tp, vec3(2.0, 2.0, 2.0) / s);
        tp = opR(tp, vec3(2.0, 2.0, 2.0) / s);
        sd = opU(sd, box(tp, vec3(0.66, 0.66, 0.66) / s));
    }
    fd = opS(fd, sd);
    d = opU(d, fd);
    return d;
}

float findDist(vec3 ro, vec3 rd, int max_steps, float min_dist, float max_dist) {

    float len = min_dist;
    for (int i = 0; i < max_steps && len < max_dist; ++i) {

        float dist = map(ro + rd * len);
        if (dist <= min_dist) {

            return len;
        }
        len += dist;
    }
    return RAY_MAX_DIST;

}

vec3 findNormal(vec3 p) {

    float u = map(p + vec3(0.0, 0.0, NORMAL_SAMPLE_LENGTH))
        , d = map(p - vec3(0.0, 0.0, NORMAL_SAMPLE_LENGTH))
        , r = map(p + vec3(NORMAL_SAMPLE_LENGTH, 0.0, 0.0))
        , l = map(p - vec3(NORMAL_SAMPLE_LENGTH, 0.0, 0.0))
        , f = map(p + vec3(0.0, NORMAL_SAMPLE_LENGTH, 0.0))
        , b = map(p - vec3(0.0, NORMAL_SAMPLE_LENGTH, 0.0));
    return normalize(vec3(r - l, f - b, u - d));
}

float findAO(vec3 p, vec3 n) {

    float ao = 0.0;
    for (int i = 0; i < AO_SAMPLES; ++i) {

        float w = pow(float(i + 1), 2.0);
        float sd = 0.001 * w;
        ao += max(0.0, map(p + n * sd) / sd) / float(AO_SAMPLES);
    }
    return max(min(ao, 1.0), 0.0);
}

vec3 phong(vec3 p, vec3 e, vec3 n, vec3 l, vec3 c, float i) {

    vec3 ptol = l - p;
    vec3 etop = p - e;
    float diff = dot(normalize(ptol), n);
    float spec = pow(sat(dot(normalize(etop), normalize(ptol))), SPECULAR_POWER) * SPECULAR_INTENSITY;
    float dist = 1.0 / (pow(length(ptol) / i, 2.0));
    if (findDist(l, normalize(-ptol), LIGHT_RAY_STEPS, LIGHT_RAY_DIST, length(ptol)) < RAY_MAX_DIST) {

        return vec3(0.0, 0.0, 0.0);
    }
    return (c * (diff + spec) * dist) / i;
}

void mainImage( out vec4 fragColor, in vec2 fc ) {

    vec2 uv = (vec2(fc.x, fc.y) - iResolution.xy * 0.5) / iResolution.y;
    vec3 e = vec3(0.0, iTime * 0.25, 0.0);
    vec3 l = e + vec3(0.0, 1.0, 0.0);

    vec3 f = normalize(l - e);
    vec3 u = vec3(COSTIME, 0.0, SINTIME);
    vec3 r = cross(u, f);

    vec3 rd = normalize(f + r * uv.x * FOV + u * uv.y * FOV);
    vec3 ro = e + rd * RAY_MIN_DIST;

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    float d = findDist(ro, rd, RAY_STEPS, RAY_MIN_DIST, RAY_MAX_DIST);
    if (d < RAY_MAX_DIST) {

        vec3 p = ro + rd * d;
        vec3 n = findNormal(p);
        float ao = findAO(p, n);

        vec3 l = phong(p, e, n, e + vec3(0.0, 1.0, 0.0), vec3(1.0, 1.0, 1.0), LIGHT_INTENSITY) + LIGHT_AMBIENT;
        l *= ao;
        vec3 c = normalize(vec3(mod(floor(p / SHAPE_SPACE), vec3(1.0, 1.0, 1.0) * SHAPE_SPACE)) * 0.5 + 0.5);
        fragColor = vec4(c * l, 1.0);
    }
}

