vec2 Rot2D (vec2 q, float a);

vec3 ltDir;
float tCur, dstFar;
const float pi = 3.14159;

float ObjDf (vec3 p)
{
  float cvOrd, a;
  cvOrd = 7.;
  a = atan (p.z, p.x) / (2. * pi);
  p.xz = Rot2D (vec2 (length (p.xz) - 2., mod (p.y + 2. * a + 2., 2.) - 1.),
     2. * pi * (cvOrd - 1.) * a);
  return 0.4 * (length (Rot2D (p.xz, - (floor ((0.5 * pi - atan (p.x, p.z)) + pi / cvOrd))) -
     vec2 (0.6, 0.)) - 0.15);
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 200; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col = vec3 (0.7, 0.7, 0.8) * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       0.4 * vec3 (1., 1., 0.7) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  } else {
    col = vec3 (0.6, 0.6, 1.) * (0.2 + 0.2 * (rd.y + 1.) * (rd.y + 1.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az -= 0.1 * pi * tCur;
    el -= 0.35 * pi * sin (0.07 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, 0.4 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -20.);
  rd = vuMat * normalize (vec3 (uv, 5. + 2. * sin (0.05 * pi * tCur)));
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const int MAX_ITERATION = 256;

struct Ray
{
    vec3 pos;
    vec3 dir;
};

struct Camera
{
    vec3 pos;
    vec3 forward;
    vec3 right;
    vec3 up;
    float fovScalar;
};

// to setup camera
void setCamera(vec3 pos, vec3 forward, vec3 upGuide, float fov, out Camera cam)
{
    cam.pos = pos;
    cam.forward = normalize(forward);
    cam.right = cross(-cam.forward, normalize(upGuide));
    cam.up = cross(cam.right, -cam.forward);
    cam.fovScalar = tan(radians(fov/2.0));
}

// to setup ray
Ray castRay(vec2 uv, in Camera cam)
{
    uv *= cam.fovScalar;
    return Ray(
        cam.pos,
        normalize(cam.forward + uv.x * cam.right + uv.y * cam.up)
    );
}

// rand generator
float rand(float x)
{
    vec2 xx = vec2(x);
    return fract(sin(dot(xx, vec2(12.9898,78.233))) * 43758.5453);
}

// test intersection with randomly generated voxel
bool randIntersected(vec3 p)
{
    vec3 upperBound = vec3(4,4,4);
    vec3 lowerBound = vec3(-4,0,-4);

    if(all(lessThanEqual(p, upperBound))
       && all(greaterThanEqual(p, lowerBound)))
    {
        float x = ceil(iTime/4.+1.) * dot(p, p);

        if(rand(x) > 0.75)
           return true;
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
	uv.y *= iResolution.y/iResolution.x;

    // sun pos
    vec3 sunPos = vec3(cos(iTime)*7.,6,-sin(iTime)*7.);

    // camera
    Camera cam;
    vec3 pos = vec3(cos(iTime/4.)*20., sin(iTime)*10.+15., -sin(iTime/4.)*20.);
    setCamera(pos, vec3(0, 1, 0)-pos, vec3(0, 1, 0), 70., cam);

    // initialize ray pos
    vec3 mapPos = floor(cam.pos);

    // ray and ray displacement setup
    Ray ray = castRay(uv, cam);
    vec3 rayStep = sign(ray.dir);
    vec3 deltaDist = abs(length(ray.dir)/ray.dir);
    vec3 sideDist = sign(ray.dir)*(mapPos - ray.pos + sign(ray.dir)*0.5+0.5)*deltaDist;

    bvec3 mask;
    vec3 col = vec3(0);

    // ray incrementation loop
    for(int i = 0; i < MAX_ITERATION; ++i)
    {
        // check for intersection /w sun
        if(all(equal(mapPos, ceil(sunPos))))
        {
            col = vec3(1);
            break;
        }
        // check for intersection /w noise generated voxel
        if(randIntersected(mapPos))
        {
            vec3 blockCol = vec3(
                rand(mapPos.x+mapPos.y),
                rand(mapPos.x-mapPos.y),
                rand(mapPos.x*mapPos.y));

            vec3 normal = -normalize(rayStep*vec3(mask));
            vec3 toSun = normalize(sunPos - mapPos);
            col = blockCol*(vec3(max(dot(vec3(normal), toSun), 0.0)) + 0.35);
            break;
        }
        // check for intersection /w floor
        if(mapPos.y < 0.)
        {
            vec3 normal = -normalize(rayStep*vec3(mask));
            vec3 toSun = normalize(sunPos - mapPos);
            col = vec3(max(dot(vec3(normal), toSun), 0.0));
            break;
        }

        // increment ray in x, y, or z direction

        mask = lessThanEqual(sideDist.xyz, min(sideDist.yxx, sideDist.zzy));

        mapPos += vec3(mask) * rayStep;
    	sideDist += vec3(mask) * deltaDist;
    }

    // final color
    fragColor = vec4(col,1.0);
}

// @lsdlive

mat2 r2d(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

void amod(inout vec2 p, float m) {
    float a = mod(atan(p.x, p.y) - m*.5, m*.5) - m*.5;
	p = vec2(cos(a), sin(a)) * length(p);
}

void mo(inout vec2 p, vec2 d) {
    p.x = abs(p.x) - d.x;
    p.y = abs(p.y) - d.y;
    if(p.y>p.x) p=p.yx;
}

float ncos(float a) {
    return .5 + .5 * cos(a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy - .5;
    p.x *= iResolution.x / iResolution.y;

    p *= 5.;

    float circ = length(p);

    p*=r2d(3.14/2.);
    //p *= r2d(iTime*.2);
    mo(p, vec2(.2, 1.));//.3
    amod(p, 6.28/(1. + ncos(iTime)*3.));
    mo(p, vec2(.7, .1));

    p.x = abs(p.x) - .3 - ncos(iTime*4.)*.2;
    vec3 c = vec3(1) * smoothstep(p.x, p.x+.01, p.y);

    /*
    c+=.8;
    c*=length(p);
    c = step(.4, c);
	*/    

    c+=.12;
    c-=smoothstep(2., 2.02, circ);

    float stp=12.;
	if(length(fract((floor(p*stp)/stp))) > .85){
        c=vec3(.5, 0, 0);
    }


    fragColor = vec4(c, 1.);
}

float glow = 0.0;
float time, timeOff;
const float EPSILON = 0.000001;

mat3 makeRotationMatrix(vec3 a)
{
    return mat3(
    	cos(a.x) * cos(a.z) - sin(a.x) * cos(a.y) * sin(a.z),
        -cos(a.x) * sin(a.z) - sin(a.x) * cos(a.y) * cos(a.z),
        sin(a.x) * sin(a.y),
        sin(a.x) * cos(a.z) + cos(a.x) * cos(a.y) * sin(a.z),
        -sin(a.x) * sin(a.z) + cos(a.x) * cos(a.y) * cos(a.z),
        -cos(a.x) * sin(a.y),
        sin(a.y) * sin(a.z),
        sin(a.y) * cos(a.z),
        cos(a.y)
    );
}

#define sph(p, r) (length(p) - r)
#define cube(p, b) length(max(abs(p) - vec3(b), 0.))

// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float capsules(vec3 q) {
    float capsulesT = sin(iTime);
    vec3 a = vec3(0.05);
    vec3 b = vec3(0.2);
    float r = 0.05 * abs(capsulesT);

    float c1 = sdCapsule(q, a, b, r);
    float c2 = sdCapsule(q, -a, -b, r);
    float c3 = sdCapsule(vec3(-q.x, -q.y, q.z), a, b, r);
    float c4 = sdCapsule(vec3(-q.x, -q.y, q.z), -a, -b, r);

    float c5 = sdCapsule(vec3(-q.x, q.y, q.z), a, b, r);
    float c6 = sdCapsule(vec3(-q.x, q.y, q.z), -a, -b, r);
    float c7 = sdCapsule(vec3(q.x, -q.y, q.z), a, b, r);
    float c8 = sdCapsule(vec3(q.x, -q.y, q.z), -a, -b, r);

    return min(min(min(min(min(min(min(c1, c2), c3), c4), c5), c6), c7), c8);
}

float sceneSDF(vec3 p) {
    float rotationDir = p.x > 0.0 ? 1.0 : -1.0;
    mat3 rot = makeRotationMatrix(vec3(0.0, 0.0, rotationDir * iTime * 0.6));
    vec3 q = rot * p.xyz;
	float d = max(sph(p, mix(.125, .15, time)), cube(q, .092)); 

    d = max(-capsules(q), d);

    d = max(-sph(p.xyz, mix(.1, .125, time)), d);

	// https://www.shadertoy.com/view/4t2yW1
	glow += 0.0001 / (.01 + d * d);
	return d;
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 computeSingleLight(vec3 n, vec3 p, vec3 l)
{
    float diffTerm = max(dot(normalize(n), normalize(l)), 0.0) * 0.6;
    vec3 reflectVec = normalize(-reflect(l, n));
    float specTerm = pow(max(dot(reflectVec, normalize(p)), 0.0), 10.0) * 0.8;

    return vec3(1.0 + timeOff * 0.3, 0.4, 0.4) * diffTerm +
       vec3(0.5 + timeOff * 0.5, 1.0, 1.0) * specTerm;
}

vec3 computeLighting(vec3 n, vec3 p)
{    
    return computeSingleLight(n, p, vec3(0.2, 0.4, 0.6))
        + computeSingleLight(n, p, vec3(-0.4, -0.3, 0.7));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;
	vec3 ro = vec3(0., 0., -iTime), p, repeatedP;

    float rt = sin(iTime * 0.6);
    mat2 rot2 = mat2(cos(rt), sin(rt), -sin(rt), cos(rt));
	vec3 rd = normalize(vec3(rot2 * uv, -1));

    time = sin(iTime * 2.);
    timeOff = sin(iTime * 2.0 + 3.14);
    time = time*time;
    timeOff = timeOff*timeOff;

	p = ro;

    float repeat = 0.28;

	float t = 0.;
    float hit = 0.;
    vec3 normal = vec3(0.0);

	for (float i = 0.; i < 3.0; i += .01) {
		p = ro + rd * t;
        repeatedP = mod(p, repeat) - repeat * 0.5;

		float d = sceneSDF(repeatedP);
        if (d < .001) {
            hit = 1.0;
            normal = estimateNormal(repeatedP);
            break;
        }

		t += d * 0.3;
        if (t > 70.) {
            hit = 0.0;
            break;
        }
	}
    vec3 l = computeLighting(normal, p);
    l = mix(vec3(0.0), l, hit);
    vec3 glowC = glow * vec3(timeOff * 0.5 + 0.65, 1.0, 1.0) * 0.5;
	vec3 c = mix(l, glowC, max(glow - 0.2, 0.0));
	fragColor = vec4(c, 1.);
}

#define AA 1
const float tau =atan(1.)*8.;

highp float hash(float x) {
    return fract(sin(x)*43758.5453);
}
highp float rand(vec2 co) {
    return fract(sin(mod(dot(co.xy ,vec2(12.9898,78.233)),3.14))*43758.5453);
}
float mark(int n,float ang2,vec2 uv,float mn,float mx){
	float aa=float(AA)/iResolution.y;
    ang2-=tau/float(n)/2.;
	return smoothstep(mn,mx,distance(uv,vec2(0)))*smoothstep(aa*2.,0.,abs(fract(ang2/tau*float(n))-0.5)/float(n)*tau*distance(uv,vec2(0)));
}
float circle(float dist,vec2 uv){
	float aa=float(AA)/iResolution.y;
	return (smoothstep(dist-aa*2.,dist-aa,distance(uv,vec2(0)))-smoothstep(dist,dist+aa,distance(uv,vec2(0))))/10.;
}
float point(vec2 coord,vec2 uv,float ang){
	float aa=float(AA)/iResolution.y;
	return smoothstep(0.004+aa,0.002,distance(uv,coord))*smoothstep(tau*0.7,0.,ang)/2.;
}

void mainImage( out vec4 o, in vec2 fragCoord ){
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;
	float aa=float(AA)/iResolution.y;

    //uv.x+=(rand(uv*iTime)-0.5)/100.*smoothstep(0.7,1.,hash(floor(iTime)));

    float dist=distance(uv,vec2(0));
	float sdist=smoothstep(0.5+aa,0.5,dist);

    float ang=iTime*tau/5.;
	float ang2=atan(uv.x,uv.y);
    ang=mod(ang2+ang,tau);

    float col=smoothstep(1.,0.,ang);
    col/=2.;
    col+=smoothstep(tau-aa/distance(uv,vec2(0)),tau,ang)/2.;
	col+=sdist/10.;
    col+=smoothstep(aa,0.,abs(uv.x))/4.;
    col+=smoothstep(aa,0.,abs(uv.y))/4.;

    col+=mark(9*4,ang2,uv,0.45,0.5)/2.;
    col+=mark(9*4*5,ang2,uv,0.475,0.5)/5.;
    col+=circle(0.1,uv);
    col+=circle(0.2,uv);
    col+=circle(0.3,uv);
    col+=circle(0.4,uv);
   	col+=point(vec2(0.3,0.1),uv,ang);
    col+=point(vec2(-0.2,-0.3),uv,ang);
    col+=point(vec2(0.2,-0.1),uv,ang);

    o = mix(vec4(0,col,0,0),vec4(0.055,0.089,0.0,0)/1.3,1.-sdist);
	o=o;

    float l=cos(fragCoord.y);
    l*=l;
    l/=3.;
    l+=0.6+rand(uv*iTime);

    o*=l;
}

