//JW
float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

//JW
float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

//JW
float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

// iq
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

//iq
float smin2( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}


// iq
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

//iq
float sdCone( vec3 p, vec2 c )
{
    // c must be normalized
    float q = length(p.xy);
    return dot(c,vec2(q,p.z));
}

// iq
float cylinderSDF( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

//http://glslsandbox.com/e#40928.0
float saturate( float x )
{
    return clamp( x, 0.0, 1.0 );
}


// Based off: //http://glslsandbox.com/e#40928.0
// Creates the main body of tentacle
float noodle (vec3 p)
{

    float scale = 1.0 - 2.5 * saturate( abs( p.y ) * 0.01 );  

    // sdCapsule, second argument defines lower end point, third is upper end point
    // p is the point being evaluated, last argument is thickness
    float cap = sdCapsule(p, vec3( 0.0, -6.0, 0.0 ), vec3( 0.0, 4.0, 0.0 ), 0.3 * scale );

    // want more tapering at the end
    float cone = sdCone(rotateX(-PI/2.0) * p + vec3(0.0, 0.0, -1.0), normalize(vec2(1.5, .30)));
    //return cone;
    return smin(cone, cap, -.5);
}

float suctionCup(vec3 p, float scale, int i, int tentacleID)
{

    vec3 cp = rotateX(-PI/2.0) * p;
    float outer = cylinderSDF(cp, vec2(1.0, .30) / scale);
    float inner = cylinderSDF(cp, vec2(.25, .5) / ((.4) * scale));

    float mid = differenceSDF(outer, inner);

    if(mid < EPSILON) {
        float currTent = mod(float(tentacleID), 4.0);
        float currSC = mod(float(i), 2.0);

        if(currTent == 0.0)
        {
   			if(currSC == 0.0 && !colorSet)
    		{ 
        		K_d = PINK;
        		colorSet = true;
    		} else if(currSC != 0.0 && !colorSet)
    		{
        		K_d = DARK_PURPLE;
            	colorSet = true;
    		}
        } else if (currTent == 1.0) {
            if(currSC == 0.0 && !colorSet)
    		{ 
        		K_d = TEAL;
        		colorSet = true;
    		} else if(currSC != 0.0 && !colorSet)
    		{
        		K_d = DARK_TEAL;
            	colorSet = true;
    		}
        } else if (currTent == 2.0) {
            if(currSC == 0.0 && !colorSet)
    		{ 
        		K_d = RED;
        		colorSet = true;
    		} else if(currSC != 0.0 && !colorSet)
    		{
        		K_d = DARK_RED;
            	colorSet = true;
    		}
        } else {
            if(currSC == 0.0 && !colorSet)
    		{ 
        		K_d = ORANGE;
        		colorSet = true;
    		} else if(currSC != 0.0 && !colorSet)
    		{
        		K_d = DARK_ORANGE;
            	colorSet = true;
    		}
        }
    }

    return mid;
}




//http://glslsandbox.com/e#40928.0
// Creates the tentacle by calling noodle and placing suction cups along body of tentacle
float Tentacle( vec3 p , int tentacleID)
{
    p.x -= sin( p.y * 2.0 + iTime * 7.0 ) * 0.21;

    float noodle = noodle(p); // The "main body" of the tentacle
    if(noodle < EPSILON)
    {

        float pt = PolkaDot3D(p, .0, .5);
        if(pt < EPSILON)
        {
            K_d = BLACK;//vec3(pt, pt, pt);
            colorSet = true;
        } else {
       		K_d = NAVY;// vec3(0.0, 0.0, 0.0);// palette(sin(iTime), A, B, C, D);
            colorSet = true;
        }
    } 

    float scale = 150.0;
    p += vec3(0.0, -.70, 0.0);
    vec3 sc_p1 = p - vec3(0.01, 0.0, 0.03);
    vec3 sc_p2 = p - vec3(-0.01, 0.0, 0.03);


    sc_p1 = rotateY(PI/4.0) * sc_p1;
    sc_p2 = rotateY(-PI/4.0) * sc_p2;

    vec3 init_sc_p1 = sc_p1;
    vec3 init_sc_p2 = sc_p2;
    float init_scale = scale;

    float sc1 = suctionCup(sc_p1, scale, 0, tentacleID);
    float sc2 = suctionCup(sc_p2, scale, 0, tentacleID);
    float sc = unionSDF(sc1, sc2);

    // first six suction cups: start small and scale bigger
    // as they are placed from the tip towards the base
    // also, rotate so that they lie "on" the surface of the tentacle
    // alternate colors
    for(int i = 0; i < 6; i++)
    {
        scale /= 1.45;
        sc_p1 += vec3(-0.00005, .05 * float(i),float(i) * -0.0085);
        sc_p2 += vec3(0.00005, .05 * float(i),float(i) * -0.0085);
        sc1 = suctionCup(sc_p1, scale, i, tentacleID);
        sc2 = suctionCup(sc_p2, scale, i, tentacleID);    
        sc = unionSDF(sc, sc1);
        sc = unionSDF(sc, sc2);
     }


    sc_p1 = rotateY(-PI/8.0) * sc_p1;
    sc_p2 = rotateY(PI/8.0) * sc_p2;

    // Once close enough to the base, can be the same size and rotation
    for(int j = 6; j < 15; j++)
    {

        sc_p1 += vec3(0, .25 , -0.0179);
        sc_p2 += vec3(0, .25 , -0.0179);
        sc1 = suctionCup(sc_p1, scale, j, tentacleID);
        sc2 = suctionCup(sc_p2, scale, j, tentacleID);
        sc = unionSDF(sc, sc1);
        sc = unionSDF(sc, sc2);
    }


    return smin(noodle, sc, .01);

}

// modified from iq
float opRep(vec3 p, int i)
{
    vec3 q = vec3(p.x + cos(iTime), p.y + sin(iTime), mod(p.z,9.0));

    float scale = 1.0 - 2.5 * saturate( abs( q.y ) * 0.01 );  
   	float boundingCapsule = sdCapsule(q, vec3( 0.0, -6.0, 0.0 ), vec3( 0.0, 4.0, 0.0 ),  .50 * scale );
    float tentacle; 

    if(boundingCapsule < .001)
   	{
    	tentacle = Tentacle(q, i);

    } else {
        tentacle = boundingCapsule;
   	}

    return tentacle;
}


// Creates the tunnel and maps the tentacles onto it
float map( vec3 p){

    float tentacleRing = -1.0;
    float amplitude = 1.9;
    int numTentacles = 12;
    float offSet = (noise(.75 * PI * p.xy)/2.0 * sin(3.0 * iTime));
    float m = cos(p.x) + cos(p.y) + sin(p.z) + offSet;

    if(m < EPSILON)
    {

        vec3 colorPoint = vec3(cos(p.x) + offSet + p.x + sin(iTime), cos(p.y) + offSet + p.y + iTime, sin(p.z) + offSet + p.z + iTime);
        float whitept = PolkaDot3D(mod(p, 3.0), .0, .50);
        float blackInside = PolkaDot3D(mod(p, 3.0), .0, .4);


        float greenpt = PolkaDot3D(p, .30, .35);
        float blackInside2 = PolkaDot3D(p, .30, .15);

        float ptOffY = PolkaDot3D(colorPoint + p, .0, .2);
        float ptOffR = PolkaDot3D(mod(colorPoint, 25.0), .0, .2);

        vec3 redDotsOff = vec3(ptOffR, 0.0, 0.0);
        vec3 yellowDotsOff = vec3(ptOffY, ptOffY, 0.0);


       if(whitept > 0.0)
        {
            if(blackInside > 0.0)
            {
                K_d = BLACK;//vec3(-1.0);
            } else {
                K_d = WHITE;
            }
        }
        else if(redDotsOff.x > 0.0)
        {
            K_d = RED;
        }
         else if(greenpt > 0.0)
        {
            if(blackInside2 > 0.0)
            {
                K_d = BLACK;//vec3(-1.0);
            } else {
                K_d =  ORANGE;
            }
        }
        else if(yellowDotsOff.x > 0.0)
        {
            K_d = ORANGE;
        } else {
            vec3 c1 = .52 * palette( p.z, A4, B4, C4, D4);
            vec3 c2 = .52 * palette( p.z , A3, B3, C3, D3);

            K_d = mix(c1, c2, cos(iTime * 2.));

        }

    }
    float gap = 360.0 / float(numTentacles) * deg2rad;

    for(int i = 0; i < numTentacles; i++)
    {

       	float angle = float(i) * gap;     

        float offsetX = (amplitude) * sin(angle);
    	float offsetY = (amplitude) * cos(angle);

        vec3 localP = p;
        localP += vec3(offsetX, offsetY, 0.0);
        localP = rotateZ(-angle) * localP;


        float tentacle = opRep(localP, i);

        if(tentacleRing == -1.0)
        {
            tentacleRing = tentacle;
        }

        tentacle = unionSDF(tentacle, m); 
        tentacleRing = unionSDF(tentacle, tentacleRing);

    }



    return tentacleRing;

}


float sceneSDF(vec3 samplePoint) {

    float sdf = map(samplePoint);
     return sdf;


}


/** JW
 * 
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/** JW
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/** JW
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON_NORM, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON_NORM, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON_NORM, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON_NORM, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON_NORM)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON_NORM))
    ));
}


float lambert(vec3 N, vec3 L)
{
  vec3 nrmN = normalize(N);
  vec3 nrmL = normalize(L);
  float result = dot(nrmN, nrmL);
  return max(result, 0.0);
}


/** JW
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

//iq
vec3 applyFog( in vec3  rgb,      // original color of the pixel
               in float distance, // camera to point distance
               in vec3  rayOri,   // camera position
               in vec3  rayDir )  // camera to point vector
{
    float b = .1;
    float c = .1;
    float fogAmount = c * exp(-rayOri.y*b) * (1.0-exp( -distance*rayDir.y*b ))/rayDir.y;
    vec3  fogColor  = mix(vec3(01.5,0.6,1.7), TEAL, sin(iTime));// mix(TEAL, vec3(01.5,0.6,1.7), sin(iTime));
    return mix( rgb, fogColor, fogAmount );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, 2.5 * iTime);

    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;


    worldDir.xy = rot(worldDir.xy, (iTime));


    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(WHITE,1.0);//vec4(mix(vec3(01.5,0.6,1.7), TEAL, sin(iTime)),1.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;


    vec3 color = K_d * lambert(estimateNormal(p), eye);

    vec3 color_fog = applyFog(color, dist, eye, worldDir);

    fragColor = vec4(color_fog, 1.0);


}

#define PI 3.1415926535897932384626433832795

float map(vec3 p){
    vec3 q = fract(p) * 2. - 1.;

    return length(q) - 1.25;
}

float trace(vec3 o, vec3 r){
    float t = 0.;
    for (int i = 0; i < 32; ++i){
        vec3 p = o + r * t;
        float d = map(p);
        t += d * .5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);

    vec3 r = normalize(vec3(uv, 1.));
    float rot = iTime * .5;
    r.xz *= mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
    r.xy *= mat2(cos(rot), -sin(rot), sin(rot), cos(rot));

    vec3 o = vec3(.5 + cos(iTime * PI * .5), .5 + sin(iTime * PI * .5), -iTime);
    float t = trace(o, r);
    float fog = 1. / (1. + t * t * 2.);

    col += fog;


    fragColor = vec4(col,1.);
}

#define PI 3.14159265359
#define rot(a) mat2(cos(a + PI*0.25*vec4(0,6,2,0)))
#define FOV 2.0
#define HEX vec2(1, 1.73205080757)

#define LIGHT_ENABLE
#define LIGHT_FREQ 0.3
#define LIGHT_COLOR vec3(0.05, 0.2, 0.8)

// Dave Hoskins hash
float hash13( in vec3 p3 ) {
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

// hexagonal tiling
vec2 hexCenter( in vec2 p ) {
    vec2 centerA = (floor(p.xy*HEX)+0.5)/HEX;
    vec2 centerB = (floor((p.xy+HEX*0.5)*HEX)+0.5)/HEX-HEX*0.5;
    vec2 a = p.xy-centerA.xy; vec2 b = p.xy-centerB.xy;
    return dot(a,a)<dot(b,b) ? centerA : centerB;
}

// control sphere height
vec3 getSphereCenter( in vec2 c ) {
    return vec3(c, sin(c.x-c.y*4.3+iTime)*0.2);
}

// main distance function, returns distance and color
float de( in vec3 p, in vec3 dir, in float r, out vec3 color ) {

    // translate and get the center
    p.xy += iTime;
    vec2 center = hexCenter(p.xy);
    // find out where the red light is
    float red = floor(iTime*LIGHT_FREQ)+0.5;
    float fRed = smoothstep(0.5, 0.0, abs(fract(iTime*LIGHT_FREQ)-0.5));
    vec3 centerRed = getSphereCenter(hexCenter(red/LIGHT_FREQ + vec2(0.5, 1.5)));

    #ifndef LIGHT_ENABLE
    fRed = 0.0;
    #endif

    // accumulate distance and color
    float d = 9e9;
    color = vec3(0);
    float colorAcc = 0.0;
    for (int i = 0 ; i < 7 ; i++) {
        float theta = float(i) * (2.0*PI/6.0);
        vec2 offset = vec2(sin(theta), cos(theta))*min(1.0/HEX.y, float(i));
        vec3 sphere = getSphereCenter(center + offset);
        vec3 inCenter = p - sphere;
        float len = length(inCenter);
        vec3 norm = inCenter / len;
        vec3 toRed = sphere-centerRed;

        // select the nearest sphere
        float dist = len-0.3;
        d = min(d, dist);

        // colors and light
        vec3 albedo = vec3(sin(sphere.x*90.0+sphere.y*80.0)*0.45+0.5);
        vec3 colorHere = vec3(0);

        if (dot(toRed, toRed) < 0.001) {
            albedo = mix(albedo, vec3(0.0), fRed);
            colorHere += LIGHT_COLOR*fRed*4.0;
        } else {
            vec3 lightDir = centerRed-p;
            float len = dot(lightDir, lightDir);
            lightDir *= inversesqrt(len);
            vec3 col = LIGHT_COLOR*fRed/(len+1.0)*2.0;
            colorHere += albedo*max(0.0, dot(norm, lightDir)+0.5/len)*col;
            colorHere += albedo*pow(max(0.0, dot(lightDir, reflect(dir, norm))), 8.0)*col;
        }

        const vec3 lightDir = normalize(vec3(1, -1, 3));
        colorHere += albedo*max(0.0, dot(lightDir, norm));
        colorHere += albedo*pow(max(0.0, dot(lightDir, reflect(dir, norm))), 8.0);

        // accumulate color across neighborhood
        float alpha = max(0.0001, smoothstep(r, -r, dist));
        color += colorHere*alpha;
        colorAcc += alpha;
    }

    color /= colorAcc;
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;
	vec3 from = vec3(0, 0, 1.2);
	vec3 dir = normalize(vec3(uv, -1.0/tan(FOV*0.5)));
	dir.yz *= rot(-0.85);
    dir.xy *= rot(0.2);

    float focal = 2.5;
    if (iMouse.z > 0.5) focal = 1.0+iMouse.y/iResolution.y*4.0;
    float sinPix = sin(FOV / iResolution.y);
    vec4 acc = vec4(0, 0, 0, 1);
    vec3 dummy = vec3(0);
    float totdist = de(from, dir, 0.0, dummy)*hash13(vec3(fragCoord, iFrame));
    for (int i = 0 ; i < 100 ; i++) {
		vec3 p = from + totdist * dir;
        float r = max(totdist*sinPix, abs((totdist-focal)*0.1));
        vec3 color = vec3(1);
        float dist = de(p, dir, r, color);

        // cone trace the surface
		float alpha = smoothstep(r, -r, dist);
        acc.rgb += acc.a * (alpha*color.rgb);
        acc.a *= (1.0 - alpha);

        // hit a surface, stop
        if (acc.a < 0.01) break;
        // continue forward
        totdist += max(abs(dist), r*0.5);
	}

    fragColor.rgb = clamp(acc.rgb, vec3(0), vec3(1));
    fragColor.rgb = pow(fragColor.rgb, vec3(1.0/2.2));
    fragColor.a = 1.0;
}

// variant of https://shadertoy.com/view/4sdBW8

void mainImage(out vec4 O, vec2 U) {

    float t = iTime,l,d, r=20., a;
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution, c,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),    // ray direction
          p = 90./q;                          // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                 // rotation
        c = q, c.x+=r, a = atan(c.z,c.x), 
        q.x = length(c.xz)-r, q.z = r*a, q.y = mod(q.y-a*r/6.3,r)-r/2.,// global helix
        l = length(q.xy), d = sin(atan(q.y,q.x)-q.z),           // sin: double
                      //  d = mod(atan(q.y,q.x)-q.z,6.28)-3.14, // mod: single
        t = length(vec2(l-4.,d)) - .5,        // double-spring
        p += t*D;                             // step forward = dist to obj
}

