{
    return vec3(tan((uv.x+uv.y+(-iTime/10.0))*10.0)*10.0,tan((uv.x+uv.y+(-iTime/10.0))*10.0)*10.0,tan((uv.x+uv.y+(-iTime/10.0))*10.0)*10.0);
}

float noise(float x) {
    return fract(sin(dot(vec2(x), vec2(12.9898, 78.233)))* 43758.5453);
}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

// Tunnel pattern studied from shane & shau
// i.e. https://www.shadertoy.com/view/4tKXzV
vec2 path(float t) {
    float a = sin(t*.2 + 1.5), b = sin(t*.2);
    return vec2(a*2., a*b);
}

vec3 camera(vec3 ro, vec2 uv, vec3 ta) {
    vec3 fwd = normalize(ta - ro);
    vec3 left = cross(vec3(0, 1, 0), fwd);
    vec3 up = cross(fwd, left);
    return normalize(fwd + uv.x*left + up*uv.y);
}

float sdSphere (vec3 p, float r) {
    return length(p)-r; 
}

vec4 combine(vec4 val1, vec4 val2 ){
    if ( val1.w < val2.w ) return val1;
    return val2;
}

vec4 map(vec3 p) {
    vec3 lTex = lineTex(p.xy);

    p.xy -= path(p.z)*0.1;
    float t = 100.0;
    float w = 0.0;

    float n = floor(p.z/0.5);

    vec3 oldp = p;
    p.z = mod(p.z, 1.0)-0.5;

    w = sdBox(p-vec3(0.0,-1.2,0.0),vec3(0.03, 0.01, 0.3));
    t = min(t, w);

    w = 0.1 + dot(oldp, vec3(0, 0.0, 0.0));
    t = min(t, w);


    vec4 temp1 = vec4(0.0,0.0,0.0, t);
 	vec4 temp2 = vec4(0.8,0.8,0.1, sdBox(p-vec3(0.0,-1.21,0.0),vec3(1.0, 0.01, 1.0)));

    p.y += noise(n)*1.0+sin(2.0)*0.1;
    p.x += noise(n)*0.3-0.15;


    vec4 temp3 = vec4(lTex, sdBox(p+vec3(0.8,0.0,0.0),vec3(0.2, 1.0, 0.3)));
    vec4 temp4 = vec4(lTex, sdBox(p+vec3(-0.8,0.0,0.0),vec3(0.2, 1.0, 0.3)));

    vec4 res1 = combine(temp1,temp2);
    vec4 res2 = combine(temp3,temp4);
    vec4 res3 = combine(res1,res2);

    return res3;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = ( fragCoord.xy / iResolution.xy ) * 2.0 - 1.0;
    vec3  dir;

    vec3 pos = vec3(0.0, 0.0, iTime*SPEED);

    float dt = iTime * 6.;
    vec3 ro = vec3(0, 0, -4. + dt);
    vec3 ta = vec3(0, -2, dt);
    vec3 rd;

    ro.xy += path(ro.z);
    ta.xy += path(ta.z);

    dir = camera(ro, uv, ta);

    float t = 0.0;
    vec3 modelCl;
    for(int i = 0 ; i < 50; i++) {
        vec4 temp = map(t * dir + pos);
        modelCl = temp.xyz;
        if(temp.w < 0.01) break;
        t += temp.w * 1.0;
    }

    vec3 ip = pos + dir * t;
    vec4 cl = vec4(t * 0.01) * map(ip - 0.02) + t * 0.3;

    fragColor = cl+vec4(modelCl,1.0);
}

