    vec2 center = vec2(0.5*iResolution.x, 0.5*iResolution.y);
    vec2 uv = fragCoord.xy-center;
    uv = 2.0*uv/iResolution.y;
    float c = gain*integrate(uv);
    fragColor = vec4(1.2*sqrt(c), c, 0.5*c, 1.0);
}

float hash( float n ) {
    return fract(sin(n)*43758.5453123);
}

float noise( in vec2 x ) {
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*157.0;
    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
               mix( hash(n+157.0), hash(n+158.0),f.x),f.y);
}

const mat2 m2 = mat2( 0.80, -0.60, 0.60, 0.80 );

float fbm( vec2 p ) {
    float f = 0.0;
    f += 0.5000*noise( p ); p = m2*p*2.02;
    f += 0.2500*noise( p ); p = m2*p*2.03;
    f += 0.1250*noise( p ); p = m2*p*2.01;
    f += 0.0625*noise( p );

    return f/0.9375;
}


vec3 clouds(vec2 st){


	vec3 color = vec3(0);

	vec2 q = vec2(0.);
	q.x = fbm( st + 0.00*iTime);
	q.y = fbm( st + vec2(1.0));

	vec2 r = vec2(0.);
	r.x = fbm( st + 1.0*q + vec2(1.7,9.2)+ 0.25*iTime );
	r.y = fbm( st + 1.0*q + vec2(8.3,2.8)+ 0.126*iTime);

	float f = fbm(st+r);

	color = mix(vec3(0.101961,0.619608,0.666667),
		vec3(0.666667,0.666667,0.498039),
		clamp((f*f)*4.0,0.0,1.0));

	color = mix(color,
		vec3(0,0,0.164706),
		clamp(length(q),0.0,1.0));

	color = mix(color,
		vec3(0.666667,1,1),
		clamp(length(r.x),0.0,1.0));

	return color;
}

vec3 postprocess(vec2 uv,vec3 col){

	col.gb *=  uv.y * .6; 
	col.g = 0.0+0.6*smoothstep(-0.1,0.9,col.g*2.0);
	col = 0.001+pow(col, vec3(1.2))*1.5;
	//col = clamp(1.06*col-0.03, 0., 1.);   
    col *= mod(gl_FragCoord.y, 4.0)<2.0 ? 0.6 : 1.0;

	return col;
}
