{

	vec2 uv = ( fragCoord.xy / iResolution.xy );
	vec2 p = -1.0 + 2.0*uv;

	p.x *= iResolution.x / iResolution.y;

	if (uv.y < .12 || uv.y >= .88) {
		fragColor=vec4(0.,0.,0.,1.);
		return;
	}

	vec3 color =  vec3(0);
		 color = clouds(p);	

	vec3 sundot = vec3(2.0,2.0,1.2)*(sun(p.x,p.y,iTime)/10.0);

    p.x += 1.5;
	float r = 0.2 + 0.1*cos( atan(p.y,p.x)*10.0 + 30.0*p.x + 1.5 * sin(iTime));
	color *= smoothstep( r, r+0.01, length( p ) );
	r = 0.015;
	r += 0.002*sin(120.0*uv.y);
	r += exp(-20.0*uv.y);
 	color *= 1.0 - (1.0-smoothstep(r,r+0.002, abs(p.x-0.25*sin(2.0*p.y))))*(1.0-smoothstep(0.0,0.1,p.y));

	color = postprocess(p,sundot + color);


	vec4 final = vec4( color, 1.0 ) ;

   	if(-p.y< hash(ceil(p.x*20.))*.5) final-=final*p.y; 

	fragColor = final ;

}









