const int MAX_MARCHING_STEPS = 255; 
const float MIN_DIST = 0.0; 
const float MAX_DIST = 100.0; 
const float EPSILON = 0.0001; 
const float PI = 3.14159; 
const float sq2 = cos(PI/4.0); 

/**
 * Signed distance function for a sphere centered at the origin with radius 1.0;
 */
float boxSDF(vec3 p, vec3 b ) {
    return length(max(abs(p) - b, 0.0)); 
}

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */

float pMod(float t, float d) {
    return mod(t + d/2.0, d) - d/2.0; 
}

vec3 qrotate(vec3 axe, float angl, vec3 v) {
    float a = angl/2.0; 
    vec3 axens = normalize(axe) * sin(a); 
    return v + 2.0 * cross(cross(v, axens) - v * cos(a), axens); 
}


float bras(float an, vec3 pos, vec3 p) {
 vec3  p1 = qrotate(vec3(0., 1., 0.), an + PI/4.0, p); 
 p1 = p1 + vec3(sq2, 0., 0.); 
 vec3 p2 = qrotate(vec3(0.0, 0.0, 1.0), iTime, p1); 
 float boo = boxSDF(p2, vec3(0.1, 0.1, 2.0)); 
 vec3 p3 = qrotate(vec3(0.0, 1.0, 0.0),  - PI/4.0, p2); 
 p3 = p3 + vec3(0.25, 0., 0.25); 
 float bo1 = boxSDF(p3, vec3(0.25, 0.1, 0.25)); 
return min(boo, bo1); 
}



float sceneSDF(vec3 p) {
 float  s0 = bras(0.0, vec3(-1.0, 0.0, -1.0), p); 
 float  s1 = bras(PI/2.0, vec3(-1., 0.0, -1.0), p); 
 float  s2 = bras(PI, vec3(-1., 0., -1.), p); 
 float  s3 = bras(3.0 * PI/2.0, vec3(-1., 0., -1.), p); 	
return min(s3, min(s2, min(s1, s0))); 
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)), 
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)), 
        sceneSDF(vec3(p.x, p.y, p.z + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    )); 
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phoneContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                            vec3 lightPos, vec3 lightIntensity)
{ 
    vec3 N = estimateNormal(p); // Estimate normal of surface
    vec3 L = normalize(lightPos - p); // Point from point on surface to light
    vec3 V = normalize(eye - p); // Viewing vector, used to diffuse reflected light
    vec3 R = normalize(reflect(-L, N)); // Reflect light to the normal

    float dotLN = dot(L,N); // cosine angle between light direction and normal direction
    float dotRV = dot(R,V); // cosine angle between reflection direction and viewing direction

    // Light is coming from behind the normal of the face, pitch black
    if (dotLN < 0.)
    {
        return vec3(0.0);
    }
    // Reflected light points away from the camera, so there are no direct light. Only ambient light and diffuse color
    if (dotRV < 0.)
    {
        // This value maxes when dotLN = 1, which is when L(light) and N(normal) are equal. 100% of the light is reflected back
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
    }

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 globalAmbentLight = 0.9 * vec3(1.);
    vec3 color = globalAmbentLight * k_a; // Multiply brightness by color to get ambient color

    // Light 1
    vec3 light1Pos = vec3(0.,1.,3.);
    vec3 light1Insentity = vec3(0, 67.1/100., 43.5/100.);
    color += phoneContribForLight(k_d, k_s, alpha, p, eye, light1Pos, light1Insentity);


    // Light 2
    vec3 light2Pos = vec3(-1.,-1.,1.);
    vec3 light2Insentity = vec3(100./100., 59.2/100., 0);
    color += phoneContribForLight(k_d, k_s, alpha, p, eye, light2Pos, light2Insentity);

    // Light 3
    vec3 light3Pos = vec3(-0.5,2,-3.);
    vec3 light3Insentity = vec3(4.7/100., 35.3/100., 65.1/100.);
    color += phoneContribForLight(k_d, k_s, alpha, p, eye, light3Pos, light3Insentity);

    return color;
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */



float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end)
{
    // Start depth
    float depth = start;
    // Keep looking for where the marching ray hits a surface
    for (int i = 0; i < MAX_MARCHING_STEPS; ++i)
    {
        // Get the distance from marching ray point to surface of box
        float dist = sceneSDF(eye + marchingDirection * depth);
        // If we've hit near the surface, return this distance
        if (dist < EPSILON) {
            return depth; 
        }
        // Didn't find anything, let's go to where we found something
        depth   = depth + dist; 
      // We're at the end, stop
        if (depth > end)
        {
            return end;
        }
    }
    // Ran out of steps before we hit the end, just return end
    return end;
}

/**
    fov:         Field of View of camera
    screen_size: Screen size
   fragCoord:   Screen coord of pixel
    return:      Direction of rendering ray of the projection camera
 */



vec3 rayDirection(float fov, vec2 screen_size, vec2 fragCoord)
{
	vec2 xy = fragCoord - screen_size / 2.0;
    float z = (screen_size.y / 2.) / tan(radians(fov) / 2.);
    return normalize(vec3(xy,-z));
}




/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 lookAtMatrix(vec3 eye, vec3 center, vec3 up) 
{
    // Based on gluLookAt man page
    // Forward/Look at vector
    vec3 f = normalize(center - eye);
    // Right vector
    vec3 v = normalize(cross(f, up));
    // Camera local up Vector
    vec3 u = cross(v, f);
    return mat3(
        vec3(v),
        vec3(u),
        vec3(-f)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 camera_space_dir = rayDirection(30., iResolution.xy, fragCoord.xy);
    vec3 eye = 
        vec3(
            6.*cos(iTime) +2.,
            6.*cos(iTime*0.5) +2.,
            6.*sin(iTime)+2. 
            );

   vec3 world_space_dir = lookAtMatrix(eye, vec3(0.), vec3(0,1,0)) * camera_space_dir;
    // Find shortest distance surface
    float dist = shortestDistanceToSurface(
        eye,
        world_space_dir, 
        MIN_DIST, 
        MAX_DIST);
    if (dist > MAX_DIST - EPSILON)
    {
        fragColor = vec4(0.07);
        return;
    }

    // We've hit a surface
    // Phong shading time!!
    // Surface point
    vec3 p = eye + dist * world_space_dir; 

    vec3 K_ambientColor = vec3(0.2, 0.2, 0.0); 
    vec3 K_diffuseColor = vec3(0.7, 1.0, 1.0); 
    vec3 K_specularColor = vec3(1.0, 0.5, 1.0); 
    float shineness = 100.0; 

    vec3 color = phongIllumination(K_ambientColor, K_diffuseColor, K_specularColor, shineness, p, eye); 
    fragColor = vec4(color, 1.); 
}

//// ABSTRACT
//
//	The purpose behind this shader was a cleaner hex pattern than my previous
//	attempt and I figured while I was at it a truchet pattern, through some trial
//	and error and a bit of intuition I managed to find a way to get coordinates
//	for the lines which seemed to flow from one tile to the other.
//
//	Overall I'm happy with this shader, its looking cleaner than my previous
//	and the result is certainly more interesting, 
//
//// AUTHOR
//
//	Scott R Howell (Bombshell93)


#define SCALE .25

// hex axes

const vec2 X = vec2(1., 0.);
const vec2 Y = vec2(.5, .866);
const vec2 Z = vec2(-.5, .866);

// handy dandy pi bros

const float pi = 3.14;
const float pi2 = 6.28;

// used when finding hex coordinates and hex corners

const float sqrt3 = sqrt(3.);
const float sqrtc = sqrt3 / 3. * .5;

//// axial
//
//	finds axial coordniates via dot products of	axis vectors, effectively the
//	matrix multiplication I was using before albeit minimalized, based on the
//	"branchless method" found here:
//		https://www.redblobgames.com/grids/hexagons/more-pixel-to-hex.html

vec2 axial(vec2 p) {

    float y3 = p.y * sqrt3;
    return floor((
        floor(vec2(p.x * 2., y3 - p.x) + 1.)
        + (y3 + 1. + p.x)) / 3.);
}

//// pixel
//
//	finds pixel coordinates via axial coordinates

vec2 pixel(vec2 a) {

    return vec2(a.x - a.y * .5, a.y * sqrt3 * .5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


    vec2 uv = iTime * .33 + fragCoord / iResolution.y / SCALE;

    // get axial coordinates

    vec2 a = axial(uv);

    // find pixel coordinates of hex tile and distance from center

    vec2 h = pixel(a);
    vec2 d = uv - h;
    float da =  atan(d.y, d.x);
    d = abs(vec2(dot(d, X), dot(d, Y)));

    // find arbitrary index and use it to decide whether to use fipped corners

    float i = mod(mod(h.x, 3.) - mod(h.y * 2., 16.), 3.);
    float flip = step(1.5, i) * 2. - 1.;

    //find distance to corners

    vec2 dta = (pixel(a) + (X + Y) * flip / 3.) - uv
    	, dtb = (pixel(a) - (Y + Z) * flip / 3.) - uv
    	, dtc = (pixel(a) - (X - Z) * flip / 3.) - uv;
    float lta = dot(dta, dta)
        , ltb = dot(dtb, dtb)
        , ltc = dot(dtc, dtc);

    // find distance from nearest tile corner and mask the truchet pattern line

    float len = sqrt(min(lta, min(ltb, ltc)));
    float m = 1.;
    m *= step(0., len - sqrtc + .1) 
        - step(0., len - sqrtc - .1);

    // find the angle of the truchet pattern line

    vec2 dt = lta < ltb ? (lta < ltc ? dta : dtc) : (ltb < ltc ? dtb : dtc);
    vec3 pcol = vec3(1., mod(flip * atan(dt.x, dt.y) + iTime, pi / 3.), 0.);

    // find cube-like color and mix it with the truchet pattern respecting its mask

    float bgl = floor(mod(da - .52359, pi2) / (pi2 / 3.)) * .5;
    vec3 col = mix(vec3(0., 0., 1.) * (1. - bgl) + vec3(1.) * bgl, pcol, m);

    fragColor = vec4(col, 1.0);
}

#define LIGHT_RADIUS 0.7
#define SHAPE_RADIUS 0.1
#define PI 3.1415



float circle(in vec2 _st, in float _radius){
    float dist = distance(_st, vec2(0.0));
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dist);
}


bool seg_cir(in vec2 A, in vec2 B, in vec2 C, in float r) {
	vec2 d = B - A;
    vec2 f = A - C;

    float a = dot(d, d);
	float b = 2.0 * dot(f, d);
	float c = dot(f, f) - r * r;

	float discriminant = b * b - 4.0 * a * c;
    if( discriminant < 0.0 ) {
		return false;
	} else {
		discriminant = sqrt( discriminant );

		float t1 = (-b - discriminant)/(2.0*a);
		float t2 = (-b + discriminant)/(2.0*a);

        if( t1 >= 0.0 && t1 <= 1.0 ) {
		    return true ;
        }

        if( t2 >= 0.0 && t2 <= 1.0 ) {
    		return true ;
		}

		return false ;
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/min(iResolution.x,iResolution.y);
    vec2 mouse = iMouse.xy/min(iResolution.x,iResolution.y);

    vec3 col = vec3(0.0);

    // grid
    //vec2 grid = uv * 10.0;
    //if (fract(grid.x) < 0.05 || fract(grid.y) < 0.05) col += 1.0;

    vec2 circle_pos = vec2(cos(iTime),sin(iTime)) * 0.25 + 0.5;
    //vec2 circle_pos = vec2(0.5);

    // circle shape, casts shadow
    col += circle(uv - circle_pos, SHAPE_RADIUS);
    col -= circle(uv - circle_pos, SHAPE_RADIUS * 0.9);

    // mouse is a light source
    col += circle(uv - mouse, 0.0005);

    // ray casting
    vec2 ray = uv;

    float dist = distance(ray, mouse);
    if (dist < LIGHT_RADIUS) {
        if (!seg_cir(ray, mouse, circle_pos, SHAPE_RADIUS)) {
            col += 1.0 - smoothstep(0.0, 1.0, dist / LIGHT_RADIUS);
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

//NOTE(chen): here are the defines u can tweak

//change the below define if you don't want to see cool glitch
#define SHOW_GLITCH 1

float hash(vec2 uv)
{
    return fract(sin(dot(uv, vec2(73211.171, 841.13))) * 32131.18128);
}

float noise(vec2 uv)
{
    vec2 ipos = floor(uv);
    vec2 fpos = fract(uv);

    float a = hash(ipos);
    float b = hash(ipos + vec2(1, 0));
    float c = hash(ipos + vec2(0, 1));
    float d = hash(ipos + vec2(1, 1));

    vec2 t = smoothstep(0.0, 1.0, fpos);

    return mix(mix(a, b, t.x), mix(c, d, t.x), t.y);
}

float fbm(vec2 uv)
{
    float acc = 0.0;
    float amp = 0.5;
    float freq = 1.0;
    for (int i = 0; i < 6; ++i)
    {
        acc += amp * noise(freq * uv);
        freq *= 2.0;
        amp *= 0.5;
    }
    return acc;
}

float intersect(in vec3 ro, in vec3 rd, in float t_max, out bool hit)
{
    hit = false;
    float t = 0.1;

    for (int i = 0; i < 256; ++i)
    {
        vec3 p = ro + t*rd;
        float h = p.y - fbm(p.xz);
        if (h < 0.004*t || t > t_max)
        {
#if !SHOW_GLITCH
            if (t <= t_max)
            {
#endif
            	hit = true;
#if !SHOW_GLITCH  
            }
#endif
            break;
        }


        t += 0.45 * h;
    }
    return t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0.0, 1.0, 0.0);
    vec3 at = vec3(0.0, 0.2, 4.0);
    vec3 cam_z = normalize(at - ro);
    vec3 cam_x = normalize(cross(vec3(0, 1, 0), cam_z));
    vec3 cam_y = normalize(cross(cam_z, cam_x));
    vec3 rd = cam_x * uv.x + cam_y * uv.y + 2.0 * cam_z;

    ro += vec3(0.0, 0.8, mod(10.0*iTime, 1000.0));
    vec3 l = normalize(vec3(0.5, -0.5, 0.5));

    bool hit;
    float t_max = 27.5;
    float t = intersect(ro, rd, t_max, hit);

    vec3 p = ro + t*rd;

    //NOTE(chen): a hack, note dpdx and dpdz are not actual partial derivatives, just some offseted vector
    vec3 px = vec3(p.x + 0.0001, 0.0, p.z);
    vec3 pz = vec3(p.x, 0.0, p.z + 0.0001);
    vec3 dpdx = vec3(px.x, fbm(px.xz), px.z);
    vec3 dpdz = vec3(pz.x, fbm(pz.xz), pz.z);

    vec3 normal = normalize(cross(dpdz, dpdx));

    vec3 background = vec3(0.8, 0.8, 0.5);
    vec3 col = background;
    if (hit)
    {
        float occ = p.y;
        float dl = 0.25 * pow(dot(normal, -l) + 1.0, 2.0);
        float lighting = 0.9 * dl + 0.3;
        vec3 mat = vec3(0.8, 0.3, 0.0);

        col = mix(occ * lighting * mat, background, pow(t / t_max, 1.5));
    }

    fragColor = vec4(col, 1.0);
}

