#define PI 3.14159265359

mat2 rot(float r) {
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 1.0 / 3.0; // triangle length
                             // as fraction of viewport height

    //scale = 1.0 / (10.0 + 3.0 * sin(iTime / 5.0));

    float tHeight = scale * sqrt(3.0) / 2.0;
    float tCenterToBase = scale * 0.5 * tan(PI / 6.0);
    float tRadius = tHeight - tCenterToBase;

    // Aspect-normalized pixel coordinates (from 0 to 1 for y axis)
    vec2 R = iResolution.xy,
        uv = fragCoord / R.y;

    // Repeat triangles
    vec2 Repeat = vec2(tHeight, scale),
        Index = uv / Repeat;
    uv = mod(uv, Repeat);

    // Flip every column
    uv.x = mod(Index.x, 2.0) > 1.0 ? uv.x : tHeight - uv.x;

    // normalize texture coordinates
   	uv /= scale;

    // reflect
    float tAngle = radians(60.0);
    vec2 angleVec = vec2(cos(-tAngle), sin(-tAngle));
    float dAngleBottom = dot(uv, angleVec);
    vec2 bottomReflect = uv - dAngleBottom * 2.0 * angleVec;
    uv = dAngleBottom > 0. ? bottomReflect : uv;

    vec2 angleVecReflected = vec2(angleVec.x, -angleVec.y);
    float dAngleTop = dot(uv - vec2(0.0, 1.0), angleVecReflected);
    vec2 topReflect = uv - dAngleTop * 2.0 *  angleVecReflected;
    uv = dAngleTop > 0.0 ? topReflect : uv;

    // adjust texture
    float theta = floor(Index.y) * 2.0 * PI / 3.0 + iTime / 2.0;
    uv -= vec2(tCenterToBase / scale, 0.5);
    uv *= rot(theta);
    uv /= (2.0 * tRadius) / scale;
    uv += 0.5;

    vec4 tColor = texture(iChannel0, uv);
    vec2 cuv = fragCoord/R;
    tColor = vec4(cuv.x, 0.35 + 0.25 * sin(iTime + cuv.y), 1.0 - cuv.x, 1.0) - tColor.r;
    fragColor = tColor;
}

