#define BEATMOVE 1

const float FREQ_RANGE = 64.0;
const float PI = 3.1415;
const float RADIUS = 0.6;
const float BRIGHTNESS = 0.2;
const float SPEED = 0.5;

//convert HSV to RGB
vec3 hsv2rgb(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float luma(vec3 color) {
  return dot(color, vec3(0.299, 0.587, 0.114));
}

float getfrequency(float x) {
	return texture(iChannel0, vec2(floor(x * FREQ_RANGE + 1.0) / FREQ_RANGE, 0.25)).x + 0.06;
}

float getfrequency_smooth(float x) {
	float index = floor(x * FREQ_RANGE) / FREQ_RANGE;
    float next = floor(x * FREQ_RANGE + 1.0) / FREQ_RANGE;
	return mix(getfrequency(index), getfrequency(next), smoothstep(0.0, 1.0, fract(x * FREQ_RANGE)));
}

float getfrequency_blend(float x) {
    return mix(getfrequency(x), getfrequency_smooth(x), 0.5);
}

vec3 doHalo(vec2 fragment, float radius) {
	float dist = length(fragment);
	float ring = 1.0 / abs(dist - radius);

	float b = dist < radius ? BRIGHTNESS * 0.3 : BRIGHTNESS;

	vec3 col = vec3(0.0);

	float angle = atan(fragment.x, fragment.y);
	col += hsv2rgb( vec3( ( angle + iTime * 0.25 ) / (PI * 2.0), 1.0, 1.0 ) ) * ring * b;

	float frequency = max(getfrequency_blend(abs(angle / PI)) - 0.02, 0.0);
	col *= frequency;

	// Black halo
	col *= smoothstep(radius * 0.5, radius, dist);

	return col;
}

vec3 doLine(vec2 fragment, float radius, float x) {
	vec3 col = hsv2rgb(vec3(x * 0.23 + iTime * 0.12, 1.0, 1.0));

	float freq = abs(fragment.x * 0.5);

	col *= (1.0 / abs(fragment.y)) * BRIGHTNESS * getfrequency(freq);	
	col = col * smoothstep(radius, radius * 1.8, abs(fragment.x));

	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 fragPos = fragCoord / iResolution.xy;
	fragPos = (fragPos - 0.5) * 2.0;
    fragPos.x *= iResolution.x / iResolution.y;

	vec3 color = vec3(0.0134, 0.052, 0.1);
	color += doHalo(fragPos, RADIUS);

    float c = cos(iTime * SPEED);
    float s = sin(iTime * SPEED);
    vec2 rot = mat2(c,s,-s,c) * fragPos;
	color += doLine(rot, RADIUS, rot.x);

	color += max(luma(color) - 1.0, 0.0);

	fragColor = vec4(color, 1.0);
}

mat3 rx(float a){ float s=sin(a), c=cos(a); return mat3(1.,0.,0.,0.,+c,+s,0.,-s,+c); }
mat3 ry(float a){ float s=sin(a), c=cos(a); return mat3(+c,0.,+s,0.,1.,0.,-s,0.,+c); }
mat3 rz(float a){ float s=sin(a), c=cos(a); return mat3(+c,+s,0.,-s,+c,0.,0.,0.,1.); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    float mTime = mod(iTime, 65.);

	vec2 uv = (.7+3.*exp(-mTime)) * (2.*fragCoord.xy-iResolution.xy) / iResolution.y;

    float a = dot(uv,uv);
    vec3 sn = vec3(pow(0.1*a, 0.1));

    if(3.*a < 1.)
    {
    	float t = (2.-sqrt(1.-3.*a))/(1.+a);
    	sn = vec3(t*uv, t-2.);
        sn *= rx(-.2*iTime) * ry(iTime);
    }

    vec3 yv = fract(exp(.2*mTime)*mTime*sn);
    float y = yv.x * yv.y * yv.z;

    fragColor = vec4(y);
}

#define pi 3.14159
#define tau (2.0 * pi)

// Gold Noise ©2017-2018 dcerisano@standard3d.com
// - based on the Golden Ratio, PI and the Square Root of Two
// - fastest noise generator function
// - works with all chipsets (including low precision)

precision lowp    float;

float PHI = 1.61803398874989484820459 * 00000.1; // Golden Ratio   
float PI  = 3.14159265358979323846264 * 00000.1; // PI
float SQ2 = 1.41421356237309504880169 * 10000.0; // Square Root of Two

float gold_noise(in vec2 coordinate, in float seed){
    return fract(sin(dot(coordinate*(seed+PHI), vec2(PHI, PI)))*SQ2);
}



vec3 getColor(vec2 fragCoord, float time, vec3 color, float theta, float rad, float len, float life, float dist, float ang) {

    float beamWidth = 2.0;
    float timeLived = mod(time, life);

    rad -= timeLived * rad / life;			// Reduce radius as we spiral towards center


    float angdiff = theta - ang;			// Angle between pixel and head of tracker
    if(angdiff < 0.0) 
        angdiff += tau;

    float alpha = 0.0;
    if(dist > (rad - beamWidth * (1.0 - angdiff)) && dist < rad + beamWidth + angdiff)
        alpha = (1.0 - angdiff / len) * smoothstep(0.1, 1.0, timeLived/life + .4);

  	return vec3(color * alpha);
}


vec3 getBaseColor(int index) {

    if(index == 1)
     	return vec3( 0.0, 0.25, 0.8 );
    if(index == 2)
     	return vec3( 0.0, 0.5,  1.0 );
    if(index == 3)
     	return vec3( 0.0, 0.0,  1.0 );
    if(index == 4)
     	return vec3( 0.0, 1.0,  1.0 );
    if(index == 5)
     	return vec3( 0.0, 0.5,  0.5 );
    if(index == 6)
     	return vec3( 0.0, 0.0,  1.0 );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float rad =  80.0;


    const int trackers = 100;
    #define baseColorCount 6

    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;
    vec2 cen = iResolution.xy / 2.0;
    float dist = distance(fragCoord, cen);


    vec3 color = vec3(0.0);	// Start with black; we'll add up colors as we go

    if(dist < rad) {
    	float ang = -atan(fragCoord.y - cen.y, fragCoord.x - cen.x);

        for(int i = 0; i < trackers; i++) {

            float fi = float(i);

            float life = 5.0 + 4.0 * gold_noise(vec2(fi),fi);
            float len = 0.25 + 1.75 * gold_noise(vec2(fi)*3.0, fi); // in radians
            float theta = mod(iTime * (0.5 +  2.0 * gold_noise(vec2(fi)*2.0,fi)), tau) - pi;

            int colorIndex = int(mod(fi,float(baseColorCount)));

            vec3 baseColor = getBaseColor(colorIndex);

            vec3 col = getColor(fragCoord, iTime, baseColor, theta, rad, len, life, dist, ang);

            color += col;
        }
    }


    // Output to screen
    fragColor = vec4(color, 1.0);
}

