float shininess;

float noise( in vec2 x )
{
	return sin(1.5*x.x)*sin(1.5*x.y);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}
//sdSphere(p - vec3(0.0, -2.0, 0.0), .40)
float opTwist( vec3 p )
{
    float c = cos(20.0*p.y) + noise(p.xy);
    float s = sin(20.0*p.y) + noise(p.xy);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xz,p.y);
    return sdSphere(q/2.0, .40);
}



/**
 * Rotation matrix around the X axis. https://www.shadertoy.com/view/4tcGDr
 */
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

/**
 * Rotation matrix around the Y axis. https://www.shadertoy.com/view/4tcGDr
 */
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

/**
 * Rotation matrix around the Z axis. https://www.shadertoy.com/view/4tcGDr
 */
mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

