float saturate( float x )
{
    return clamp( x, 0.0, 1.0 );
}

//http://glslsandbox.com/e#40928.0
float Smooth( float x )
{
	return smoothstep( 0.0, 1.0, saturate( x ) );   
}



// Based off: //http://glslsandbox.com/e#40928.0
float noodle (vec3 p)
{

    float scale = 1.0 - 2.5 * saturate( abs( p.y ) * 0.01 );  
    //p.x -= sin( p.y * 2.0 + iTime * 10.0 ) * 0.1; //controls animation of tentacle


    // For ret: sdCapsule, second argument defines lower end point, third is upper end point
    // p is the point being evaluated, last argument is thickness
    float cap = sdCapsule(p, vec3( 0.0, -2.0, 0.0 ), vec3( 0.0, 4.0, 0.0 ), 0.3 * scale );

    // want more tapering at the end
    float cone = sdCone(rotateX(-PI/2.0) * p + vec3(0.0, 0.0, -1.0), normalize(vec2(1.5, .30)));//sdCappedCone(p , vec3(1.0, 1.0, 1.0));
    //return cone;
    float f = smin(cone, cap, -.5);

    return smin(f, opTwist(p - vec3(0.0, -2.0, 0.0)), .1);//sdSphere(p - vec3(0.0, -2.0, 0.0), .40));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float suctionCup(vec3 p, float scale)
{

    vec3 cp = rotateX(-PI/2.0) * p;
    float outer = cylinderSDF(cp, vec2(1.0, .30) / scale);
    float inner = cylinderSDF(cp, vec2(.25, .5) / scale);

    float mid = differenceSDF(outer, inner);


    if(mid <= 0.0 + EPSILON)
    {
        K_d = vec3(1.0, 0.0, 1.0);
    } 

    return mid;
}


//http://glslsandbox.com/e#40928.0
float Tentacle( vec3 p )
{
    p.x -= sin( p.y * 2.0 + iTime * 10.0 ) * 0.1;

    float noodle = noodle(p); // The "main body" of the tentacle
    if(noodle < 0.0 + EPSILON)
    {
        K_d = vec3(1.0, 0.0, 0.0);
    } 

    float scale = 150.0;
    p += vec3(0.0, -.70, 0.0);
    vec3 sc_p1 = p - vec3(0.01, 0.0, 0.02);
    vec3 sc_p2 = p - vec3(-0.01, 0.0, 0.02);


    sc_p1 = rotateY(PI/4.0) * sc_p1;
    sc_p2 = rotateY(-PI/4.0) * sc_p2;
    float sc1 = suctionCup(sc_p1, scale);
    float sc2 = suctionCup(sc_p2, scale);
    float sc = unionSDF(sc1, sc2);


    // first six suction cups: start small and scale bigger
    // as they are placed from the tip towards the base
    // also, rotate so that they lie "on" the surface of the tentacle
    for(int i = 0; i < 6; i++)
    {
        scale /= 1.45;
        sc_p1 += vec3(-0.00005, .05 * float(i),float(i) * -0.0085);
        sc_p2 += vec3(0.00005, .05 * float(i),float(i) * -0.0085);
        sc1 = suctionCup(sc_p1, scale);
        sc2 = suctionCup(sc_p2, scale);
        sc = unionSDF(sc, sc1);
        sc = unionSDF(sc, sc2);
    }

    sc_p1 = rotateY(-PI/8.0) * sc_p1;
    sc_p2 = rotateY(PI/8.0) * sc_p2;

    // Once close enough to the base, can be the same size and rotation
    for(int j = 6; j < 14; j++)
    {
        if(j > 12)
        {
            sc_p1 = rotateY(PI/16.0) * sc_p1;
    		sc_p2 = rotateY(-PI/16.0) * sc_p2;
        }
        sc_p1 += vec3(0, .25 , -0.0176);
        sc_p2 += vec3(0, .25 , -0.0176);
        sc1 = suctionCup(sc_p1, scale);
        sc2 = suctionCup(sc_p2, scale);
        sc = unionSDF(sc, sc1);
        sc = unionSDF(sc, sc2);
    }


    float f = smin(noodle, sc, .01);
    return f;//unionSDF(f, sdSphere(p - vec3(0.0, .50, 0.0), .40));

}

