/*
 * 0: basic
 * 1: turbulent smooth
 * 2: turbulent basic
 * 3: turbulent sharp
 * 4: dynamic
 */
int fractalType = 3;
/*
 * 0: block
 * 1: linear
 * 2: softlinear
 * 3: spline
 */
int noiseType = 3;

bool invert = false;
float contrast = 1.;
float brightness = 0.;
/*
 * 0: clip
 * 1: soft clamp
 * 2: wrap back
 * 3: allow hdr results
 */
int overflow = 3;

// transform
float rotation = 0.; // in Periode
vec2 scale = vec2(32., 32.);
vec2 offsetTurbulence = vec2(0., 0.);

float complexity = 5.;

// sub settings
float subInfluence = 0.7;
float subScaling = 0.57;
float subRotation = 0.; // in Periode
vec2 subOffset = vec2(0., 0.);
bool centerSubscale = false;

float evolution = 0.; // in Periode
bool cycleRevolution = false;
int cycle = 1; // not used if cycleRevolution is false

int randomSeed = 0;

#define SIZE 64.0
#define PI acos(-1.)
#define TAU 2.*PI
#define toRad PI/180.
#define toDeg 180./PI

// copy from https://www.shadertoy.com/view/4djSRW

#define HASHSCALE3 vec3(.1031, .1030, .0973)

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

vec2 flip(vec2 coord) {
    return vec2(coord.x, iResolution.y - coord.y);
}

float bicubic(float a, float b, float c, float d, float t) {
    float p = - a / 2. + b * 3./2. - c * 3./2. + d / 2.;
    float q = a - b * 5./2. + c * 2. - d / 2.;
    float r = - a / 2. + c / 2.;
    float s = b;

    return p * t*t*t + q * t*t + r * t + s;
}

float wrap(float value, float minValue, float maxValue) {
    return mod((value - minValue), (maxValue - minValue)) + minValue;
}

// Fractal Type

float basic(float f) {
    return f;
}

float turbulentBasic(float f) {
    return abs(f - 0.5) * 2.;
}

float turbulentSmooth(float f) {
    float x = turbulentBasic(f);
    return x*x;
}

float turbulentSharp(float f) {
    float x = turbulentBasic(f);
    return sqrt(x);
}

float selectFractal(float f) {
    float ret = 0.;
    if (fractalType == 0) {
        ret = basic(f);
    } else if (fractalType == 1) {
        ret = turbulentSmooth(f);
    } else if (fractalType == 2) {
        ret = turbulentBasic(f);
    } else if (fractalType == 3) {
        ret = turbulentSharp(f);
    } else {
        ret = basic(f);
    }
    return ret;
}

// Cycle

float selectCycle(float value, float base, float periode) {
    float ret = 0.;
    if (cycleRevolution) {
        ret = wrap(value, base, base + periode);
    } else {
        ret = value;
    }
    return ret;
}

// Noise Type

float block(vec2 fragCoord, float depth) {
    float randf = float(randomSeed);
    if (centerSubscale) randf += depth;
    vec3 hash = hash33(vec3(floor(fragCoord), randf));
    float freq = hash.x; // random freq for each coord
    float periode = 1. + floor(freq * float(cycle + 1));
    if (cycleRevolution) {
        freq = periode / float(cycle);
    }
    float evo = evolution * freq + hash.y;
    float e = randf + floor(evo); // evolution here
    float f = fract(evo);

    float a = hash33(vec3(floor(fragCoord), selectCycle(e - 1., randf, periode))).x;
    float b = hash33(vec3(floor(fragCoord), selectCycle(e + 0., randf, periode))).x;
    float c = hash33(vec3(floor(fragCoord), selectCycle(e + 1., randf, periode))).x;
    float d = hash33(vec3(floor(fragCoord), selectCycle(e + 2., randf, periode))).x;

    return bicubic(a, b, c, d, f);
}

float linear(vec2 fragCoord, float depth) {
    fragCoord -= 0.5;
    float tl = block(fragCoord, depth);
    float tr = block(fragCoord + vec2(1., 0.), depth);
    float bl = block(fragCoord + vec2(0., 1.), depth);
    float br = block(fragCoord + vec2(1., 1.), depth);

    vec2 f = fract(fragCoord);
    return mix(mix(tl, tr, f.x), mix(bl, br, f.x), f.y);
}

float softLinear(vec2 fragCoord, float depth) {
    fragCoord -= 0.5;
    float tl = block(fragCoord, depth);
    float tr = block(fragCoord + vec2(1., 0.), depth);
    float bl = block(fragCoord + vec2(0., 1.), depth);
    float br = block(fragCoord + vec2(1., 1.), depth);

    vec2 f = fract(fragCoord);
    f = smoothstep(0., 1., f);
    return mix(mix(tl, tr, f.x), mix(bl, br, f.x), f.y);
}

float spline(vec2 fragCoord, float depth) {
    fragCoord -= 0.5;

    float ttll = block(fragCoord + vec2(-1., -1.), depth);
    float ttl = block(fragCoord + vec2(0., -1.), depth);
    float ttr = block(fragCoord + vec2(1., -1.), depth);
    float ttrr = block(fragCoord + vec2(2., -1.), depth);

    float tll = block(fragCoord + vec2(-1, 0.), depth);
    float tl = block(fragCoord, depth);
    float tr = block(fragCoord + vec2(1., 0.), depth);
    float trr = block(fragCoord + vec2(2, 0.), depth);

    float bll = block(fragCoord + vec2(-1., 1.), depth);
    float bl = block(fragCoord + vec2(0., 1.), depth);
    float br = block(fragCoord + vec2(1., 1.), depth);
    float brr = block(fragCoord + vec2(2., 1.), depth);

    float bbll = block(fragCoord + vec2(-1., 2.), depth);
    float bbl = block(fragCoord + vec2(0., 2.), depth);
    float bbr = block(fragCoord + vec2(1., 2.), depth);
    float bbrr = block(fragCoord + vec2(2., 2.), depth);

    vec2 f = fract(fragCoord);

    float tt = bicubic(ttll, ttl, ttr, ttrr, f.x);
    float t = bicubic(tll, tl, tr, trr, f.x);
    float b = bicubic(bll, bl, br, brr, f.x);
    float bb = bicubic(bbll, bbl, bbr, bbrr, f.x);

    return bicubic(tt, t, b, bb, f.y);
}

// Overflow

float clipOverflow(float value) {
    return clamp(value, 0., 1.);
}

float softClampOverflow(float value) {
    return 1. / (1. + exp(2. - 4.*value));
}

float wrapBackOverflow(float value) {
    return abs(value - 2.*floor(value*0.5 + 0.5));
}

float allowHdrResultsOverflow(float value) {
    return value;
}

float selectOverflow(float value) {
    float ret = 0.;
    if (overflow == 0) {
        ret = clipOverflow(value);
    } else if (overflow == 1) {
        ret = softClampOverflow(value);
    } else if (overflow == 2) {
        ret = wrapBackOverflow(value);
    } else if (overflow == 3) {
        ret = allowHdrResultsOverflow(value);
    } else {
        ret = allowHdrResultsOverflow(value);
    }
    return ret;
}

float layer(vec2 fragCoord, float depth) {
    float ret = 0.;
    if (noiseType == 0) {
        ret = block(fragCoord, depth);
    } else if (noiseType == 1) {
        ret = linear(fragCoord, depth);
    } else if (noiseType == 2) {
        ret = softLinear(fragCoord, depth);
    } else if (noiseType == 3) {
        ret = spline(fragCoord, depth);
    } else {
        ret = spline(fragCoord, depth);
    }
    return selectFractal(ret);
}

mat3 inverseMatrix(vec2 translate, float rotate, vec2 scale) {
    return transpose(mat3(
        cos(-rotate)/scale.x, -sin(-rotate)/scale.x, -translate.x,
        sin(-rotate)/scale.y, cos(-rotate)/scale.y, -translate.y,
        0., 0., 1.
    ));
}

void transformed( out vec4 fragColor, in vec2 fragCoord ) {
    mat3 matrix = inverseMatrix(subOffset, subRotation * TAU, vec2(subScaling));

    float val = 0.;

    float totalWeight = 0.;
    mat3 trans = mat3(1.);
    float weight = 1.;
    for (float i=1.; i<complexity; i++) {
        vec2 newCoord = (trans * vec3(fragCoord, 1.)).xy;
        val  += layer(newCoord, i) * weight;

        totalWeight += weight;

        trans = matrix * trans;
        weight *= subInfluence;
    }

    float f = fract(complexity);
    if (f == 0.) f = 1.;

    vec2 newCoord = (trans * vec3(fragCoord, 1.)).xy;
    val  += layer(newCoord, floor(complexity)+1.) * weight * f;

    totalWeight += weight * f;

    val /= totalWeight;

    // color

    if (invert) {
        val = 1. - val;
    }
    val = (val - 0.5) * contrast + 0.5;
    val += brightness;

    val = selectOverflow(val);

    fragColor = vec4(vec3(val),1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    evolution = iTime;

    vec2 flipCoord = flip(fragCoord);
    mat3 trans = inverseMatrix(offsetTurbulence, rotation * TAU, scale);
    flipCoord = (trans * vec3(flipCoord, 1.)).xy;
    transformed(fragColor, flipCoord);
}

#define PI 3.14159265359

vec4 logo(in vec2 uv, float time) {
    float rad = atan(-uv.y, uv.x) + time * 0.1;
    float r = length(uv * smoothstep(0.0, 1.0, time)) - tan(rad * 3.0) * 0.1;
    float rayv = fract((rad - sin(r * 64.0 + time * 4.0) * r / 32.0) / PI * 16.0);
    float ray = smoothstep(0.45, 0.5, rayv) * smoothstep(0.9, 0.85, rayv);
    float circle = smoothstep(1.0, .2, r);     
    float circle2 = smoothstep(0.2, 0.4, r);
    float radiance = 0.6 + 0.4 * fract((1.0 - r*r) * 16.0 - time);
    return circle * circle2 * radiance * (mix(vec4(0.278, 0.733, 0.831, 1.0), vec4(0.145, 0.541, 0.627, 1.0), ray));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = fract(iTime / 4.0) * 5.0;
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y * (1.0 + smoothstep(4.0, 5.0, time) * 2048.0);
    fragColor = logo(uv, time) / dot(uv, uv) / 4.0 * smoothstep(0.0,1.0,time) * (1.0 - smoothstep(3.5, 4.5, time));

}

const float interval = .8;
const float cycle = .5;
const float gradient = 2.;
const float speed = .8;

const float angle = radians(135.);

const vec3 color = vec3(0.3, 1., 0.2);

#define RADIAL 0
#define SOLID 0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.x;

    float r = angle;
    vec2 dir = normalize(vec2(sin(r), cos(r)));
    vec2 p = vec2(0.5, 0.5 * (iResolution.y / iResolution.x));

    float m = gradient;
    float b = mod(iTime * speed, interval);

#if RADIAL
    float x = -distance(uv, p);
#else 
    float x = -dot(uv, dir);
#endif

    float y = (m * x) + b;
    y = mod(y, interval) * (1./cycle);

#if SOLID
    y = round(y);
#endif

    // Output to screen
    fragColor = vec4(color - y, 1.0);
}

// variant of "helix 5b ( triple helix )" https://shadertoy.com/view/XdtfD8
//                    and "helix 4 (DNA)" https://www.shadertoy.com/view/XddBD8

void mainImage(out vec4 O, vec2 U) {

    float t = iTime/2.+11., l,a,d,v, ds = 1.,  // ds=.5 for better look
          r0 = 200., r1 = 20., n = 3.82,       // n = 24/2pi
          A = 2.38, H = 4.*cos(A/2.);
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );  
    vec3  q = iResolution,
          D = normalize(vec3(.3*(U+U-q.xy)/q.y, -1)).zxy,  // ray direction
          c, p // = 30./q;                     // marching point along ray 
               = vec3(-30.*(iTime-2.),-2,-15.); D.yz*=R;
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015*ds )
        q = p, //q.xz *= R, q.yz *= R,         // rotation (could be factored out loop on p,D)

        c = q, c.z += r0, a = atan(c.y,c.z), 
        q.z = length(c.zy)-r0, q.y = r0*a, q.x = mod(q.x-a*r0/6.3,r0)-r0/2.,// large helix
        c = q, c.x += r1, a = atan(c.z,c.x), 
        q.x = length(c.xz)-r1, q.z = r1*a, q.y = mod(q.y-a*r1/6.3,r1)-r1/2.,// medium helix

        l = length(q.xy), a = atan(q.y,q.x), d = a-q.z,
        d = min( abs( mod(d  ,6.28) -3.14), 
                 abs( mod(d-A,6.28) -3.14) ),  // double strand (~2pi/3)
        t = length( vec3( l-4., d, fract(n*a)-.5 ) ) - .3, // spheres along spring
        d = a - round(q.z*n)/n -A/2. -3.14 +.5/n,
        d = ( length(vec2( (fract(q.z*n-.5)-.5)/n, l*cos(d)-H ))-.05 )/n, //rods
        t = min( t, v=max(l-4.,d) ),           // bounded rods
        p += ds*t*D;                           // step forward = dist to obj

    if (t==v) O.rg *= .9; else O.gb *= .9;     // colored rods vs spheres
}

// First I implemented model in full
// When it weren't behaving as it should i started commenting out parts trying to pinpoint
// problematic spot
// It seems to be displacement calculation that is a problem 
// but maybe there is something in twist_trajectory calculation also
//
// Commenting out all parts that seem irrelevant to the problem 
// as to concentrate just on the problematic spot 

// UPDATE: FIXED, displacement calculation indeed was lacking small detail which is 
// thickness change compensation at the beginning of formula 
// instead of just ( 2.0 ) we need ( 2.0 / thickness )
// uncommenting full model

#define M_PI 3.1415926535897932384626433832795

// Used to perturb the value of the displacement for rough thread appearance
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float remap( float range_a_point, float a0, float a1, float b0, float b1 ){
    return (((range_a_point - a0) * (abs(b1-b0)))/abs(a1-a0)) + b0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float x = uv.x * 5.;
    float y = uv.y * 1.;


    // When the facet length increases, if we still keep x and y varying between [0,1], 
    // then the texture stretches to facetlength and would appear loose. 
    // By increasing the tightness by a factor of facetlength, the thread appears tight again 

    float facetlength = 5.0;
    float tightness = 0.5; // (0,1]
    float twist_trajectory = 
        ( ( asin( 2. * y - 1. )/( M_PI / 2. ) + 1. ) * tightness * facetlength ) / 2.; // (1)


    // The trajectory of the twist in equation (1) is used to deﬁne the shading by 
    // considering the distance/displacement of a point from the trajectory
    // where thickness indicates the thickness of the bundle that is twisted and has values in (0,1]

    float thickness = 1. - remap( sin( iTime - M_PI / 2.), -1., 1., 0., 0.6 ); // (0,1]
    float x_plus_traj = x + twist_trajectory;
    float displacement = 
        ( 2. / thickness ) * ( x_plus_traj - ( floor( x_plus_traj / thickness ) * thickness) ) - 1.; // (2)


    // To add roughness to the appearance of the twist 
    // we perturb the value of the displacement by a random value
    // For this a parameter smoothness in [0,1] is introduced
    // where rand_value has values in [ 0, thickness )

    float smoothness = 1.; //[0,1]
    float rand_value = rand( uv ) * thickness;
    float pdisplacement = 
        smoothness * displacement + ( 1. - smoothness) * rand_value; // (3)


    // When the displacement is zero we would like the shading to be maximum 
    // and as the displacement increases we would like the value to decrease 
    // therefore we use the Gaussian function to achieve this behavior 

    float twist_shading = exp( -(  pow( pdisplacement, 2. )/0.5 ) ); // (4)

    // Linear shading also looks great
    // twist_shading = abs(pdisplacement);

    // The appearance of a thread facet is created by 
    // modulating the above shading of the twist of ﬁbers with 
    // the shading resulting from the shape of the thread. 
    // offset in [0,1] is used to control the darkness of shading at the periphery

    float y_offset = 0.1; // [0,1]
    float y_shading = y_offset + ( 1. - y_offset ) * sin( y * M_PI );


    // The same formula can be used for the shading in the x direction 
    // if the thread facet is short. However the length of a facet can vary 
    // depending on the weave pattern, and if the facet is long the above shading 
    // gives the appearance of a wicker basket to the weave 
    // The shading when the thread facet is long is therefore deﬁned with the help 
    // of the tanh function as follows

    float x_shading = 1.;
    float x_offset = 0.3; // [0,1]
    float steepness = facetlength; // facetlength used as in original paper
    if( uv.x < 0.5 ){
        x_shading = x_offset + ( 1. - x_offset ) * tanh( uv.x * steepness );
    }
    if( uv.x >= 0.5 ){
        x_shading = x_offset + ( 1. - x_offset ) * tanh( ( 1.0 - uv.x ) * steepness );
    }

    // Final calculation of thread appearance
    float thread_shading = twist_shading * y_shading * x_shading;

    vec3 col = vec3( thread_shading );

    // Output to screen
    fragColor = vec4( col, 1.0);
}

float Band(float t, float start, float end, float blur) 
{
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep(end + blur,   end - blur,   t);

    return step1*step2;
}

float Rect(vec2 uv, float l, float r, float bottom, float top, float blur)
{
    float band1 = Band(uv.x, l,        r, blur);
    float band2 = Band(uv.y, bottom, top, blur);

    return band1 * band2;
}

float r(vec2 uv, float xBegin, float yBegin, float h, float w) 
{
    float xEnd = xBegin + w;
    float yEnd = yBegin + h;

    return Rect(uv, xBegin, xEnd, yBegin, yEnd, 0.000001);
}

float draw(vec2 uv, float x, float y, float w, float h, int digit)
{
    float f = 0.0;

	if(digit == 0)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w); f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 0., y + h * 1., h, w); f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 1)
	{
		f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 2)
	{
	    f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 0., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 3)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
    	f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 4)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w); f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 5)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
        f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 6)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
        f += r(uv, x + w * 0., y + h * 1., h, w); f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 7)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
        f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 8)
	{
	    f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w); f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 0., y + h * 1., h, w); f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 9)
	{
	    f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w); f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = gl_FragCoord.xy / iResolution.xy;

	uv *= 3.;
	vec2 idx = floor( uv );

	uv = fract( uv );

    uv.x *= iResolution.x/iResolution.y;

	float h  = 0.025;
	float w = h;

    vec3 col = vec3(0.0);

    float f = 0.0;

    float xx = w;
	float yy = h * 18.;

	float xxx = xx;
	float yyy = yy;

	float a;

	vec3 test = vec3(iTime, 1.0 / 3.0, -1389.4235);
	vec3 v = test;

	if(idx.x == 1. && idx.y == 2.)
	{
	    a = v.x;
		yyy -= h * 18.0;
	}

	else if(idx.x == 1. && idx.y == 1.)
	{
	    a = v.y;
	}

	else if(idx.x == 1. && idx.y == 0.)
	{
	    a = v.z;
		yyy += h * 17.0;
	}

	else
	{
	    fragColor = vec4(0.0);
	    return;
	}

	f = 0.0;

	// Minus 
	if(a < 0.0)
	{
	    f += r(uv, xxx + w * 0., yyy + h * 2., h, w);
    	f += r(uv, xxx + w * 1., yyy + h * 2., h, w);
	    f += r(uv, xxx + w * 2., yyy + h * 2., h, w);
		a = abs(a); 
    }

	xxx += w * 4.0;

	int e  = int(mod(a,         10.));
	int z  = int(mod(a /   10., 10.));
	int hu = int(mod(a /  100., 10.));
	int t  = int(mod(a / 1000., 10.));

	int n1 = int(mod(a *    10., 10.));
	int n2 = int(mod(a *   100., 10.));
	int n3 = int(mod(a *  1000., 10.));
	int n4 = int(mod(a * 10000., 10.));

    f += draw(uv, xxx, yyy, w, h, t);
	xxx += w * 4.0;
    f += draw(uv, xxx, yyy, w, h, hu);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, z);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, e);
	xxx += w * 4.0;

	f += r(uv, xxx + w * 0.0, yyy + h * 0.0, h, w);

	xxx += w * 2.0;
	f += draw(uv, xxx, yyy, w, h, n1);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, n2);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, n3);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, n4);

    col = vec3(0.0, 1.0, 0.0) * f;

    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    // ring
    float u = abs(sin((atan(p.y, p.x) + iTime * 0.5 + length(p) ) * 15.0)*0.5) + 0.25;
    float t = 0.2 / abs(0.35*sin(iTime) + u - length(p));  
    fragColor = vec4(vec3(t*0.75), 1.0);
}

const float INFINITY = 1.e20;
const float PI       = 3.1415;

// scene globals
vec3 light_pos;
vec3 origin;
vec3 target;
vec3 up;

// update scene
vec3 path(float time) {
    float x = time * 8.;
    return vec3(x, -2. - 1.5 * cos(x / 7. * PI), sin(iTime / 2.) * 2.);
}

void update_world() {
    light_pos = vec3(3.5 * cos(iTime / 3.), 3.5 * sin(iTime / 3.), 4.);
    origin = path(iTime);
    up = normalize(vec3(0., cos(origin.x / 7. * PI), 6.));
    light_pos = path(iTime + 1. + 0.8 * cos(iTime / 3.));
    light_pos.z = light_pos.z * 2.;
    target = light_pos;
    target.yz = mix(origin.yz, target.yz, 0.2);
}


// operations

float smootherstep(float e0, float e1, float x) {
    x = clamp((x - e0) / (e1 - e0), 0., 1.);
    return x * x * x * (x * (x * 6. - 15.) + 10.);
}

float smin(float a, float b, float k) {
    float h = smootherstep(0., 1., 0.5+0.5*(b-a)/k);
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec3 repeat(vec3 pos, vec3 distance) {
    return mod(pos + 0.5 * distance, distance) - 0.5 * distance;
}

// produces n-1 steps of a staircase
float stairs(float a, float b, float r, float n) {
	float s = r/n;
	float u = b-r;
	return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2. * s)) - s)));
}

// primitives

float plane(vec3 pos) {
    return pos.z;
}

float sphere(vec3 pos, float radius) {
    return length(pos) - radius;
}

float cylinder(vec3 pos, vec3 axis, float radius) {
    return length(cross(pos, axis)) - radius;
}

// map function

vec2 map(vec3 pos) {
    vec3 abspos = pos;
    pos.z = -abs(pos.z);

    pos.y += 4. * round(pos.x / 7.);
    pos = repeat(pos, vec3(7., 8., 100.));

    return vec2(smin(
        stairs(
            cylinder(pos, vec3(0., 0., 1.), 1.),
            sphere(pos, 2.),
            2., 8.
        ),
        plane(pos + vec3(0., 0., 6. + cos(pos.x / 7. * PI * 4.) * cos(pos.y / 8. * PI * 4.) * 0.5)),
        3.
    ) * 0.8, 1.);
}

// rendering

vec2 march_ray_enhanced(vec3 origin, vec3 direction, float pixel_radius) {
    // see http://erleuchtet.org/~cupe/permanent/enhanced_sphere_tracing.pdf for details
    // ray marching parameters
    const float min_distance = .1;
    const float max_distance = 500.;
    const int max_iter = 50;
    const int max_smooth_iter = 4;

    // initial conditions
    float omega = 1.2;
    float distance = min_distance;
    float candidate_error = INFINITY;
    float candidate_distance = 0.;
    float candidate_material = 0.;
    float previous_radius = 0.;
    float advance_size = 0.;

    int i;
    for (i = 0; i < max_iter; i++) {
        // determine distance and material of closest-by object
        vec2 dist_mat = map(origin + direction * distance);

        // determine absolute radius for over-relaxation and error checks
        float radius = abs(dist_mat.x);

        // checks if we were progressing over-relaxed and if it failed
        bool failed_over_relaxed_advance = omega != 1.0 && (radius + previous_radius) < advance_size;

        // update the previous radius 
        previous_radius = radius;

        // calculate error as a screen space angle
        float error = radius / distance;

        // determines how far we should advance
        if (failed_over_relaxed_advance) {
            // if we failed, we have to step back to where normal advancement would've brought us
            advance_size -= advance_size * omega;
            omega = 1.;

        } else {
            advance_size = dist_mat.x * omega;

            // was this a better hit than the previous one?
            if (error < candidate_error) {
                candidate_distance = distance;
                candidate_error = error;
                candidate_material = dist_mat.y;
            }

            // did we hit something or the end of the ray
            if (error < pixel_radius || distance > max_distance) {
                break;
            }
        }

        // advance
        distance += advance_size;
    }

    if (distance > max_distance) {
        return vec2(INFINITY, -1.);
    }

    // post-processing on the hit point: a few iteration steps to get smooth coordinates
    for (int j = 0; j < max_smooth_iter; j++) {
        float allowable_error = pixel_radius * candidate_distance;
        candidate_distance += map(origin + direction * candidate_distance).x - allowable_error;
    }

    return vec2(candidate_distance, candidate_material);
}

vec3 estimate_normal(vec3 pos) {
    vec2 e = vec2(1.0,-1.0)*0.5773*0.05;

    // for four points on a cube, none of them
    // next to another, calculate the change
    // basically in any axis there are two points at +e and two points at -e
    // note that length(e) is sqrt(3 * e) for any point to get the actual change
    // in that axis you should divide by sqrt(3) (1/sqrt(3) = 0.5773)
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}

float shadow(vec3 p, vec3 light_dir, float mind, float maxd) {
    float d = mind;
    float dist;
    float res = 1.;

    for (int i = 0; i < 10; i++) {
        vec2 dist_mat = map(p + d * light_dir);
        dist = dist_mat.x;
        res = min(res, 4. * dist / d);
        d += clamp(dist, mind, mind * 10.);

        if (dist < 0.001 || d > maxd) {
          break;
        }

    }

    return clamp(res, 0., 1.);
}

// really cheap occlusion
// 0.0 = occluded, 1.0 = not occluded
float occlude(vec3 p, vec3 norm, float fewaf) {
    float dist = .1;
    float tot = 0.;

    for (int i = 1; i < 7; i++) {
        float try_dist = dist * float(i);
        float d = map(p + norm * try_dist).x;
        tot += (d / try_dist);
    }
    return clamp((tot - 2.) / 4., 0.0, 1.0);

}

vec3 render(vec3 origin, vec3 direction, float pixel_radius) {
    // cast our initial ray to determine what we'll hit
    vec2 dist_mat = march_ray_enhanced(origin, direction, pixel_radius);
    float distance = dist_mat.x;
    float material = dist_mat.y;

    vec3 hit = origin + direction * distance;

    // calculate a normal vector around the hit point and a reflection vector
    vec3 normal = estimate_normal(hit);
    vec3 reflect_dir = reflect(direction, -normal);

    // return the color (currently just based on total distance travelled through 2 reflections.
    //return (material > -0.5) ? vec3(10. / (dist_mat.x + reflect_dist_mat.x + rreflect_dist_mat.x)) : vec3(0.);
    if (material < 0.) {
        return vec3(0.02, 0.015, 0.01) * 0.5;
    }

    // basic phong shading
    vec3 light_dir = hit - light_pos;
    vec3 reflect_light_dir = reflect(normalize(light_dir), -reflect_dir);

    float diff = max(0.0, -dot(normal, normalize(light_dir)));
    float spec = pow(max(0.0, -dot(direction, reflect_light_dir)), 4.) * smoothstep(0., 1., diff * 10.);

    float occ = shadow(hit, -light_dir, 0.01, length(light_dir));
    float light_intensity = 40. * occ * pow(1. / length(light_dir), 2.);

    vec3 color = vec3(0.01) +
                 vec3(0.04) * diff * light_intensity +
                 vec3(0.8)  * spec * light_intensity;

    // occlusion
    color *= vec3(occlude(hit, normal, 0.04));

    // particle effect
    float z_plane = floor(hit.z * 100.) / 100.;

    float angle = atan(normal.y, normal.x) * ((hash(vec2(z_plane, 8.)) > .5) ? -1. : 1.);
    float expected_angle = hash(vec2(z_plane, 5.)) * 2. * PI + iTime * 2. * pow(hash(vec2(z_plane, 4.)), 2.);

    float h = hash(vec2(z_plane, 1.));
    float angle_from_lead = mod((angle - expected_angle + sin(hit.x * 0.1 * h) - cos(hit.y * 0.13 * h)) / 2. / PI, 1.);

    float brightness = pow(max(angle_from_lead - 0.9, 0.0) * 10., 4.);
    float fade = max(0.0, min(1.0, 5. * cos(iTime * 0.1 * hash(vec2(z_plane, 2.))) - 2. + 5. * hash(vec2(z_plane, 3.)) ));

    if (length(normal.xy) > 0.01) {
        color += brightness * fade * vec3(0.8, 0.5, 0.1) * length(normal.xy) * 2.;
    }

    // fog
    color = mix(vec3(0.02, 0.015, 0.01) * 0.5, color, exp(-distance * 0.1));

    // sphere glow
    vec3 sphere_diff = light_pos - origin;
    float sphere_dist = length(sphere_diff - direction * dot(sphere_diff, direction));
    color += smoothstep(-3.5, 0., distance - length(sphere_diff)) * min(1.0, exp(-(sphere_dist - 0.6) * 4.)) * vec3(1.);

    return color;
}

mat3 look_at(vec3 origin, vec3 target, vec3 reference_up, float tilt) {
    // note: I'm using a reference frame where Z is upwards, and X/Y a groundplane
    // direction the camera is looking at.
    // this function returns a transformation matrix that takes X/Y screenpos + Z depth into that reference frame
    vec3 direction = normalize(target - origin);

    // construct upwards pointing direction aligned frame
    vec3 side = normalize(cross(direction, reference_up));
    vec3 up   = cross(side, direction);

    // rotate around direction
    vec3 up_rotated   = cos(tilt) * up   - sin(tilt) * side;
    vec3 side_rotated = cos(tilt) * side + sin(tilt) * up;

    // construct reference frame.
    return mat3(side_rotated, up_rotated, direction);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // calculate scene
    update_world();

    // anti-aliasing oversampling
    const int AA = 1;
    // screen height / screen distance. 2 = 90deg, 1.15 = 60deg
    const float FOV_scale = 4.;

    // normalized cursor location
    vec2 cursor = (2. * iMouse.xy - iResolution.xy) / iResolution.y;

    // approximate normalized projected pixel size. multiply by distance to get local projected pixel radius
    // Note that this is only really true for the center pixel, the sphere that fits in pixels further to the
    // outside gets smaller
    float pixel_radius = FOV_scale / iResolution.y / 2.;

    // camera location
    //vec3 origin = vec3(-10. * cos(cursor.x), -10. * sin(cursor.x), cursor.y * 5.);

    // camera to world transform matrix
    mat3 camera = look_at(origin, target, up, 0.);

    vec3 color = vec3(0.);
    for (int i = 0; i < AA; i++) {
        for (int j = 0; j < AA; j++) {

            // subpixel coordinates for anti-aliasing
            vec2 subpixel_coord = -0.5 + (0.5 + vec2(float(i), float(j))) / float(AA);   

            // map pixel location to a -1 to 1 field on the y axis, and an appropriately scaled x axis.
            // Scale this to control FoV (currently 90 deg)
            vec2 pixel_coord = FOV_scale * (fragCoord + subpixel_coord - 0.5 * iResolution.xy) / iResolution.y;

            // translate screenspace to worldspace and normalize pixel coordinate position
            vec3 direction = normalize(camera * vec3(pixel_coord, 1.));

            // render in that direction
            color += render(origin, direction, pixel_radius);

        }
    }

    color /= pow(float(AA), 2.);

    // gamma correct and write to output
    fragColor = vec4(pow(color, vec3(1. / 2.2)), 1.);
}

// "Lightning Attractor" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// Lightning in 3D (from the Tesla demos)

float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
vec2 Rot2D (vec2 q, float a);
float Fbm1 (float p);
float Fbm2 (vec2 p);

vec4 pth[8];
vec3 sunDir, qHit;
float tCur, dstFar;
int idObj;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float SparkDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  dMin *= 2.;
  for (int k = 0; k < 8; k ++) {
    q = p;
    if (k < 4) {
      q.xz = Rot2D (q.xz, (0.5 + float (k)) * 0.5 * pi + 0.15 * tCur);
      q.yz = Rot2D (q.yz, 0.25 * pi);
    } else {
      q.xz = Rot2D (q.xz, (0.5 + float (k)) * 0.5 * pi - 0.15 * tCur);
      q.yz = Rot2D (q.yz, -0.25 * pi);
    }
    q.z += 20.2;
    d = PrCylDf (q, 0.9, 20.);
    if (d < dMin) {
      q.xy -= (1. - smoothstep (19.5, 20., q.z)) * (2. * vec2 (Fbm2 (vec2 (0.4 * q.z, 0.) +
         pth[k].xy), Fbm2 (vec2 (0.3 * q.z, 0.) + pth[k].zw)) - 1.);
      d = PrCylDf (q, 0.02, 20.);
      DMINQ (1);
    }
  }
  return 0.5 * dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;
  d = PrSphDf (q, 0.3);
  DMINQ (2);
  dMin = SparkDf (p, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj, f, t;
  for (int k = 0; k < 8; k ++) {
    f = 0.1 * float (k);
    t = 0.1 * tCur;
    pth[k] = vec4 (4. + f - (10. + 3. * f) * t + (0.8 - f) * sin ((0.1 + 0.1 * f) * t),
       sin ((0.11 + 0.1 * f) * t) + (0.3 + 0.2 * f) * sin ((0.17 + 0.5 * f) * t),
       5. + 2. * f - (12. - 2. * f) * t + (0.5 + 0.3 * f) * sin ((0.15 - 0.2 * f) * t),
       sin ((0.12 - 0.1 * f) * t) + (0.4 - 0.2 * f) * sin ((0.12 + 0.2 * f) * t));
  }
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) {
      col = vec3 (1., 1., 0.6) * clamp (1.3 - 0.3 * Fbm1 (5. * qHit.z), 0., 1.) *
         (0.1 + 1.3 * max (0.3 - dot (rd, vn), 0.)) * (1. - dstObj / dstFar);
    } else if (idObj == 2) {
      col = vec3 (0.5, 0.5, 0.6) * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) +
         0.1 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    }
  } else {
    col = vec3 (0.6, 0.6, 1.) * (0.05 + 0.245 * (rd.y + 1.) * (rd.y + 1.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur += 10.;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az = 2. * pi * mPtr.x;
    el = 0.2 * pi * mPtr.y;
  } else {
    az = 0.1 * pi * tCur;
    el = 0.15 * pi * sin (0.13 * pi * tCur);
  }
  el = clamp (el, -0.1 * pi, 0.1 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -30.);
  zmFac = 6. + 2. * sin (0.13 * pi * tCur);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  dstFar = 100.;
  sunDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

