{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 p1=vec3(1.0,0.8,0);
    vec3 p2=vec3(0,0,1.0);
    vec3 p3=vec3(1.0,0,0);
    vec3 p4=vec3(1.0,1.0,1.0);
    vec3 p5=vec3(0,0,0);
    vec3 p6=vec3(1.0,0,0);


    float midpoint=0.499*sin(iTime)+0.5;


    float factor=0.0;
    if(midpoint<=0.5)
    {
        factor=1.0-pow(1.0-uv.x,1.0/(midpoint*2.0));
    }
    else
    {
        factor=pow(uv.x,1.0/((1.0-midpoint)*2.0));
    }

    vec3 interpolated1=(1.0-factor)*p1+(factor)*p2;
    vec3 interpolated2=(1.0-factor)*p3+(factor)*p4;
    vec3 interpolated3=(1.0-factor)*p5+(factor)*p6;
    if(uv.y>=0.0 && uv.y<=0.33)
    	fragColor = vec4(interpolated1,0);    
    else if(uv.y>0.33 && uv.y<=0.66)
        fragColor = vec4(interpolated2,0);
    else 
        fragColor = vec4(interpolated3,0);
}

