const vec2 c = vec2(1.,0.);
float iScale;

vec3 rot(vec3 x, vec3 theta)
{
    vec3 c = cos(theta), s = sin(theta);
    return mat3(c.x,0.,s.x,0.,1.,0.,-s.x,0.,c.x)
        *mat3(1., 0., 0., 0., c.y, -s.y, 0., s.y, c.y)
        *mat3(c.z,-s.z,0., s.z,c.z,0., 0.,0.,1.)*x;
}

float rand(vec2 x)
{
    return fract(255123.*sin(3241.*x.x+2123034.*cos(2144235.*x.y)));
}

vec2 scene(vec3 x)
{
    x += .5;
    x = vec3(length(x), acos(x.x/length(x)), acos(x.y/length(x)));

    x.y += x.y*sin(x.x);
    x = rot(x, 1.e-1*vec3(1.,1.4,2.)*iTime);

    vec3 y = x-.1;

    x = mod(x, .2)-.1;
    vec2 sdf = vec2(length(x)-.05-.05*iScale, 1.);
    x = mod(x, .02)-.01;
    vec2 sda = vec2(length(x)-.005-.005*iScale, 2.);
    sdf = mix(sda, sdf, step(-sdf.x, sda.x));

    y = mod(y, .2)-.1;
    vec2 sdb = vec2(length(y)-.05-.05*(.5-iScale), 3.);
    sdf = mix(sdf, sdb, step(sdb.x, sdf.x));
    y = mod(y, .02)-.01;
    vec2 sdc = vec2(length(y)-.005-.005*(.5-iScale), 4.);
    sdf = mix(sdc, sdf, step(-sdf.x, sda.x));

    return sdf;
}

vec3 normal(vec3 x)
{
    float s = scene(x).x;
    return normalize(vec3(scene(x+1.e-4*c.xyy).x-s, scene(x+1.e-4*c.yxy).x-s, scene(x+1.e-4*c.yyx).x-s));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy, sdm = c.yy;
    vec3 col = c.yyy, o = 2.*c.xxx, t = c.yxy, dir = normalize(t-o), r = c.xyy, ro = uv.x*r+uv.y*cross(r, dir), x=c.yyy;
    iScale = mod(iTime, .5);
    int i=0, ni=100;
    float d = 0.;
    for(i; i<ni; ++i)
    {
        x = ro + d*dir;
        sdm = scene(x);
        if(sdm.x<1.e-4)break;
        if(d>=100.)return;
        d += sdm.x;
    }
    if(i==ni)return;

    vec3 n = normal(x);
    if(sdm.y == 1.)
        col = .2*c.yyx+c.xyy*dot(c.yxx, n);
    else if(sdm.y == 2.)
        col = .2*c.yyx+c.yxy*dot(c.yyx, n);
    else if(sdm.y == 3.)
        col = .2*c.xyy+c.yyx*dot(c.yyx, n);
    else if(sdm.y == 4.)
        col = .2*c.xyy+c.yxy*dot(c.yyx, n);
    else col = c.yyy;

    col = mix( rot(col, -vec3(2.*iTime+length(x))), rot(col, vec3(3.*iTime-length(x))), .5+.5*sin(5.*iTime));
    // Output to screen
    fragColor = vec4(col,1.0);
}

