float ballDistP(vec3 objpos, vec3 rd, vec3 ro){
return length(cross(objpos - ro, rd))/length(rd);
}

float cubepoints(float radius, float blur, vec3 rd, vec3 ro, vec3 pos){
float d = 0.;
d = smoothstep(radius,radius-blur,ballDistP(vec3(pos), rd, ro));
  d += smoothstep(radius,radius-blur,ballDistP(vec3(1,0,0)+vec3(pos), rd, ro));
  d += smoothstep(radius,radius-blur,ballDistP(vec3(0,1,0)+vec3(pos), rd, ro));
  d += smoothstep(radius,radius-blur,ballDistP(vec3(0,0,1)+vec3(pos), rd, ro));
  d += smoothstep(radius,radius-blur,ballDistP(vec3(1,1,0)+vec3(pos), rd, ro));
  d += smoothstep(radius,radius-blur,ballDistP(vec3(1,0,1)+vec3(pos), rd, ro));
  d += smoothstep(radius,radius-blur,ballDistP(vec3(0,1,1)+vec3(pos), rd, ro));
  d += smoothstep(radius,radius-blur,ballDistP(vec3(1,1,1)+vec3(pos), rd, ro));
  return d;
}

void mainImage( out vec4 FragColor, in vec2 FragCoord ){
    vec2 touch = iMouse.xy;
    float time = iTime;
vec2 uv = FragCoord.xy / iResolution.xy;
  uv += -.5;
  uv.y *= iResolution.y/iResolution.x;
  float t= time*.4;
  vec2 T = 6.*touch/iResolution.y;

  //Camera setup
  float R = 1.;
  float zoom = 0.3;
  vec3 ro = vec3(R*sin(t+T.x)*cos(1.7*t + T.y),R*cos(t+T.x)*cos(2.1*t+T.y),R*cos(t+T.x));
  vec3 lookat = vec3(0,.3,0);
  vec3 fw = normalize(lookat - ro);
  vec3 r = cross(vec3(0,1.,0), fw);
  vec3 up = cross(fw,r);
  vec3 scrC = ro + zoom*fw;
  vec3 scrP = scrC + uv.x*r + uv.y*up;
  vec3 rd = scrP - ro;

  float radius = .1;
  float blur = .03;

  float d = cubepoints(radius, blur, rd, ro, vec3(0,.1,0));
  float d1= cubepoints(radius, blur, rd, ro, vec3(0,.1,blur));
  float d2= cubepoints(radius, blur, rd, ro, vec3(0,blur+.1,0));

FragColor = vec4(d,d1,d2,1.) + .95*vec4(.7,.8,.7,1.)+.3*vec4(uv.x*sin(t+T.x),uv.y*cos(2.*t+T.y),1,.1);
}

float rnd(float a, float i, float arms){
    //const float arms=20.;
    float i1=mod(floor(a*arms),arms)+mod(i*12.672,2236.77621);
    float i2=mod(floor(a*arms+1.),arms)+mod(i*12.672,2236.77621);

    float i11=mod(cos(i1*mod(i1,3.)*.00632)*356.9,1.);
    float i22=mod(cos(i2*mod(i2,3.)*.00632)*356.9,1.);
    float span=mod(a*arms,1.);
    return i22*span+i11*(1.-span);
}

vec3 col(float i){
    if(cos(i*i)<-.98){
        return (1.+cos(acos(-1.)*vec3(mod(cos(i*.045621)*2341.,1.),mod(cos(i*.085601)*2311.,1.),mod(cos(i*.049691)*2141.,1.))))*.5;
    }
    else{
        float phase=mod(floor(i/20.),3.);
        float mixx=mod(i,20.)/20.;
        vec3 c1;
        vec3 c2;
        if(phase<.5){
            c1=vec3(0.,0.,0.);
            c2=vec3(1.,0.6,0.);
        }
        else if(phase<1.5){
            c1=vec3(1.,0.6,0.);
            c2=vec3(0.,0.3,1.);
        }
        else{
            c1=vec3(0.,0.3,1.);
            c2=vec3(0.,0.,0.);
        }
        return c1*(1.-mixx)+c2*mixx;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.yy;

    // Output to screen
    fragColor = vec4(0.0,0.0,0.0,1.0);

    float angle=atan(uv.y,uv.x)/acos(-1.)/2.;

    float layer=floor(iTime/2.);
    float zoom=mod(iTime/2.,1.);
    float pixelsize=1./iResolution.y;
    float stepsclose=floor(log(length(uv))/log(1.2));
    float aa;
    for(aa=1.;aa<14.;aa++){
        float a=aa+stepsclose;
        float zooma=pow(1.2,a+zoom);
        vec2 uvzoom=uv/zooma;
        float bend=.6-length(uvzoom);
        float pixelsize2=pixelsize/length(uv)*.05;
        float angle2=mod(angle+bend*bend*bend*bend*.4*sin((a-layer)*.3+iTime),1.);
        float b;
        float cover=0.;
        for(b=-2.;b<=2.;b++){
            float h=rnd(mod(angle2+b*pixelsize2,1.),a-layer,floor(60.*(1.2+sin(0.0774*(a-layer)))))*.5+.1;
            if(h<length(uvzoom)){
                cover+=0.2;
                //fragColor = vec4(vec3(a+zoom)*.02+.2,1.0);
            }
        }
        fragColor = vec4(col(a-layer),1.0)*cover+fragColor*(1.-cover);
    }


}

