void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	 //sets 0 as the centre of the screen 
	vec2 r = 2.*fragCoord / iResolution.xy -1.;

    //variables
    float x = r.x;
    float y = r.y;
    float Thickness = 0.3;
    float Yoffset = 0.9; 
    float Height = 0.2;

    //Colors
    vec3 white = vec3(1,1,1);
    vec3 green = vec3(0,1,0);
    vec3 blue = vec3(0,0,1);
    vec3 pixelCol = vec3(0, 0, 0);

    if(abs((x * Height) * sin(PI * (x + iTime)) - (y - Yoffset)) < Thickness) pixelCol = white;
    if(abs((x * Height) * sin(PI * (x + iTime)) - (y + Yoffset)) < Thickness) pixelCol = blue;
    if(abs((x * Height) * sin(PI * (x + iTime)) - y) < (Thickness * 2.0)) pixelCol = green;

    // Output to screen
    fragColor = vec4(pixelCol,1.0);
}

