mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, s, -s, c);
}

void amod(inout vec2 p, float m) {
	float a = mod(atan(p.x, p.y) - m*.5, m) - m*.5;
	p = vec2(cos(a), sin(a)) * length(p);
}

float rep(float p, float m) {
	return mod(p - m*.5, m) - m*.5;
}

vec3 rep(vec3 p, float m) {
	return mod(p - m*.5, m) - m*.5;
}

float cmin(float a, float b, float k) {
	return min(min(a, b), (a - k + b) * sqrt(.5));
}

float stmin(float a, float b, float k, float n) {
	float s = k / n;
	float u = b - k;
	return min(min(a, b), .5 * (u + a + abs((mod(u - a + s, 2. * s)) - s)));
}

float length8(vec2 p) {
	vec2 q = p*p*p*p*p*p*p*p;
	return pow(q.x + q.y, 1. / 8.);
}

float torus82(vec3 p, vec2 d) {
	vec2 q = vec2(length(p.xz) - d.x, p.y);
	return length8(q) - d.y;
}

float path(float t) {
	return cos(t*.1)*2.;
}

float g = 0.;
float de(vec3 p) {

	p.x -= path(p.z);

    vec3 q = p;
    q.x += sin(q.z*.2)*4.;
    q.y += cos(q.z*.3)*4.;
    q += iTime*2.;
    q.yz += sin(iTime*.2)*4.;
    q = rep(q, 1.);
    float s1 = length(q) - .01 + sin(iTime*30.)*.004;

	p.z = rep(p.z, 3.);

	float d = torus82(p.xzy, vec2(1., .1));
	float pl = p.y + 2.4 + p.y*texture(iChannel1, p.xz*.1).r*1.;
    float pl2 = p.y + .7;
	d = min(d, pl-d*.9);
    d = cmin(d, pl2, .1);

	amod(p.xy, 6.28 / 3.);
	p.x = abs(p.x) - 1.;
	float cyl = length(p.xy) - .05;
	d = stmin(d, cyl, .1, 4.);

    d = min(d, s1);

	g += .015 / (.01 + d*d);
	return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;

	float dt = iTime * 8.3;

	vec3 ro = vec3(0, 0, -3. + dt);
	vec3 ta = vec3(0, 0, dt);
	ro.x += path(ro.z);
	ta.x += path(ta.z);

	vec3 fwd = normalize(ta - ro);
	vec3 left = cross(vec3(0, 1, 0), fwd);
	vec3 up = cross(fwd, left);
	vec3 rd = normalize(fwd + uv.x*left + uv.y*up);

	rd.xy *= r2d(sin(-ro.x / 3.14)*.4);

	vec3 p;
	float t = 0., ri;
	for (float i = 0.; i < 1.; i += .01) {
		ri = i;
		p = ro + rd*t;
		float d = de(p);
		if (d < .001) break;
		t += d*.3;
	}

	vec3 bg = vec3(.2, .1, .2);
	vec3 col = bg;
	col = mix(vec3(.4, .52, .6)*1.5, bg,  uv.x*uv.y*uv.y+ri);
    col += g*.01;
    col.b += sin(p.z*.1)*.1;
	col = mix(col, bg, 1. - exp(-.01*t*t));

	fragColor = vec4(col, 1.);
}

vec3 smoothSine(vec2 uv, vec3 background, float freq, float amp, float size, vec2 shift, vec3 baseCol){
    vec3 ret = background;

    vec2 sine = vec2(uv.x, shift.y + amp*sin(uv.x*freq+shift.x));

    //vector tangent to curve
    //vec2 tang = vec2(uv.x, shift.y + amp*cos(uv.x*freq+shift.x));

    //vector normal to curve
    //vec2 norm = normalize(-1.*vec2(tang.y,tang.x));

    float dist = length(uv-sine);

    if (dist < size){
     ret = background + smoothstep(size,0.,dist);   
    }

 	ret*=baseCol;

    return ret;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 qr = fragCoord/iResolution.xy;
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float freq = smoothstep(.75,.1,(texture(iChannel0, vec2(qr.x,1))).x);

    vec3 baseCol = vec3(cos(iTime)*uv.x*181./255., 199./255., 229./255.);

    vec3 col = vec3(0);

    //should use loop here
    col = smoothSine(uv, col, sin(iTime+.3)*5., freq/7.+.25, .01, vec2(6.*iTime+.3,-0.10625 + sin(2.*iTime)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime+.25)*5., freq/6.+.25, .0125, vec2(6.*iTime+.25,-0.1125+ sin(2.*iTime+.05)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime+.2)*5., freq/5.+.25, .015, vec2(6.*iTime+.2,-0.125+ sin(2.*iTime+.1)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime+.15)*5., freq/4.+.25, .0175, vec2(6.*iTime+.15,-0.15+ sin(2.*iTime+.15)/6.), baseCol);  
	col = smoothSine(uv, col, sin(iTime+.1)*5., freq/5.+.25, .02, vec2(6.*iTime+.1,0.-.2+ sin(2.*iTime+.2)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime+.05)*5., freq/6.+.25, .0225, vec2(6.*iTime+.05,-.3+ sin(2.*iTime+.25)/6.), baseCol);
    col = smoothSine(uv, col, sin(iTime)*5., freq/7.+.25, .025, vec2(6.*iTime,-.425+ sin(2.*iTime+.3)/6.), baseCol); 

    // Output to screen
    fragColor = vec4(col,1.0);
}

