    q.xz*=r2d(iTime);
    q.xy*=r2d(iTime);
    float s1 = od(q, .2- sin(iTime)*.1);

    q = p;
    q.xy*=r2d(q.z*.1);
    amod(q.xy, 4./6.28);
    q.x = abs(q.x) - 3.;
    float cyl = length(q.xy) - .3;


    p.xy *= r2d(iTime*.4);

    //p.y += 2.;

    //p.xy *= r2d(p.z*.4);


    amod(p.xy, 12./6.28);
    mo(p.xy, vec2(2, 2.));
    //mo(p.zy, vec2(.04, .7));

    p.xy *= r2d(p.z*.2);

    p.z = rep(p.z, 1.);


    amod(p.xy, .785);

    //p.x = abs(p.x) - 1.;
	mo(p.xy, vec2(.8, .4));

    float sc2 = sc(p, .5);

    p.xy *= r2d(3.14*.25);

    float d = sc(p, .3);
    d = max(d, -sc2);
    d = min(d, s1);
    if(d<cyl) {
        id = 1.;
        //d= d;
    }else {
        id = 2.;
       // d= cyl;
    }
	g += .01 / (.02 + d*d);
	return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
