		p = ro + rd*t;
		float d = de(p);
		if (d < .001) break;
		t += d*.3;
	}
	vec3 bg = vec3(.2, .1, .2);
	vec3 col = mix(vec3(1., .0, .0), bg, uv.x+ri);
    /*if(id == 2.)*/ col.g += sin(p.z*.1)*.4; 
       // col = mix(vec3(.3, .1+sin(p.z)*.3, .2), bg, ri);
	col += g*.03;
	col = mix(col, bg, 1. - exp(-.01*t*t));

	fragColor = vec4(col, 1.0);
}

vec2 split_uv(vec2 uv) {
    return uv - vec2(0.5*sign(uv.x)*iResolution.x/iResolution.y, 0.0);
}

vec3 get_normal(vec2 uv) {
    // http://web.cs.ucdavis.edu/~amenta/s12/findnorm.pdf
 	uv = uv * (1.0 + 0.6*cos(iTime));

    float phi = 2.0*3.1416*uv.x;
    float theta = 2.0*3.1416*uv.y;

    vec3 dphi = vec3(-sin(phi), cos(phi), 0.0);
    vec3 dtheta = vec3(-sin(theta)*cos(phi), -sin(theta)*sin(phi), cos(theta));

    return normalize(cross(dphi, dtheta));  
}

float checkerboard(vec2 uv, float size) {
    // https://www.shadertoy.com/view/lt2XWK
	vec2 p = floor(uv / size);
    return mod(p.x + p.y, 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float cell_size = 0.125*iResolution.x/iResolution.y;

    // uv coordinates, corrected for aspect ratio
    vec2 uv0 = (2.0*fragCoord.xy - iResolution.xy)/iResolution.yy;

    // uv coordinates split in half and duplicate left hand side
    vec2 uv1 = split_uv(uv0);

    // grab the normals from the torus using these uv coordinates
    vec3 n = get_normal(uv1.yx);

    // distort uv's based on normal
    uv1 = uv1 + 0.1*cell_size*n.xy;

    // apply a scrolling motion upwards
    uv1 = uv1 - vec2(0.0, 0.2*iTime);

    // create a checkerboard effect
    float s = checkerboard(uv1, cell_size);

    // output
    if (uv0.x > 0.0) {   
        fragColor = vec4(s, s, s, 1.0);
        //fragColor = texture(iChannel0, uv1/cell_size);
    } else {
    	fragColor = vec4(0.5 + 0.5*n, 1.0);
    }
}

// Various knobs to twiddle
#define MIN_DIST 0.001
#define STEP_MULTIPLIER 0.9
#define NORMAL_OFFSET 0.01
#define MAX_STEPS 64
#define RAY_FUZZ 0.001
#define SPEC_INTENS 0.7

// Shit sync
#define BPM 128.0
#define SPEED (BPM / 60.0) * 3.14 * 0.25

// Palette helper
vec3 color(float inVal, float glow) {
	vec3 col = 
        mix(
           mix(
           	vec3(0.3, 0.3, 0.4),
           	vec3(0.2, 0.5, 1.0),
           	abs(clamp(inVal - 0.75, 0.0, 0.25)) * 4.0
           ),
           vec3(0.5, 1.0, 1.0) * (0.8 + glow * 3.0),
           abs(clamp(inVal - 0.9, 0.0, 0.1)) * 10.0
        );
    return col;
}

// For fuzzing up rays a little
vec3 hash33(vec3 p){ 
    float n = sin(dot(p, vec3(7, 157, 113)));    
    return fract(vec3(2097152, 262144, 32768)*n); 
}

// Distance / color combiner
vec4 distcompose(vec4 dista, vec4 distb, float softness) {
    float mixfact = clamp(0.5 + 0.5 * (distb.a - dista.a) / softness, 0.0, 1.0);
    return mix(distb, dista, mixfact) - vec4(0.0, 0.0, 0.0, softness * mixfact * (1.0 - mixfact));
}

// World
vec4 distfunc(vec3 pos, float glow) {
  	float dist = -(length(pos.xy) - 0.4 + cos(pos.z * 20.0 + atan(pos.y, pos.x)) * 0.04);

    float abspos = pos.z - iTime * 0.2 * SPEED;
    dist -= texture(iChannel0, vec2((cos(abspos * 1.3) + 1.0) / 2.0, 0.0)).r * 0.1 * SPEC_INTENS;

    float ranp = sin(pos.z + iTime * 2.0 * SPEED);
    float line = (length(pos.xy - vec2(0.0, 0.1)) - 0.001) + (ranp > 0.7 ? 0.0 : 1000.0);

    ranp = sin(pos.z + iTime * 1.7 * SPEED);
    line = min(line, (length(pos.xy - vec2(-0.2, 0.06)) - 0.001) + (ranp > 0.7 ? 0.0 : 1000.0));

    ranp = sin(pos.z + iTime * 2.2 * SPEED);
    line = min(line, (length(pos.xy - vec2(-0.2, -0.11)) - 0.001) + (ranp > 0.7 ? 0.0 : 1000.0));

    ranp = sin(pos.z + iTime * 2.5 * SPEED);
    line = min(line, (length(pos.xy - vec2(0.1, -0.2)) - 0.001) + (ranp > 0.7 ? 0.0 : 1000.0));

    vec4 lines = vec4(0.8, 0.8, 1.0, line);


    float colsel = cos(sin(atan(pos.y, pos.x) * 5.0 + pos.z * 10.0));
    float colval = cos(sin(atan(pos.y, pos.x) * 10.0 + pos.z * 20.0));

    if(mod(iTime * SPEED, 3.14 * 0.5) < 3.14 * 0.25) {
    	glow = glow * (colsel < 0.75 ? 2.0 : 0.2);
    }
    else {
        glow = glow * (colsel > 0.75 ? 2.0 : 0.2);
    }
    vec4 tube = vec4(color(colval, glow), dist);
    return(distcompose(tube, lines, 0.0));
}

// Renderer
vec4 pixel(vec2 fragCoord) {
    float glow = abs(cos(mod(iTime * 2.0 * SPEED, 3.14 / 2.0))) / 3.0;

    // Screen -1 -> 1 coordinates
    vec2 coords = (2.0 * fragCoord.xy  - iResolution.xy) / max(iResolution.x, iResolution.y);

    // Camera as eye + imaginary screen at a distance
    vec3 eye = vec3(cos(iTime * SPEED) * 0.1, 0.0, iTime * 0.2 * SPEED);
    vec3 lookat = vec3(cos(iTime * SPEED) * 0.1, 0.0, iTime * 0.2 * SPEED + 1.0);
    vec3 lookdir = normalize(lookat - eye);
    vec3 left = normalize(cross(lookdir, vec3(cos(iTime * 0.1 * SPEED), -sin(iTime * 0.1 * SPEED), 0.0)));
    vec3 up = normalize(cross(left, lookdir));
    vec3 lookcenter = eye + lookdir;
	vec3 pixelpos = lookcenter + coords.x * left + coords.y * up;
    vec3 ray = normalize(pixelpos - eye);
    ray += hash33(ray) * RAY_FUZZ;

    vec3 lightpos = vec3(eye.x, eye.y, eye.z + 0.8);

    // March
    vec3 pos = eye;
    float dist = 0.0;
    float curdist = 1.0;
    float iters = float(MAX_STEPS);
    for(int i = 0; i < MAX_STEPS; i++) {
        curdist = distfunc(pos, glow).a;
        dist += curdist * STEP_MULTIPLIER;
        pos = eye + ray * dist;
        if(curdist < MIN_DIST) {
        	iters = float(i);
            break;
        }
    }

    vec3 shadowray = normalize(lightpos - pos);    
    vec2 d = vec2(NORMAL_OFFSET, 0.0);
    vec3 normal = normalize(vec3(
        distfunc(pos + d.xyy, glow).a - distfunc(pos - d.xyy, glow).a,
        distfunc(pos + d.yxy, glow).a - distfunc(pos - d.yxy, glow).a,
        distfunc(pos + d.yyx, glow).a - distfunc(pos - d.yyx, glow).a
    ));
    float light = min(1.0, max(0.0, dot(normal, shadowray)) + 0.01);
    vec4 color = light * distfunc(pos, glow);
    color = color + vec4(0.5, 1.0, 1.0, 1.0) * iters * 0.05 * glow;
	return(color);
}

// Image
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = pixel(fragCoord);
}

//A metaball 'tutorial'
#define ANIMATION_SPEED_FACTOR 1.0

#define NUM_BLOBS 10
#define SPHERE 0
#define CUBE 1
#define DIAMOND 2
#define HLINE 3
#define VLINE 4

vec4 colorize(float energy)
{
    vec3 color=vec3(energy,0,0);
    if(energy>1.0)
    {
        color.x=1.0;
        color.z=energy-1.0;
    }
    if(energy>2.0)
    {
        color.x=1.0;
        color.y=energy-2.0;
        color.z=1.0;
    }
    return vec4(color,1.0);
}


struct blob {float distribution; float sd;float cd; int type;};   
blob[] blobs=blob[NUM_BLOBS]
(
	blob(10.0,	1.57,		3.14,	SPHERE),
    blob(15.0,	3.14, 		1.57,	SPHERE),
    blob(20.0,	3.24, 		1.77,	SPHERE),
    blob(25.0,	3.34, 		1.97,	SPHERE),
    blob(30.0,	3.44, 		2.17,	SPHERE),
    blob(35.0,	3.54, 		2.37,	SPHERE),
    blob(20.0,	1.05,		0.63,	CUBE),
    blob(10.0,	0.68, 		1.05,	DIAMOND),
    blob(100.0,	0.0, 		1.0,	HLINE),
    blob(100.0,	2.15, 		0.0,	VLINE)

);    
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;

    float energy=0.0;
    float hx=iResolution.x/2.0;
    float hy=iResolution.y/2.0;
    float ratio=iResolution.x;

    float tt=iTime/(1.0/ANIMATION_SPEED_FACTOR);

    for(int i=0;i<NUM_BLOBS;i++)
    {
        blob b=blobs[i];
        float radius=ratio/b.distribution;
        float bx=(hx-radius)*sin(b.sd*tt)+hx;
        float by=(hy-radius)*cos(b.cd*tt)+hy;
        switch(b.type)
        {
            case SPHERE:
            	energy+=radius/sqrt(pow(uv.x-bx,2.0)+pow(uv.y-by,2.0));
            	break;
            case CUBE:
            	energy-=radius/max(abs(uv.x-bx),abs(uv.y-by));
            	break;
            case DIAMOND:
            	energy-=radius/(abs(uv.x-bx)+abs(uv.y-by));
            	break;
           	case HLINE:
            	energy+=radius/abs(uv.y-by);
            	break; 
           	case VLINE:
            	energy+=radius/abs(uv.x-bx);
            	break;        }
    }
    fragColor=colorize(energy);
}

#define PI 3.1415
#define TAU PI*2.

uniform vec2 u_resolution;
uniform vec2 u_mouse;
uniform float u_time;

float pow3(float x) {
    return x*x*x;
}

float ease(float t) {
  return .5*(pow3(2.*t-1.)+1.);
}

// soft clamp a sdf edge to get free AA
float aaEdge(float dist) {
    float pixelSize = 4./iResolution.y;
    return clamp(dist/pixelSize, 0., 1.);
}

vec4 easeblob(vec2 uv) {
    float t = .5*(sin(iTime)+1.);

    float val = 1.;
    for (float i=0.; i<6.; i++) {
        float ang = i/6.*TAU + iTime/2.;
        vec2 v = vec2(cos(ang), sin(ang))*.5;
    	vec2 pos = mix(v, -v, ease(t));

        float blobval = aaEdge(length(uv-pos)-.1);
    	val = mix(blobval, val, blobval);
    }

    return vec4(vec3(val), 1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // pixel coords ∈ [-1, 1]^2
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    fragColor = easeblob(uv);
}

#define M_PI 3.1415926535897932384626433832795

vec2 star(vec2 uv, vec2 center, float rot, float reps) {
    vec2 diff = uv - center;
    float c = atan(diff.y, diff.x)*reps + rot;
    return vec2(clamp(cos(c)*5., -1., 1.), mod(c/4.0/M_PI, 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv_sq = fragCoord/iResolution.xx;

    //vec2 c1 = vec2(-0.1-sin(iTime/5.)/4.-0.25, 0.1);
    //vec2 c2 = vec2(1.1+sin(iTime/5.)/4.+0.25, 0.1);
    vec2 c1 = vec2(-0.1, 0.1);
    vec2 c2 = vec2(1.1, 0.1);
	float reps = 50.;
    float speed = 1.8;

    vec2 s1 = star(uv_sq, c1,  iTime*speed, reps);
    vec2 s2 = star(uv_sq, c2, -iTime*speed, reps);

    float c = smoothstep(0., 1., uv.y) * clamp(s1.x, 0., 1.0) * clamp(s2.x, 0., 1.0)
            + smoothstep(0., 1., 1.-uv.y) * clamp(-s1.x, 0., 1.0) * clamp(-s2.x, 0., 1.0);

    vec2 uv_distort = vec2(s1.y, s2.y);
    float noise = texture(iChannel0, uv_distort).r*.6 + 0.3;
    float vignette = pow((1.0-length(uv-0.5)-.2)*1.5, 2.);

    vec3 fg_color = vec3(0.8,1.3,1.5);
    vec3 bg_color = vec3(0.08,0.05,0.05);

    vec3 linearCol = (c*fg_color + bg_color) * noise * vignette;

    float displayGamma = 2.2;
    fragColor.r = pow ( linearCol.r, 1.0/displayGamma );
    fragColor.g = pow ( linearCol.g, 1.0/displayGamma );
    fragColor.b = pow ( linearCol.b, 1.0/displayGamma );
    fragColor.a = 1.0;
}

