vec2 lerp_pos(  float t){ 
    //REFAZRE POS PONTOS
    vec2 p1,p2;
int p = int(mod((t - mod(t,1.)),5.));
    switch (p){
     case 0:
        p1 = pos1;
                p2 = pos2;
        break;
             case 1:
        p1 = pos2;
                p2 = pos3;

        break;
             case 2:
        p1 = pos3;
                p2 = pos4;

        break;
             case 3:
        p1 = pos4;
                p2 = pos5;

        break;
             case 4:
        p1 = pos5;
                p2 = pos1;

        break;
    }
    return p1*mod(t,1.) + p2*(1. - mod(t,1.));

}

